    p, zoomedUv, sphereCenter, sphereRadius, focusFactor, chromaticAberrationFactor
  );

  float imageDistorted_R = texture(iChannel0, distortion.uv_R).x;
  float imageDistorted_G = texture(iChannel0, distortion.uv_G).y;
  float imageDistorted_B = texture(iChannel0, distortion.uv_B).z;

  vec3 imageDistorted = vec3(
      imageDistorted_R,
      imageDistorted_G,
      imageDistorted_B
