void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Center point of the blur effect
    vec2 center = iResolution.xy / 2.0;

    // Radius of the blur effect
    float radius = 1. * length(fragCoord - center);

    // Number of samples for the blur
    int numSamples = 17;

    // Accumulate color and weight for each sample
    vec3 accumulatedColor = vec3(0.0);
    float totalWeight = 0.0;

    for (int i = 0; i < numSamples; i++)
    {
        // Calculate the current sample position
        float angle = 2.0 * 3.14159 * float(i) / float(numSamples);
        vec2 offset = vec2(sin(angle), sin(angle)) * radius / 100.;
        vec2 samplePos = fragCoord + offset;

        // Sample the color from the original image
        vec4 sampleColor = texture(iChannel0, samplePos / iResolution.xy);

        // Calculate the weight based on the distance from the center
        float distance = length(offset);
        float weight = radius - smoothstep(0.0, radius, distance);

        // Accumulate the color and weight
        accumulatedColor += sampleColor.rgb * weight;
        totalWeight += weight;
    }

    // Normalize the accumulated color
    vec3 blurredColor = accumulatedColor / totalWeight;

    // Output the final color
    fragColor = vec4(blurredColor, 1.0);
