const int iterations = 2;
const int rayBounces = 10;
const float aperture = 0.2;
const float focalLength = 5.5;

struct material{
    vec3 col;
    float intensity;
    float smoothness;
    float specProb;
};

vec3 rotate2D(vec3 p, vec2 t){
    float stx = sin(t.x);
    float ctx = cos(t.x);
    float sty = sin(t.y);
    float cty = cos(t.y);
    mat3 xRotation;
    xRotation[0] = vec3(1, 0, 0);
    xRotation[1] = vec3(0, ctx, -stx);
    xRotation[2] = vec3(0, stx, ctx);

    mat3 yRotation;
    yRotation[0] = vec3(cty, 0, -sty);
    yRotation[1] = vec3(0, 1, 0);
    yRotation[2] = vec3(sty, 0, cty);
    return p*xRotation*yRotation;
}

vec4[100] scene;

material[100] mats;

//Dave_Hoskins' Hash Without Sine
float random3(vec3 p){
    p = fract(p*0.1031);
    p += dot(p, p.zyx + 31.32);
    return (fract((p.x + p.y)*p.z)*2.0) - 1.0;
}

float traceSphere(vec3 o, vec3 d, vec4 s){
    vec3 oc = o - s.xyz;
    float a = dot(d, d);
    float b = 2.0*dot(d, oc);
    float c = dot(oc, oc) - s.w*s.w;
    float disc = b*b - 4.0*a*c;
    return disc > 0.0 ? (-b - sqrt(disc))/2.0*a : 100000.0;
}

float traceScene(vec3 o, vec3 d, inout bool hit, inout vec3 normal, inout material mat){
    float initT = 100000.0;
    for(int i = 0; i < scene.length(); i++){
        float t = traceSphere(o, d, scene[i]);
        if(t < initT && t >= 0.0){
            initT = t;
            hit = true;
            normal = (o + d*t - scene[i].xyz)/scene[i].w;
            mat = mats[i];
        }
    }
    return initT;
}

void pathtrace(vec3 o, vec3 d, inout vec3 col, float seed){
    col = vec3(1);
    bool hit = false;
    vec3 n = vec3(0);
    material mat = material(vec3(1), 0.0, 0.0, 0.0);
    float t = traceScene(o, d, hit, n, mat);
    vec3 pn = n;
    material pmat = mat;
    float iDepth = t;        
    if(hit){
        col *= mat.col*mat.intensity;
        if(mat.intensity > 1.0) return;
        for(int i = 0; i < rayBounces; i++){
            hit = false;
            o = o + d*t;
            vec3 rd = normalize(vec3(random3(o*2093847.4832 + 293.482 + seed), random3(o*3283742.438 + 213.847 + seed), random3(o*208347.483 + 283.837 + seed)));
            float dn = dot(rd, pn);
            rd *= sign(dn);

            vec3 sd = normalize(reflect(d, pn));

            float hasSpec = (random3(o*2093847.4832 + 293.482 + seed) + 1.0)/2.0 <= mat.specProb ? 1.0 : 0.0;

            d = mix(rd, sd, mat.smoothness*(hasSpec));
            t = traceScene(o, d, hit, n, mat);

            if(hit){
                col = mix(col, mat.col, hasSpec);
                col *= mat.col*mat.intensity*clamp(dn, 0.0, 1.0)*2.0;
            }else{
                col = mix(col, mat.col, hasSpec);
                col *= vec3(1)*mat.intensity*clamp(dn, 0.0, 1.0)*2.0;
                break;
            }
            pn = n;
            pmat = mat;
            if(mat.intensity > 1.0) break;
        }
    }else{
        col *= vec3(1);
        return;
    }
    col = mix(col, vec3(1), 1.0 - exp(-iDepth*vec3(0.01, 0.011, 0.015)));
}

