void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float a = iResolution.x/iResolution.y;
    uv -= 0.5;
    uv.x *= a;
    uv += 0.5;

    float nBars = 15.;

    float bar = smoothstep(0.0, 1./nBars, mod(uv.y, 1./nBars));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float ripple = 0.5 * sin(uv.y + iTime * 4.) + 0.5;

    // Output to screen
    fragColor = vec4(col * ripple + bar * 0.25, 1.0);
}

// "Grid-Surfing Balls" by dr2 - 2023
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

/*
  In addition to collisions with each other and the surface, balls experience
  gravity and a weak force pulling them towards the grid midpoint.
  (Based on mix of "Hexagonally Discretized Waves", "Rolling Stones", etc)

  (No.92 in "Physical Dynamics" series - listed at end)

Notes:

   The simulation is tuned for the approx range 50-100 fps; too
   slow and it won't look good.

   Collisions are highly inelastic, otherwise you see bouncing balls
   instead of surfing and rolling.

   Two different 'clocks' are required to ensure both numerical stability
   of the ball dynamics (based on frame counter) as well as decent wave
   motion visuals independent of GPU speed (wall clock).

   Wave speed can be changed via the parameter 'wtFac' in all Buffers.

   Effective ball speed can be reduced via the timestep parameter 'dt'
   in all Buffers or by removing Buffers (C/B), and increased by adding
   an identical Buffer D as in previous shaders of this kind.

   Other parameters are from earlier shaders and should be ok.
*/

#define AA       0  // (= 0/1) optional antialiasing
#define SHADOWS  0  // (= 0/1) optional shadows

#define VAR_ZERO min (nFrame, 0)

mat3 QtToRMat (vec4 q);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Minv2 (vec2 p);
float Minv3 (vec3 p);
vec3 HsvToRgb (vec3 c);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Noisev2v4 (vec4 p);
vec4 Loadv4 (int idVar);

vec3 qHit, vnBall, ltDir;
vec2 cId, cMid;
float dstFar, tCur, hgSize, wavHt, whFac, wtFac;
int nFrame, idObj, idBall, nBall;
const float pi = 3.1415927, sqrt3 = 1.7320508;

float ObjDf (vec3 p)
{
  float h, sy;
  sy = 5.;
  h = p.y - wavHt + sy;
  return min (length (vec3 (p.xz - cMid, sign (h) * max (abs (h) - sy, 0.))) -
     (hgSize * 0.5 * sqrt3 - 0.005), p.y + 2. * sy);
}

float WaveHt (vec2 p)
{
  vec4 t4;
  vec2 q, t, tw;
  float wFreq, wAmp, h;
  q = 0.01 * p;
  wFreq = 1.;
  wAmp = 1.;
  tw = wtFac * tCur * vec2 (1., -1.);
  h = 0.;
  for (int j = VAR_ZERO; j < 3; j ++) {
    t4 = wFreq * (q.xyxy + tw.xxyy);
    t4 = abs (sin (t4 + 2. * Noisev2v4 (t4).xxyy - 1.));
    t4 = (1. - t4) * (t4 + sqrt (1. - t4 * t4));
    t = 1. - sqrt (t4.xz * t4.yw);
    t *= t;
    h += wAmp * dot (t, t);
    q *= mat2 (1.6, -1.2, 1.2, 1.6);
    wFreq *= 2.;
    wAmp *= 0.25;
  }
  return whFac * h;
}

void SetConf ()
{
  cMid = HexToPix (cId * hgSize);
  wavHt = WaveHt (cMid);
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM;
  float dHit, d, s, eps;
  bool cNu;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  eps = 0.001;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]), dot (ro.xz, edN[2])) / hgSize;
  dHit = 0.;
  cId = PixToHex (ro.xz / hgSize);
  pM = HexToPix (cId);
  cNu = true;
  for (int j = VAR_ZERO; j < 400; j ++) {
    if (j == 0 || cNu) {
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
      SetConf ();
      cNu = false;
    }
    d = ObjDf (ro + dHit * rd);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      cId = PixToHex ((ro.xz + dHit * rd.xz) / hgSize);
      pM += sqrt3 * edN[(s == hv.x) ? 0 : ((s == hv.y) ? 1 : 2)];
      cNu = true;
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n + 2);
    u = ro - p.xyz;
    rad = 0.5 * p.w;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vnBall = (u + d * rd) / rad;
        idBall = n;
      }
    }
  }
  return dMin;
}

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 rs, u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n + 2);
    u = ro - p.xyz;
    rad = 0.5 * p.w;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return 0.5 + 0.5 * smoothstep (0., rng, dMin);
}

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  u = vnBall * QtToRMat (Loadv4 (4 * idBall + 2 + 2));
  return 0.7 + 0.3 * step (0., sign (u.y) * sign (u.z) * atan (u.x, u.y));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, bgCol;
  float dstObj, dstBall, h, sh;
  dstObj = ObjRay (ro, rd);
  dstBall = BallHit (ro, rd);
  bgCol = vec3 (0.4, 0.4, 0.5);
  col = bgCol;
  sh = 1.;
  if (min (dstBall, dstObj) < dstFar) {
    if (dstObj < dstBall) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      col4 = vec4 (HsvToRgb (vec3 (0.05 + 0.9 * fract (ro.y / (2.6 * whFac)), 0.4, 1.)), 0.);
#if SHADOWS
      sh = BallHitSh (ro + 0.01 * vn, ltDir, 5.);
#endif
    } else {
      ro += dstBall * rd;
      vn = vnBall;
      col4 = vec4 (vec3 (1., 0.3, 0.1) * BallChqr (idBall, vnBall), 0.2);
    }
    col = col4.rgb * (0.2 + 0.2 * max (dot (vn, ltDir * vec3 (-1., 1., -1.)), 0.) +
       0.8 * sh * max (dot (vn, ltDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (dot (reflect (ltDir, vn), rd), 0.), 32.);
    col = mix (col, bgCol, smoothstep (0.8, 1., min (dstObj, dstBall) / dstFar));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, stDat;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, asp, zmFac, sr;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (0);
  nBall = int (stDat.x);
  whFac = stDat.y;
  wtFac = stDat.z;
  hgSize = stDat.w;
  stDat = Loadv4 (1);
  mPtr.xyz = stDat.xyz;
  az = 0.;
  el = -0.18 * pi;
  if (mPtr.z > 0. && Minv2 (mPtr.xy + 0.05 * vec2 (1. / asp, 1.) - 0.5) < 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
  }
  el = clamp (el, -0.4 * pi, -0.13 * pi);
  vuMat = StdVuMat (el, az);
  ro = vec3 (0., 40., -50.);
  zmFac = 2.6;
  dstFar = 200.;
  ltDir = normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    rd = vuMat * rd;
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (mPtr.z > 0. && Minv2 (uv - vec2 (asp, 1.)) > -0.1) col = mix (col, vec3 (1.), 0.4);
  fragColor = vec4 (col, 1.);
}

mat3 QtToRMat (vec4 q)
{
  mat3 m;
  vec3 a1, a2;
  a1 = q.yzx * q.zxy;
  a2 = q.w * q.xyz;
  a1 += a2;
  a2 = a1 - 2. * a2;
  m[1][2] = a1.x;  m[2][1] = a2.x;
  m[2][0] = a1.y;  m[0][2] = a2.y;
  m[0][1] = a1.z;  m[1][0] = a2.z;
  q.xyz = q.xyz * q.xyz + q.w * q.w - 0.5;
  m[0][0] = q.x;  m[1][1] = q.y;  m[2][2] = q.z;
  return 2. * m;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), 1.5 * h.y);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + vec4 (0., 1., 57., 58.)) * cHashM);
}

vec2 Noisev2v4 (vec4 p)
{
  vec4 ip, fp, t1, t2;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t1 = Hashv4f (dot (ip.xy, vec2 (1., 57.)));
  t2 = Hashv4f (dot (ip.zw, vec2 (1., 57.)));
  return vec2 (mix (mix (t1.x, t1.y, fp.x), mix (t1.z, t1.w, fp.x), fp.y),
               mix (mix (t2.x, t2.y, fp.z), mix (t2.z, t2.w, fp.z), fp.w));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

