// "Grid-Surfing Balls" by dr2 - 2023
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

#define VAR_ZERO min (nFrame, 0)

vec4 QtMul (vec4 q1, vec4 q2);
mat3 QtToRMat (vec4 q);
vec4 RMatToQt (mat3 m);
vec4 EulToQt (vec3 e);
mat3 LpStepMat (vec3 a);
float Maxv2 (vec2 p);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Hashff (float p);
vec2 Noisev2v4 (vec4 p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

float tCur, fOvlap, fricN, fricSW, fricT, hgSize, whFac, wtFac;
int nFrame, nBall;
const float pi = 3.1415927, sqrt3 = 1.7320508;

float WaveHt (vec2 p)
{
  vec4 t4;
  vec2 q, t, tw;
  float wFreq, wAmp, h;
  q = 0.01 * p;
  wFreq = 1.;
  wAmp = 1.;
  tw = wtFac * tCur * vec2 (1., -1.);
  h = 0.;
  for (int j = VAR_ZERO; j < 3; j ++) {
    t4 = wFreq * (q.xyxy + tw.xxyy);
    t4 = abs (sin (t4 + 2. * Noisev2v4 (t4).xxyy - 1.));
    t4 = (1. - t4) * (t4 + sqrt (1. - t4 * t4));
    t = 1. - sqrt (t4.xz * t4.yw);
    t *= t;
    h += wAmp * dot (t, t);
    q *= mat2 (1.6, -1.2, 1.2, 1.6);
    wFreq *= 2.;
    wAmp *= 0.25;
  }
  return whFac * h;
}

void CollFc (float rSep, float szAv, vec3 dr, vec3 dv, vec3 dw, inout vec3 am, inout vec3 wam)
{
  float fc, drv, ft;
  fc = fOvlap * (szAv / rSep - 1.);
  drv = dot (dr, dv) / (rSep * rSep);
  fc = max (fc - fricN * drv, 0.);
  am += fc * dr;
  dv -= drv * dr + cross (dw, dr);
  ft = min (fricT, fricSW * abs (fc) * rSep / max (0.001, length (dv)));
  am -= ft * dv;
  wam += (ft / rSep) * cross (dr, dv);
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm, out float sz)
{
  vec4 p;
  vec3 rmN, vmN, wmN, dr, am, wam, e;
  vec2 cId, cMid, ee[7];
  float fDamp, fCent, grav, rSep, szN, szAv, ms, dt;
  fOvlap = 1000.;
  fricN = 50.;
  fricSW = 2.;
  fricT = 0.5;
  fDamp = 0.1;
  fCent = 0.006;
  grav = 5.;
  p = Loadv4 (4 * mId + 2);
  rm = p.xyz;
  sz = p.w;
  vm = Loadv4 (4 * mId + 1 + 2).xyz;
  qm = Loadv4 (4 * mId + 2 + 2);
  wm = Loadv4 (4 * mId + 3 + 2).xyz;
  //return;
  ms = sz * sz * sz;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n + 2);
    rmN = p.xyz;
    szN = p.w;
    dr = rm - rmN;
    rSep = length (dr);
    szAv = 0.5 * (sz + szN);
    if (n != mId && rSep < szAv) {
      vmN = Loadv4 (4 * n + 1 + 2).xyz;
      wmN = Loadv4 (4 * n + 3 + 2).xyz;
      CollFc (rSep, szAv, dr, vm - vmN, (sz * wm + szN * wmN) / (sz + szN), am, wam);
    }
  }
  cId = PixToHex (rm.xz / hgSize);
  e = vec3 (1., 0., -1.);
  ee[0] = e.yy;
  ee[1] = e.yx;
  ee[2] = e.yz;
  ee[3] = e.xy;
  ee[4] = e.zy;
  ee[5] = e.xz;
  ee[6] = e.zx;
  for (int k = VAR_ZERO; k < 7; k ++) {
    rmN.xz = HexToPix ((cId + ee[k]) * hgSize);
    rmN.y = WaveHt (rmN.xz);
    dr = rm - rmN;
    dr.y = max (dr.y, 0.);
    rSep = length (dr);
    szAv = 0.5 * (sz + 1.);
    if (rSep < szAv) CollFc (rSep, szAv, dr, vm, wm, am, wam);
  }
  rSep = length (rm.xz);
  if (rSep > 0.) am.xz -= fCent * rm.xz / rSep;
  am.xz -= fDamp * vm.xz;
  am.y -= grav * ms;
  dt = 0.01;
  vm += dt * am / ms;
  rm += dt * vm;
  wm += dt * wam / (0.1 * ms * sz);
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm, out float sz)
{
  vec3 e;
  float mIdf, ne;
  mIdf = float (mId);
  ne = sqrt (float (nBall));
  rm.xz = 2. * (vec2 (mod (mIdf, ne), floor (mIdf / ne)) - 0.5 * ne);
  rm.y = 2.6 * whFac + 1.;
  vm = 2. * (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6)) - 0.5);
  e = normalize (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6)));
  qm = EulToQt (e);
  wm = vec3 (0.);
  sz = 1.;
}

const float txRow = 128.;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, stDat, qm;
  vec3 rm, vm, wm;
  vec2 canvas, iFrag;
  float sz, nStep, asp;
  int mId, pxId, kp;
  bool doInit;
  nFrame = iFrame;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  nBall = 144;
  if (iFrag.x >= txRow || pxId >= 4 * nBall + 2) discard;
  doInit = (nFrame <= 2);
  if (doInit) {
    whFac = 10.;
    wtFac = 0.07;
    hgSize = 1./sqrt3;
    nStep = 0.;
  } else {
    stDat = Loadv4 (0);
    whFac = stDat.y;
    wtFac = stDat.z;
    hgSize = stDat.w;
    nStep = stDat.x;
    stDat = Loadv4 (1);
    nStep = stDat.w;
    ++ nStep;
  }
  if (mPtr.z > 0. && Maxv2 (mPtr.xy + 0.03 * vec2 (1. / asp, 1.)) > 0.5 && nStep > 100.)
     doInit = true;
  if (doInit) nStep = 0.;
  mId = (pxId >= 2 && pxId < 4 * nBall + 2) ? (pxId - 2) / 4 : -1;
  if (mId >= 0) {
    if (doInit) Init (mId, rm, vm, qm, wm, sz);
    else Step (mId, rm, vm, qm, wm, sz);
    kp = pxId - 2 - 4 * mId;
    if      (kp == 0) stDat = vec4 (rm, sz);
    else if (kp == 1) stDat = vec4 (vm, 0.);
    else if (kp == 2) stDat = qm;
    else if (kp == 3) stDat = vec4 (wm, 0.);
  } else {
    if      (pxId == 0) stDat = vec4 (float (nBall), whFac, wtFac, hgSize);
    else if (pxId == 1) stDat = vec4 (mPtr.xyz, nStep);
  }
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  vec2 e = vec2 (1., -1.);
  return vec4 (dot (e.xyxx * q1.wzyx, q2), dot (e.xxyx * q1.zwxy, q2),
     dot (e.yxxx * q1.yxwz, q2), dot (e.yyyx * q1.xyzw, q2));
}

mat3 QtToRMat (vec4 q)
{
  mat3 m;
  vec3 a1, a2;
  a1 = q.yzx * q.zxy;
  a2 = q.w * q.xyz;
  a1 += a2;
  a2 = a1 - 2. * a2;
  m[1][2] = a1.x;  m[2][1] = a2.x;
  m[2][0] = a1.y;  m[0][2] = a2.y;
  m[0][1] = a1.z;  m[1][0] = a2.z;
  q.xyz = q.xyz * q.xyz + q.w * q.w - 0.5;
  m[0][0] = q.x;  m[1][1] = q.y;  m[2][2] = q.z;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2;
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
