mat2 Rot(in float a) {
    return mat2(cos(a),-sin(a),
                sin(a),cos(a));
}

float columnDist( vec3 p, vec3 c, vec3 r ) { 
    p.xy *= Rot(r.x);
    // p.yz *= Rot(r.y);
    // p.zx *= Rot(r.z);
    return length(p.xz-c.xy)-c.z; 
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

vec2 texNormalMap(in vec2 uv, in sampler2D tx, in vec2 txr)
{
    vec2 s = 1.0/txr.xy;

    float p = texture(tx, uv).x;
    float h1 = texture(tx, uv + s * vec2(1,0)).x;
    float v1 = texture(tx, uv + s * vec2(0,1)).x;

   	return (p - vec2(h1, v1));
}

float sphereDist(vec3 p, float s) {
    return length(p) - s;
}

vec2 map(vec3 p) {
    float mater = -1.;
    float d = 1000.;   

    // noise field
    vec3 axis = vec3(0.,1.,0.);
    vec3 nfp = p;
    nfp.xy *= Rot(sin(TT+p.z));
    float nf = simplex3d(nfp + vec3(0.,TT,0.));
    nf += 0.6 - abs(nfp.y)*0.2;

    if (nf < 0.001) mater = 2.;
    d = min(d, nf);

    return vec2(d, mater);
}

vec3 getNorm(vec3 p) {
    vec2 mapr = map(p);
    vec2 e = vec2(.001, 0);

	float d = mapr.x;
    vec3 n = vec3(0.);

    int method = 1;
    // method 1
    if (method==1) { 
        n = d - vec3(
            map(p-e.xyy).x,
            map(p-e.yxy).x,
            map(p-e.yyx).x);
    }
    // method 2
    if (method==2) {
        n = vec3(
            map(p+e.xyy).x - map(p-e.xyy).x,
            map(p+e.yxy).x - map(p-e.yxy).x,
            map(p+e.yyx).x - map(p-e.yyx).x);
    }

    return normalize(n);
}

vec3 getLightPos(in int i) {
    if (i==1){
        vec3 lightPos = vec3(0, 5, 6);
        lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
        return lightPos;
    }
}

float getLight(vec3 p, vec3 n, in vec3 rd) {
    vec3 lp = getLightPos(1);
    vec3 l = normalize(lp-p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = map(p+n*SURF_DIST*2.).x;
    if(d<length(lp-p)) dif *= .1;

    // Standard specualr term.
    float spec = pow(max( dot( reflect(-l, n), -rd ), 0.), 50.);

    return dif + spec;
}

vec3 getCol(vec3 p, float mater, in vec3 rd, in vec3 norm) {
    vec3 col = vec3(0.);
    if (mater < 0.) {
        return vec3(0.0);
    } else if (mater < 0.5) {
        col = norm;
        col *= norm;
    } else if (mater < 1.5) {
        col = vec3(sin(norm.r*100.));
        col *= norm;
    } else if (mater < 2.5) {
        vec2 txuv = norm.rg;
        txuv.x += iTime * 0.02;
        vec3 text = texture(iChannel0, txuv).rrr;
        col = mix(col,text*0.5,1.-col.r);
        col *= length(p.xy/3.)-0.25;
        col.r += sin(p.z+108.);
        col.g += sin(p.z*1.01+112.);
        col.b += sin(p.z*1.005+105.);
        col *= sin(p.z*0.9)*0.5+0.5;
        col /= 2.;
    } else if (mater < 3.5) {
        col = norm;
        col = vec3(sin(norm.b*100.));
    } else if (mater < 4.5) {
        col = p * mat3(vec2(0.5), 0.1, vec2(0.2), 0.9, vec2(0.4), 1.);
        // col = vec3(sin(norm.g*100.));
    } else {
        col = vec3(0.9,0.85,0.8) * 0.1;
    }
    return col;
}
vec3 k = vec3(0.);

bool doesBounce(in float mater) {
    if (mater > 1.5 && mater < 2.5) return true; // 2
    if (mater > 4.5 && mater < 5.5) return true; // 5
    return false;
}

vec3 materNorm(in float mater, in vec3 norm) {
    vec3 mN;
    if (mater > 1.5 && mater < 2.5) {
        // todo - improve texturing https://www.youtube.com/watch?v=VaYyPTw0V84
        vec2 txuv = norm.rg;
        txuv.x += iTime * 0.02;
        mN = texNormalMap(txuv, iChannel0, iChannelResolution[0].xy).xyx;
    }
    return mN;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvc = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 p, ro, rd, col;
    ro = vec3(0.); 
    // ro.xz *= Rot(iTime*0.3);
    ro.z += TT;
    ro -= JIGGLE*sin(vec3(iTime*1.,iTime*0.22,iTime*0.91));    
    rd = normalize(vec3(uvc.x, uvc.y, .8));
    // rd.xz *= Rot(iTime*0.3);
    rd.yz *= Rot(sin(iTime)*0.05);
    rd.xy *= Rot(sin(iTime*1.1)*0.05);
    rd.zx *= Rot(sin(iTime*0.9)*0.05);
    vec3 ord = rd;


    float d = 0.;
    bool hit = false;
    bool hit_matte = false;
    float glow = 0.;
    float mater = -1.;
	vec2 delta = vec2 (DELTA, 0.0);

    float intensity = 0.;

    // vec3 backColor = vec3(0.,0.01,0.02) * (1.-length(uv + vec2(0.,0.3)));
    vec3 backColor = vec3(54./255., 57./255., 63./255.);
    col = vec3(0.);

	float distanceFactor = 1.0;
	float refractionRatio = 1.0 / REFRACT_INDEX;
	float rayStepCount = 0.0;
	for (int rayIndex = 0; rayIndex < RAY_COUNT; ++rayIndex) {
        d = 0.; // reset ray dist
        float ds = 0.; // distance step
        for(int i=0;i<MAX_STEP;i++){
            p = ro + rd * d; // update current point
            vec2 mapr = map(p);
            ds = mapr.x; // map distance
            ds *= distanceFactor; // flip sdf when inside
            mater = mapr.y; // material index
            ds *= 0.3;
            d += max(ds, DELTA); // update march distance
            if(ds<0.) { hit=true; break; } // check hit
            if(d>MAX_DIST) break; // check limit
            glow += 1./ds; // add glow
        }
        if (!hit) {col = backColor; break;}
        vec3 norm = distanceFactor*getNorm(p);
        // material color
        col += getCol(p,mater,rd,norm) * AMBIENT;
        // matte lighting
        float light = getLight(p, norm, rd);
        if (rayIndex==0) {
            col += mix(vec3(light*light), vec3(0.), smoothstep(0.,0.5,ds)); // light outside
            col = mix(col, backColor, smoothstep(0.,0.5,ds)); // background
        }
        // check for bounces
        bool do_bounce = doesBounce(mater);
        if (!do_bounce) {
            // hit matte, done bouncing
            break;
        } else {
            // col *= 0.1;
            // apply normal to material 2 - note: make this a conditional if more reflective/refractive materials are added
            norm += materNorm(mater, norm);

            vec3 reflection = reflect(rd, norm);            
            // reflection light (i think it should be in getLight fxn so I can call it with multiple lights)
            if (distanceFactor > 0.) { // only calculate light on outside
                vec3 lp = getLightPos(1);
                vec3 lightDirection = normalize(lp-p);
                float reflectDiff = max (0.0, dot (norm, lightDirection));
                float reflectSpec = pow (max (0.0, dot (reflection, lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
                float fade = pow (1.0 - d / RAY_LENGTH_MAX, FADE_POWER);

                vec3 localColor = max(sin (k * k), 0.2);
                localColor = (AMBIENT + reflectDiff) * localColor + reflectSpec;
                localColor = mix(backColor, localColor, fade);

                col = col * (1.0 - intensity) + localColor * intensity;
                intensity *= REFRACT_FACTOR;
            }

            // Next ray...
            ro = p;
            vec3 refraction = refract(rd, norm, refractionRatio);
            if (dot (refraction, refraction) < DELTA) { // reflect at grazing angles, idk how the dot math works here
                // fragColor = vec4(1.,1.,0.,1.); return;
                rd = reflection; // update ray dir to reflected angle
                ro += rd * DELTA * 2.0; // step away from surface
            } else {
                rd = refraction; // update ray dir to refracted angle
                ro += rd * DELTA * 2.0; // step away from surface - not needed?
                refractionRatio = 1.0 / refractionRatio; // reverse refraction ratio 
                distanceFactor = -distanceFactor; // flip sdf sign while inside
            }
        }

        // todo: chromatic aberation, optical density, bevel, fresnel reflection
        // https://www.youtube.com/watch?v=0RWaR7zApEo
    }

    col *= 1.+smoothstep(0.3,1.,sin(TT*10.+p.z*2.));

    fragColor = vec4(col,1.);
}

