// The main function that is called for each pixel on the screen
void mainImage(out vec4 o, vec2 p)
{
    // Normalize the coordinates of the pixel to the range [-1, 1] to allow for rotation
    // This makes the code resolution-independent
    p = (2. * p - iResolution.xy) / iResolution.y;

    // Calculate the distance of the pixel from the center of the screen
    float r = length(p);

    // Add a time-dependent offset to the x and y coordinates to create the rotation
    float x = p.x * cos(iTime) - p.y * sin(iTime);
    float y = p.x * sin(iTime) + p.y * cos(iTime);

    // Calculate the texture coordinate based on the rotation and distance from the center
    vec2 uv = vec2(atan(y, x) / (2. * 3.1415) + .5, r);

    // Sample the texture to get the color of the pixel
    // The RGB components of the texture determine the color of the pixel
    // The alpha component of the texture determines the transparency of the pixel
    // Setting the alpha component to 1. creates a fully opaque tunnel effect
    o = texture(iChannel0, uv) * vec4(1., 1., 1., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 raw   = texture(iChannel0, fragCoord/iResolution.xy);
    vec4 bloom = texture(iChannel1, fragCoord/iResolution.xy);
    vec4 psf   = texture(iChannel2, fragCoord/iResolution.xy);

    fragColor = tanh(pow(psf*0.8+bloom*2.+0.3*raw,vec4(1.5)));
}

