#define PI 3.14159
float Sharp(float v, float n)
{
    v = 1.-v;
    v*= v;
    return max(0., 1.-n*v);
}
vec4 BackgroundColor(vec3 vd)
{
    vd = normalize(vd);
    vec3  a = abs(vd);
    float m = max(max(a.x, a.y), a.z);
    vec2 uv;
    if      (m == a.x)
    {
        uv = vec2(vd.z, vd.y);
    }
    else if (m == a.y)
    {
        uv = vec2(vd.x, vd.z);
    }
    else
    {
        uv = vec2(vd.x, vd.y);
    }
    vec4 col = vec4(0);
    mat2 rot = mat2(vec2(0.8, 0.6), vec2(-0.6, 0.8));
    uv *= rot;
    vec4 r1  = texture(iChannel0, uv/4.);
    uv *= rot;
    vec4 r2  = texture(iChannel0, uv/2.);
    uv *= rot;
    vec4 r3  = texture(iChannel0, uv/1.);

    col += (vec4(1.0, 1.0, 1.0, 0)                              )*Sharp(r1.r*r1.b, 9.)*3.;
    col += (vec4(0.4, 0.7, 1.0, 0) + vec4(0.6, 0.3, 0.0, 0)*r1.a)*Sharp(r1.g*r1.r, 9.)*3.;
    col += (vec4(1.0, 0.8, 0.4, 0) + vec4(0.0, 0.2, 0.6, 0)*r1.a)*Sharp(r1.b*r1.g, 9.)*3.;

    col += (vec4(1.0, 1.0, 1.0, 0)                              )*Sharp(r2.r*r1.a, 4.);
    col += (vec4(0.4, 0.7, 1.0, 0) + vec4(0.6, 0.3, 0.0, 0)*r2.a)*Sharp(r2.g*r1.b, 4.);
    col += (vec4(1.0, 0.8, 0.4, 0) + vec4(0.0, 0.2, 0.6, 0)*r2.a)*Sharp(r2.b*r1.g, 4.);

    col += (vec4(1.0, 1.0, 1.0, 0)                              )*Sharp(r3.r*r1.a*r2.a, 2.)*0.5;
    col += (vec4(0.4, 0.7, 1.0, 0) + vec4(0.6, 0.3, 0.0, 0)*r3.a)*Sharp(r3.g*r1.a*r2.a, 2.)*0.5;
    col += (vec4(1.0, 0.8, 0.4, 0) + vec4(0.0, 0.2, 0.6, 0)*r3.a)*Sharp(r3.b*r1.g*r2.a, 2.)*0.5;

    return col;
}
float SphereDist(float radius, vec3 position, vec3 direction)
{
    float d = -dot(position, direction) - sqrt(dot(position, direction)*dot(position, direction) - dot(direction, direction)*(dot(position, position) - radius*radius));
    return d;
}
float SphereThrough(float radius, vec3 position, vec3 direction)
{
    float d = 2.*sqrt(dot(position, direction)*dot(position, direction) - dot(direction, direction)*(dot(position, position) - radius*radius));
    return d;
}
float Dn(float n, vec3 pos)
{
    n   /= ACCDL/BHRAD;
    pos *= vec3(n, pow(1./((1./n)+N0), NPOW), n) / SCALE;
    float v = pow(n/ACCDL, 1.4)*SPEED;
    float p1= 1.1*v*iTime+46.65432*n;
    float p2= 0.9*v*iTime+54.21345*n;
    mat3  r1= mat3(vec3( cos(p1), 0, sin(p1)),
                   vec3(     0  , 1,     0  ),
                   vec3(-sin(p1), 0, cos(p1)));
    mat3  r2= mat3(vec3( cos(p2), 0, sin(p2)),
                   vec3(     0  , 1,     0  ),
                   vec3(-sin(p2), 0, cos(p2)));
    vec3 pos1= r1*pos;
    vec3 pos2= r2*pos;
    float d1  = texture(iChannel1, pos1/30.).r;
    float d2  = texture(iChannel1, pos2/20.).g;
    float d3  = texture(iChannel1, pos1/5. ).b;
    float d4  = (1./(0.3 + pos.y*pos.y));

    return exp(7.*d1 + 4.*d2 + 2.*d3 - 8.)*(5./(2. + pos.y*pos.y))*tanh(1./n - 1.) + d3*(2./(1. + pos.y*pos.y)) + d4;
}
float Density(vec3 pos)
{
    float n = ACCDL/length(pos);

    float n1= floor(n);
    float n2= ceil(n);

    float d1= Dn(n1, pos);
    float d2= Dn(n2, pos);

    return mix(d1, d2, smoothstep(n1, n2, n));
}
vec4 BB(float t)
{
    t *= TEMPMULT;
    vec4 hotblue = vec4(0.25, 0.5, 1, 0);
    vec4 white   = vec4(1);
    vec4 red     = vec4(0.8, 0.5, 0.2, 0);
    return mix(red, mix(hotblue, white, (1./(1.+t*t))), tanh(t));
}
float Boost(float v)
{
    return (sqrt((1.+v)/(1.-v)));
}
vec4 Add(vec3 pos, vec3 dir, float density)
{
    float temp = 1./dot(pos, pos);
    float v    = tanh(RVMULT/sqrt(length(pos)))*dot(cross(normalize(pos), vec3(0,1,0)), dir);
    temp *= Boost(v);
    return temp*density*BB(temp)*1.;
}
vec4 Abs(vec3 pos, float density)
{
    return vec4(0.5, 0.6, 0.8, 0)*0.8*density;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse  = iMouse.xy == vec2(0) ? vec2(0.5, 0.45)*iResolution.xy : iMouse.xy;
    float theta = (mouse.x - iResolution.x / 2.) / iResolution.y * PI;
    float phi   = (mouse.y - iResolution.y / 2.) / iResolution.y * PI*0.55;

    // CAMERA
    vec3 iCameraFwd     = vec3(sin(theta)*cos(phi),sin(phi),cos(theta)*cos(phi));
    vec3 iCameraUp      = vec3(-sin(theta)*sin(phi),cos(phi),-cos(theta)*sin(phi));
    vec3 iCameraRight   = normalize(cross(iCameraUp, iCameraFwd));

    vec3 iCameraPosition= -5.*iCameraFwd;

    float m = 1.0;

    vec3 iViewDirection = iCameraFwd + ((fragCoord.x - iResolution.x/2.0) * iCameraRight + (fragCoord.y - iResolution.y/2.0) * iCameraUp) / iResolution.x * m;
    iViewDirection      = normalize(iViewDirection);
    fragColor           = vec4(0);

    vec3  pos           = iCameraPosition + iViewDirection*SphereDist(5., iCameraPosition, iViewDirection);
    vec3  dir           = iViewDirection;
    float dis           = SphereThrough(5., iCameraPosition, iViewDirection)/NJUMPS;

    pos += dir*dis/2.;
    bool absorbed = false;

    vec4 mult = vec4(1);
    for (float n = 0.; n < NJUMPS+1.; n++)
    {
        dir -= C1*dis*normalize(pos)/dot(pos, pos);
        dir -= C2*dis*(cross(dir, vec3(0,1,0)))/pow(dot(pos,pos),1.5);
        dir  = normalize(dir);
        dis  = min(dis, SphereDist(BHRAD, pos, dir));
        pos += dir*dis;

        float dens = Density(pos);
        mult      *= exp(-dis*Abs(pos, dens)    )     ;
        fragColor +=      dis*Add(pos, dir, dens)*mult;

        if (length(pos) <= BHRAD*1.001)
        {
            absorbed = true;
            break;
        }
        else if (length(pos) > 5.)
        {
            break;
        }
    }
    if (!absorbed)
    {
        fragColor += BackgroundColor(dir)*mult*6.;
    }
}

