// Post processing
#define BLOOMSIZE 50.
#define PSFSIZE   20.

// Number of samples
#define NJUMPS    200.

// Black hole radius and lensing coefficients
// C1 is symmetric (normal gravity), C2 is antisymmetric (rotation)
#define BHRAD     0.3
#define C1        0.3
#define C2       -0.07

// Disk thickness (thickness ~ SCALE * pow(r + N0, NPOW))
#define NPOW      0.5
#define SCALE     0.03
#define N0        2.

// Orbit coefficients (number of 'bands' and orbital speed power)
#define ACCDL     10.
#define SPEED     150.

// Color coefficients (Doppler strength and temperature)
#define RVMULT    0.9
#define TEMPMULT  0.2

