float hash1(float n) { return fract(sin(n)*43758.5453); }
vec2 hash2(vec2 p) { return fract(sin(vec2(dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3))))*43758.5453); }

vec2 rotate(vec2 p, float a) {
float s = sin(a), c = cos(a);
return mat2(c, s, -s, c) * p;
}

vec3 physhue2rgb(float h, float r) {
return smoothstep(vec3(0.0), vec3(1.0), abs(mod(h + vec3(0.0, 1.0, 2.0) * (1.0/r), 1.0) * 2.0 - 1.0));
}

vec4 voronoi(vec2 x, float c, out vec2 rp) {
vec2 n = floor(x), f = fract(x);
vec3 m = vec3(4.0);
float m2 = 8.0;
for (int j = -3; j <= 2; j++)
for (int i = -3; i <= 2; i++) {
vec2 g = vec2(float(i), float(j)), o = hash2(n + g);
float cid = hash1(dot(n + g, vec2(8.0, 4.0)));
if (cid < 0.1) o = 0.5 + 0.5 * abs(mod(c + o, 2.0) - 1.0);
vec2 r = g - f + o;
vec2 d = vec2(max(abs(r.x)*0.866025+r.y*0.5,-r.y), 1.0);
if (d.x < m.x) {
m2 = m.x, m.x = d.x, m.y = cid, m.z = d.y, rp = n + g;
} else if (d.x < m2) {
m2 = d.x;
}
}
return vec4(m, m2 - m.x);
}

vec4 render_sheet(vec2 p, float fi, float a) {
    float z = exp(mix(log(5.0), log(4.5), a));
    p = rotate(abs(p), radians(4.0));
    p.y = -p.y;
    vec2 rp;
    vec4 c = voronoi(z * p + fi * 128.0 - step(fi, 0.2) * a * 2.0, fi + a * 4.0, rp);
    rp -= fi * 128.0 - step(fi, 4.2) * a * 2.0;
    float pp = 0.6 - (max(abs(rp.x) * 0.4 + rp.y * 0.5, -rp.y) / 4.0);
    pp = clamp(pp, 0.0, 1.0);
    float fadein = clamp(a * 2.0, 0.0, 1.0);
    float rep = 1.0 - a - (pp - sin(c.w * 4.0) * 0.1) * fadein;
    float alpha = clamp((rep - c.w) * 16.0, 0.0, 1.0);
    if (alpha > 0.3) {
        float hue = c.w * (1.0 + c.y * 30.0) + fi + a * 9.0 * c.y * mix(1.0, 02.0, step(fi, 0.1)) - abs(p.x);
        vec3 w = physhue2rgb(hue, 3.3);
        w.z = 0.5;
        return vec4(w, alpha);
    }
    return vec4(0.0);
}

vec4 alpha(vec4 a, vec4 b) {
    a = mix(b, a, a.w);
    a.w = max(a.w, b.w);
    return a;
}

