// Rhombic Dodecahedron Traversal - gelami
// https://www.shadertoy.com/view/mlK3DD

/*
 * Flythrough of a packed sphere grid / rhombic dodecahedral honeycomb terrain
 * 
 * Mouse drag to look around
 * Defines in Common
 * 
 * The traversal is simply done by finding the nearest cell position from the grid
 * Then for each step intersects the backfaces of a rhombic dodecahedron
 *   and advancing the cell position using its normal
 * 
 * It's similar to my hexagon grid shader, but really the method
 *   can be used for any tessellation of a space-filling polytope
 * I have a truncated octahedron grid traversal coming up soon as well
 * 
 * Extruded Hexagon Grid Traversal - gelami
 * https://www.shadertoy.com/view/Dly3zm
 * 
 * Bloom setup taken from my bloom shader, with a prefiltering threshold added
 *   and without the 3x3 blur on the upsampling pass:
 * 2-Pass Buffer Bloom - gelami
 * https://www.shadertoy.com/view/cty3R3
 *
 * FXAA 3.11 implementation from:
 * Two Grid Traversal w/ 1Pass DoF - gelami
 * https://www.shadertoy.com/view/dtt3DS
 * 
 * 
 * Other cool space-filling polyhedron shaders:
 * 
 * Octahedral Voxel Tracing - fizzer
 * https://www.shadertoy.com/view/4lcfDB
 *
 * Octahedron Grid Raycasting - Poisson
 * https://www.shadertoy.com/view/NlcBzf
 * 
 * Truncated Octahedral Voxels - dr2
 * https://www.shadertoy.com/view/3dsXWf
 * 
 * Packed Spheres SDF - blackle
 * https://www.shadertoy.com/view/3djBDh
 */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ndc = (2. * (fragCoord) - iResolution.xy) / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    #ifdef MOTION_BLUR

    const float fov = 120.0;
    const float invTanFov = 1.0 / tan(radians(fov) * 0.5);
    const float maxMotionBlur = 12.0;

    float mb = MOTION_BLUR_STRENGTH * dot(ndc, ndc) / invTanFov;
    vec2 dir = normalize(ndc) / iResolution.xy;

    float o = texelFetch(iChannel2, ivec2(fragCoord) % 1024, 0).r;
    o = fract(o + 0.61803398875 * float(iFrame));

    float w = 1.0;

    #define MOTION_BLUR_SAMPLES 8
    for (int i = 0; i < MOTION_BLUR_SAMPLES; i++)
    {
        float t = (float(i) + o) / float(MOTION_BLUR_SAMPLES) - 0.5;
        vec2 p = uv + mb * dir * t;
        if (p == saturate(p))
        {
            col += texture(iChannel0, p).rgb;
            w++;
        }
    }
    col /= w;
    #endif

    #ifdef BLOOM
    vec3 bloom = vec3(0);

    bloom += SampleLod(iChannel1, uv, iResolution.xy, 0).rgb;
    bloom += SampleLod(iChannel1, uv, iResolution.xy, 1).rgb;
    bloom += SampleLod(iChannel1, uv, iResolution.xy, 2).rgb;
    bloom += SampleLod(iChannel1, uv, iResolution.xy, 3).rgb;
    bloom += SampleLod(iChannel1, uv, iResolution.xy, 4).rgb;
    bloom += SampleLod(iChannel1, uv, iResolution.xy, 5).rgb;

    bloom /= 6.0;

    col += bloom * BLOOM_STRENGTH;
    #endif

    #if 0
    col = col / (1.0 + col);
    col = smoothstep(vec3(0), vec3(1), col);
    #else
    col = ACESFilm(col * 0.35);
    #endif

    fragColor = vec4(linearTosRGB(col), 1);
    fragColor += (dot(hash23(vec3(fragCoord, iTime)), vec2(1)) - 0.5) / 255.;
}

