#define BLOOM
#define BLOOM_MAX_LOD 6
#define BLOOM_THRESHOLD 0.6
#define BLOOM_STRENGTH 0.75

#define CAMERA_SPEED 6.5

#define EMI_SCALE 5.0

#define FXAA
#define MOTION_BLUR

#define MOTION_BLUR_STRENGTH 4.0

//#define SIMPLE_TRAVERSAL
#define SPHERES

#define STEPS 256
#define MAX_DIST 100.
#define EPS 1e-4

#define PI (acos(-1.))
#define TAU (PI*2.)

vec4 SampleLod(sampler2D tex, vec2 uv, vec2 res, const int lod)
{
    vec2 hres = floor(res / 2.0);

    vec2 nres = hres;
    float xpos = 0.0;
    int i = 0;
    for (; i < lod; i++)
    {
        xpos += nres.x;

        nres = floor(nres / 2.0);
    }

    vec2 nuv = uv * vec2(nres);

    nuv = clamp(nuv, vec2(0.5), vec2(nres)-0.5);
    nuv += vec2(xpos, 0);

    return texture(tex, nuv / res);
}

float pack(vec2 v)
{
    return uintBitsToFloat(packHalf2x16(v));
}

vec2 unpack(float v)
{
    return unpackHalf2x16(floatBitsToUint(v));
}

// Ray-sphere intersesction
// https://www.iquilezles.org/www/articles/intersectors/intersectors.htm
vec2 sphereIntersection(vec3 ro, vec3 rd, float ra )
{
    vec3 oc = ro;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    if (b > 0.0 && c > 0.0)
        return vec2(MAX_DIST);
    float h = b*b - c;
    if( h<0.0 ) return vec2(MAX_DIST); // no intersection
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}

// Ray-rhombic dodecahedron intersection
vec2 rhombicDodecahedronIntersection(vec3 ro, vec3 rd, float s, out vec3 normal)
{
    const vec3 rdn = vec3(0.5, -0.5, sqrt(2.0)/2.0);

    float tN, tF;
    {
        vec2 ird = 1.0 / rd.xz;
        vec2 n = ro.xz * ird;
        vec2 k = abs(ird) * s;

        vec2 t0 = -n - k;
        vec2 t1 = -n + k;

        tN = max(t0.x, t0.y);
        tF = min(t1.x, t1.y);

        normal = vec3(-sign(ird) * step(t0.yx, t0), 0).xzy;
    }

    {
        vec4 ird = 1.0 / vec4(dot(rd, rdn.xzx), dot(rd, rdn.yzx), dot(rd, rdn.xzy), dot(rd, rdn.yzy));
        vec4 n = ird * vec4(dot(ro, rdn.xzx), dot(ro, rdn.yzx), dot(ro, rdn.xzy), dot(ro, rdn.yzy));
        vec4 k = abs(ird) * s;

        vec4 t0 = -n - k;
        vec4 t1 = -n + k;

        float t = max(max(t0.x, t0.y), max(t0.z, t0.w));

        if (t > tN)
        {
            if (t0.x == t)
                normal = rdn.xzx * -sign(ird.x);
            else if (t0.y == t)
                normal = rdn.yzx * -sign(ird.y);
            else if (t0.z == t)
                normal = rdn.xzy * -sign(ird.z);
            else
                normal = rdn.yzy * -sign(ird.w);
        }

        tN = max(tN, t);
        tF = min(tF, min(min(t1.x, t1.y), min(t1.z, t1.w)));
    }

    if (tN > tF)
    {
        tN = MAX_DIST;
        tF = MAX_DIST;
    }

    return vec2(tN, tF);
}

// Same as above but returns the far normal
vec2 rhombicDodecahedronIntersection2(vec3 ro, vec3 rd, float s, out vec3 normal)
{
    const vec3 rdn = vec3(0.5, -0.5, sqrt(2.0)/2.0);

    float tN, tF;
    {
        vec2 ird = 1.0 / rd.xz;
        vec2 n = ro.xz * ird;
        vec2 k = abs(ird) * s;

        vec2 t0 = -n - k;
        vec2 t1 = -n + k;

        tN = max(t0.x, t0.y);
        tF = min(t1.x, t1.y);

        normal = vec3(-sign(ird) * step(t1, t1.yx), 0).xzy;
    }

    {
        vec4 ird = 1.0 / vec4(dot(rd, rdn.xzx), dot(rd, rdn.yzx), dot(rd, rdn.xzy), dot(rd, rdn.yzy));
        vec4 n = ird * vec4(dot(ro, rdn.xzx), dot(ro, rdn.yzx), dot(ro, rdn.xzy), dot(ro, rdn.yzy));
        vec4 k = abs(ird) * s;

        vec4 t0 = -n - k;
        vec4 t1 = -n + k;

        float t = max(max(t0.x, t0.y), max(t0.z, t0.w));
        float t2 = min(tF, min(min(t1.x, t1.y), min(t1.z, t1.w)));

        if (t2 < tF)
        {
            if (t1.x == t2)
                normal = rdn.xzx * -sign(ird.x);
            else if (t1.y == t2)
                normal = rdn.yzx * -sign(ird.y);
            else if (t1.z == t2)
                normal = rdn.xzy * -sign(ird.z);
            else
                normal = rdn.yzy * -sign(ird.w);
        }

        tN = max(tN, t);
        tF = min(tF, t2);
    }

    if (tN > tF)
    {
        tN = MAX_DIST;
        tF = MAX_DIST;
    }

    return vec2(tN, tF);
}

mat3 getCameraMatrix(vec3 ro, vec3 lo)
{
    vec3 cw = normalize(lo - ro);
    vec3 cu = normalize(cross(cw, vec3(0, 1, 0)));
    vec3 cv = cross(cu, cw);

    return mat3(cu, cv, cw);
}

