// Scene

// Fork of "Gelami Raymarching Template" by gelami. https://shadertoy.com/view/mslGRs
// 2023-05-14 11:59:30

vec3 getID(vec3 p)
{
    p -= 0.5;
    p.y /= sqrt(2.0);

    vec3 id0 = floor(p);
    vec3 id1 = floor(p + 0.5);

    vec3 p0 = fract(p) - 0.5;
    vec3 p1 = fract(p + 0.5) - 0.5;

    p0.y *= sqrt(2.0);
    p1.y *= sqrt(2.0);

    float d0 = dot(p0, p0);
    float d1 = dot(p1, p1);

    vec3 id = d0 < d1 ? id0 : id1 - 0.5;

    id += 0.5;
    id.y *= sqrt(2.0);

    return id;
}

vec3 getLightsID(vec3 p)
{
    const float s = EMI_SCALE;

    p.xz *= rot2D(PI/4.0);
    p.yx *= rot2D(PI/4.0);

    vec3 id = (floor(p / s) + 0.5) * s;

    id.yx *= rot2D(-PI/4.0);
    id.xz *= rot2D(-PI/4.0);

    return id;
}

vec3 IDToPos(vec3 id)
{
    id = id * 0.5 + 0.5;
    id.y *= sqrt(2.0);

    return id;
}

vec3 posToID(vec3 p)
{
    p.y *= 1.0 / sqrt(2.0);
    p = (p - 0.5) * 2.0;
    return floor(p);
}

vec3 getCameraPos(float time)
{
    return vec3(sin(time*0.3)*3.0 + cos(time*0.8)*2.0, cos(time * 0.2) + sin(time * 0.5) * 4.0, time * CAMERA_SPEED);
}

float map(vec3 p)
{
    //p = IDToPos(posToID(p));

    p.y *= -1.0;
    vec3 dp = p - vec3(-2,5,7);

    dp *= 0.1 / 32.0;

    float d = texture(iChannel1, dp).r*0.5;
    d += texture(iChannel1, dp*2.0).r*0.25;
    d += texture(iChannel1, dp*4.0).r*0.125;
    //d += texture(iChannel1, dp*8.0).r*0.0625;

    // 0.9375
    d = d / 0.825 - 0.51;

    p.y *= -1.0;
    vec3 po = texture(iChannel2, 0.3 * p / 32.0).rgb - 0.5;
    vec3 pc = p + po * 4.0;
    float pdc = (length(pc.xy - getCameraPos(pc.z / CAMERA_SPEED).xy)) - 4.8;

    d = max(d * 32.0 / 0.1, -pdc * CAMERA_SPEED);

    return d;
}

vec3 mapGrad(vec3 p, float eps)
{
    vec2 e = vec2(eps, 0);
    return (map(p) - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx)
    )) / e.x;
}

vec3 getColor(vec3 id)
{
    id.y /= sqrt(2.0);
    id = floor(id * 2.0);

    float h = abs((sin(id.x * 0.15) + sin(id.y * 0.25) + cos(id.z * 0.4)) * 0.333);

    return vec3(palette2(h));
    return palette2(hash13(id));
}

vec3 getEmission(vec3 id)
{
    id.y /= sqrt(2.0);
