struct HitInfo
{
    float t;
    vec3 n;
    vec3 id;
};

#ifdef SIMPLE_TRAVERSAL
bool trace(vec3 ro, vec3 rd, out HitInfo hit, float tmax)
{
    vec3 pos = ro;
    vec3 id = getID(pos);
    vec3 n;
    float t = 0.0;

    for (int i = 0; i < STEPS; i++)
    {
        float d = map(id);
        if (d < 0.0)
        {
            #ifdef SPHERES
            if (d < -3.0)
            {
                vec2 st = sphereIntersection(ro - (id + 0.5), rd, 0.5);

                if (st.x > t && st.x < MAX_DIST)
                {
                    hit.t = st.x;
                    hit.n = normalize(ro - (id + 0.5) + rd * st.x);
                    hit.id = id;
                    return true;
                }
            } else
            #endif
            {
                hit.t = t;
                hit.n = n;
                hit.id = id;
                return true;
            }
        }

        vec2 rdi = rhombicDodecahedronIntersection2(pos - id - 0.5, rd, 0.5, n);

        if (rdi.x >= tmax)
            return false;

        t = rdi.y;
        id -= n;
    }
    return false;
}
#else

bool trace(vec3 ro, vec3 rd, out HitInfo hit, float tmax)
{
    const vec3 rdn = vec3(0.5, -0.5, sqrt(2.0)/2.0);
    const mat4x3 rdm = mat4x3(rdn.xzx, rdn.yzx, rdn.xzy, rdn.yzy);
    const float s = 0.5;

    vec3 id = getID(ro);

    vec3 pp = ro;

    vec2 ird0 = 1.0 / rd.xz;
    vec2 k0 = abs(ird0) * s;
    vec2 srd0 = sign(ird0);

    vec3 n0 = -srd0.x * vec3(1, 0, 0);
    vec3 n1 = -srd0.y * vec3(0, 0, 1);

    vec4 ird1 = 1.0 / vec4(dot(rd, rdn.xzx), dot(rd, rdn.yzx), dot(rd, rdn.xzy), dot(rd, rdn.yzy));
    vec4 k1 = abs(ird1) * s;
    vec4 srd1 = sign(ird1);

    vec3 n2 = -srd1.x * rdn.xzx;
    vec3 n3 = -srd1.y * rdn.yzx;
    vec3 n4 = -srd1.z * rdn.xzy;
    vec3 n5 = -srd1.w * rdn.yzy;

    vec3 n;
    float t;
    bool surf = false;

    for (int i = 0; i < STEPS; i++)
    {
        float d = map(id);
        if (d < 0.0)
        {
            #ifdef SPHERES
            if (d < -3.0)
            {
                vec2 st = sphereIntersection(ro - (id + 0.5), rd, 0.5);

                if (st.x > t && st.x < MAX_DIST)
                {
                    hit.t = st.x;
                    hit.n = normalize(ro - (id + 0.5) + rd * st.x);
                    hit.id = id;
                    return true;
                }
            } else
            #endif
            {
                hit.t = t;
                hit.n = n;
                hit.id = id;
                return true;
            }
        }

        vec3 pp = ro - (id + 0.5);

        vec2 id0 = ird0 * pp.xz;
        vec4 id1 = ird1 * vec4(dot(pp, rdn.xzx), dot(pp, rdn.yzx), dot(pp, rdn.xzy), dot(pp, rdn.yzy));

        vec2 sd0 = -id0 + k0;
        vec4 sd1 = -id1 + k1;

        //vec2 sdn0 = -id0 - k0;
        //vec4 sdn1 = -id1 - k1;

        float t0 = min(sd0.x, sd0.y);
        float t1 = min(min(sd1.x, sd1.y), min(sd1.z, sd1.w));

        //float tn0 = max(sdn0.x, sdn0.y);
        //float tn1 = max(max(sdn1.x, sdn1.y), max(sdn1.z, sdn1.w));

        vec3 nrd;
        if (t1 < t0)
        {
            if (sd1.x == t1)
            {
                nrd = n2;
            } else if (sd1.y == t1)
            {
                nrd = n3;
            } else if (sd1.z == t1)
            {
                nrd = n4;
            } else
            {
                nrd = n5;
            }
        } else if (sd0.x == t0)
        {
            nrd = n0;
        } else
        {
            nrd = n1;
        }

        //float tN = max(tn0, tn1);
        float tF = min(t0, t1);

        if (tF >= tmax)
            return false;

        t = tF;
        n = nrd;
        id -= n;
    }

    return false;
}
