void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ndc = (2. * (fragCoord) - iResolution.xy) / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;

    const float fov = 120.0;
    const float invTanFov = 1.0 / tan(radians(fov) * 0.5);

    vec3 ro = getCameraPos(iTime);
    vec3 lo = vec3(0, 0, 1);

    vec2 m = iMouse.xy / iResolution.xy;

    float ax = iMouse.x == 0. ? -PI * .7 + iTime * .2 : -m.x * TAU + PI;
    float ay = iMouse.y == 0. ? PI * 0.2 : -m.y * PI + PI * 0.5;

    if (iMouse.z > 0.0)
    {
        lo.yz *= rot2D(-ay);
        lo.xz *= rot2D(ax);
        lo += ro;
    } else
    {
        lo = getCameraPos(iTime + 0.25);
    }

    mat3 cmat = getCameraMatrix(ro, lo);

    vec3 rd = normalize(cmat * vec3(ndc, invTanFov));

    vec3 col = vec3(0);

    const vec3 rdn = vec3(0.5, -0.5, sqrt(2.0)/2.0);

    #if 0

    vec3 normal;
