        normal = normal3;
    }

    vec3 pos = ro + rd * t;

    col = normal;

    if (t >= MAX_DIST)
        col = rd;

    #else

    vec3 pos = ro;
    vec3 id = getID(pos);
    vec3 n;
    float t = 0.0;

    HitInfo hit;
    bool isHit = trace(pos, rd, hit, MAX_DIST);
    t = isHit ? hit.t : MAX_DIST;
    n = hit.n;
    id = hit.id;

    pos = pos + rd * t;

    id = getID(pos - n * EPS);

    vec3 alb = getColor(id);
    vec3 emi = getEmission(id);

    vec3 fogCol = vec3(0.6, 0.85, 1)*0.6;//vec3(0.03, 0.03, 0.08);
    vec3 ambCol = fogCol;
    vec3 accCol = vec3(1, 0.12, 0.15);

    float ft = smoothstep(MAX_DIST*0.7, MAX_DIST, t) * smoothstep(0.98, 1.0, rd.z);//pow(max(rd.z, 0.0), 20.0) * 2.0;

    fogCol = mix(fogCol, accCol * 5.0, saturate(ft));

    vec3 ref = reflect(rd, n);

    vec3 ligCol = vec3(1, 0.85, 0.7) * 1.5;
    vec3 lpos = getCameraPos(iTime + 1.25);
    vec3 ldir = lpos - pos;
    float ldist = length(ldir);
    ldir /= ldist;

    HitInfo hitL;
    bool isHitL = trace(pos + n * EPS, ldir, hitL, ldist);

    float atten = 1.0 / (0.8 + 0.08*ldist + 0.05*ldist*ldist);

    float dif = max(dot(n, ldir), 0.0) * float(!isHitL);
    float spec = pow(max(dot(ref, ldir), 0.0), 5.0) * float(!isHitL);

    float ao = smoothstep(-2.5, 1.0, map(pos) / length(mapGrad(pos, 1.0)));

    col += alb * ligCol * dif * atten * 1.8;
    col += alb * ligCol * spec * atten * 1.0 * step(hash13(id), 0.5);

    col += (alb * 0.2 + 0.1) * ambCol;

    vec3 emiPos = getID(getLightsID(id));
    vec3 emiDir = emiPos - pos;
    float edist = length(emiDir);
    emiDir /= edist;

    float edif = max(dot(n, emiDir), 0.0);

    HitInfo hitE;
    bool isHitE = trace(pos + n * EPS, emiDir, hitE, edist);

    vec3 eid = getID(pos + n * EPS + emiDir * hitE.t - hitE.n * 1e-3);
    bool isE = isHitE && distance(emiPos, hitE.id) < 1e-3;

    float estr = 10.0 * ((sin((hash13(emiPos) + iTime*0.25)*TAU)*0.5+0.5)*0.8+0.2);
    vec3 ecol = accCol * estr;//vec3(0.5, 0.8, 1) * estr;

    float eAtten = 1.0 / (0.8 + 0.4*edist + 0.3*edist*edist);

    col += alb * edif * ecol * eAtten * float(isE);

    //float r0 = 0.06;
    //float fre = r0 + (1.0 - r0) * pow(1.0 - dot(-rd, n), 5.0);

    col *= ao * 0.8 + 0.2;

    if (distance(emiPos, id) < 1e-3)
        col = ecol;

    col = mix(col, fogCol, 1.0 - exp(-max(t*t - 50.0, 0.0) * 0.0003));

    if (!isHit)
        col = fogCol;
    #endif

    col = max(col, vec3(0));
    col = col / (1. + col);
    col = linearTosRGB(col);

    /*
