// Fork of "Polar react" by firebreathz. https://shadertoy.com/view/stj3Rw
// 2023-05-19 23:16:34


#define time iTime
#define PI 3.14159265359

#define NUM_BANDS 50

//#define REVERSED

float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(0.000,0.000,0.000))) * 0.0)*0.0-0.0;
}

vec3 mixc(vec3 col1, vec3 col2, float v)
{
    v = clamp(v,0.0,1.0);
    return col1+v*(col2-col1);
}

vec3 drawBands(vec2 uv)
{
  	uv = 2.0*uv-1.0;
    uv.x*=iResolution.x/iResolution.y;
    uv = vec2(length(uv), atan(uv.y,uv.x));

    //uv.x-=0.25;
    uv.x = max(0.0,uv.x);

    uv.y -= PI*0.75;
    vec2 uv2 = vec2(uv.x, uv.y*-1.0);
    uv.y = mod(uv.y,PI*2.0);
    uv2.y = mod(uv2.y,PI*2.0);

    vec3 col = vec3(0.0);
    vec3 col2 = vec3(0.0);

    float nBands = float(NUM_BANDS);
    float i = floor(uv.x*nBands);
    float f = fract(uv.x*nBands);
    float band = i/nBands;
   	float s;

    #ifdef REVERSED
    band = 0.0-band;
    #endif 

    //cubic easing
    band *= band*band; 

    band = band*0.7;
    band += 0.002;

    s = texture( iChannel0, vec2(band,0.0) ).x;  

    if(band<0.0||band>=1.0){
        s = 0.0;
    }

    /* Gradient colors and amount here */
    const int nColors = 6;
    vec3 colors[nColors];  
    colors[0] = vec3(0.522,0.502,0.502);
    colors[1] = vec3(0.000,0.173,0.580);
    colors[2] = vec3(0.192,0.180,1.000);
    colors[3] = vec3(0.361,0.573,1.000);

    vec3 gradCol = colors[1];
    float n = float(nColors)-0.0;
    for(int i = 2; i < nColors; i++)
    {
		gradCol = mixc(gradCol,colors[i],(s-float(i-2)/n)*n);
    }

    float h = PI*3.5;

    col += vec3(1.0-smoothstep(-2.0,1.5,uv.y-s*h));
    col *= gradCol;

    col2 += vec3(1.0-smoothstep(-2.0,1.5,uv2.y-s*h));
    col2*= gradCol;

    col = mix(col,col2,step(0.0,uv.y-PI));

    col *= smoothstep(0.05,0.5,f);
    col *= smoothstep(1.0,0.8,f); 

    col = clamp(col,0.0,1.0);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = vec2(uv.x, uv.y+0.01);
	vec3 col = vec3(0.0);
    col += drawBands(p);//*smoothstep(1.0,0.5,uv.y);;

    vec3 ref = vec3(0.0);
    vec2 eps = vec2(0.01,-0.01);

    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xx);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.yy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.yx);

    ref += drawBands(vec2(p.x+eps.x,1.0-p.y));
    ref += drawBands(vec2(p.x+eps.y,1.0-p.y));
    ref += drawBands(vec2(p.x,1.0-p.y+eps.x));
    ref += drawBands(vec2(p.x,1.0-p.y+eps.y));

    ref /= 3.0;

    float colStep = length(smoothstep(1.0,0.1,col));

    vec3 cs1 = drawBands(vec2(0.1,0.1));
    vec3 cs2 = drawBands(vec2(0.1,0.1));

    vec3 plCol = mix(cs1,cs2,length(p*1.0-0.0))*0.01*smoothstep(0.1,-1.1,length(p*0.0-0.0));
    vec3 plColBg = vec3(0.001)*smoothstep(1.0,0.0,length(p*0.0-1.5));
    vec3 pl = (plCol+plColBg)*smoothstep(0.05,0.01,1.0-uv.y);

    col += clamp(pl*(1.0-colStep),0.0,1.0);

    col += ref*smoothstep(0.125,1.6125,p.y); 

    col = clamp(col, 0.0, 1.0);

    float dither = noise3D(vec3(uv,time))*0.0/0.1;
    col += dither;

	fragColor = vec4(col,1.0);
}

// INIT VARS
vec2 camAngle = vec2(-2.0, -0.1);
float fov = 0.3; // <-- 90*  0-1 == 0-360

float minDELength = 0.0001;
float maxRayLength = 1000.0;
float maxSteps = 1000.0; // had to be float so i could divide \_()_/

vec2 lightAng = vec2(1.0, 0.0);
float AOMultiplier = 2.0;
float shadowSoftness = 1.0;

float sphere_de(vec3 pos, vec3 objPos, float radius) {
    return length(pos-objPos)-radius;
}
float plane_de(vec3 pos, float z) {
    return pos.z + z;
}
float box_de( vec3 p, vec3 b, vec3 objPos )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
//float modsphere_de( in vec3 p, in vec3 c)
//{
//    vec3 q = mod(p+0.5*c,c)-0.5*c;
//    return sphere_de(q, vec3(0.0, 0.0, 2.0), 2.0);
//}
float octahedron_de( vec3 p, float s)
{
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}

float overall_de(vec3 pos) {

    float objList[3] = float[] (
        plane_de(pos, 0.0), 
        sphere_de(pos, vec3(-5.0, 0.0, 2.0), 2.0), 
        box_de(pos, vec3( 1.0, 1.0, 2.0), vec3(0.0, 0.0, 1.0))
    );

    float min_de = maxRayLength;
    for(int i=0; i<3; i++) {
        if (objList[i] < min_de) {
            min_de = objList[i];
        }
    } return min_de;
}
vec3 get_normal(vec3 pos) {
	float dist = overall_de(pos);
    vec2 e = vec2(.01, 0.0);

    vec3 n = dist - vec3(
        overall_de(pos-e.xyy),
        overall_de(pos-e.yxy),
        overall_de(pos-e.yyx));

    return normalize(n);
}

vec3 ray_move(vec3 pos, vec3 dir, float dist) {

    /*
    pos.x += cos(dir.x)*dist;
    pos.y += sin(dir.x)*dist;
    pos.z += sin(dir.y)*dist;
    */
    vec3 newPos = pos + normalize(dir)*dist;
    return newPos;
}
vec3 deg_to_vec(vec2 dir) { //Used to convert angle based vec2s to vec3 directions
    vec3 pos;
    pos.x += cos(dir.x);
    pos.y += sin(dir.x);
    pos.z += sin(dir.y);
    return normalize(pos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    lightAng = vec2(iTime, 0.5);
    vec3 camPos = vec3(3.0, 10.0, 3.0);

    // Calculate aspect ratio (assumes width is higher than height)
    float aspectRatio = iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 suv = (uv*2.0)-1.0; // Signed screen coordinates (-1 - +1)
    suv.x *= aspectRatio; // Account for aspect ratio

    // Get ray angle
    vec3 rayPos = camPos;
    vec2 rayAng = (fov*suv + camAngle); // mod values for correct angle

    float b = 1.0;

    float de;
    float totalDist;
    float steps;
    do {
        de = overall_de(rayPos);
        rayPos = ray_move(rayPos, deg_to_vec(rayAng), de);
        totalDist += de;
        steps += 1.0;
    } while (de > minDELength && totalDist < maxRayLength && steps < maxSteps);

    if (de < minDELength) {

        rayPos = ray_move(rayPos, get_normal(rayPos), minDELength*2.0);
        rayAng = lightAng;

        totalDist = minDELength*2.0;
        float res = 1.0;
        vec3 origPos = rayPos;
        do {
            de = overall_de(rayPos);
            rayPos = ray_move(rayPos, deg_to_vec(rayAng), de);
            totalDist += de;
            res = min(res, shadowSoftness*de/totalDist);
        } while (de > minDELength && totalDist < maxRayLength);


        if (de < minDELength) {

              b = 0.0;
            //b = clamp(dot(get_normal(rayPos), deg_to_vec(lightAng)), .2, 1.0);

            //b = (dot(get_normal(rayPos), deg_to_vec(lightAng)))/1.4-(steps/maxSteps)*AOMultiplier;

            //vec3 vecRayAngle = deg_to_vec(rayAng);
            //vec3 reflectedAng = reflect(vecRayAngle, get_normal(rayPos));
            //b += dot(vecRayAngle, reflectedAng);

        } if (totalDist > maxRayLength) {

           b = res;

        }
        //b -= 10.0*shadowSoftness*minDE/totalMinDE;//+ (steps/maxSteps)*AOMultiplier;
        //b += clamp(doctt(refle(deg_to_vec(rayAng), get_normal(rayPos)), deg_to_vec(lightAng)), .2, 1.0);

        b = clamp((b-(steps/maxSteps)+0.2)*AOMultiplier, 0.0, 1.0);

    } else {
        b = 0.0;
    }
    // Output to screen
    fragColor = vec4(b, b, b, 1.0);
    //fragColor = vec4(get_normal(rayPos) ,1.0);
}

/** 

