// BUFFER A (0.24) of VoidWorm by QuantumSuper
// draw a morphing shape with polar sound texure dilation & shift it through buffer
// 
// - use with audio in iChannel0 of Buffer A -


#define aTime 128./60.*iTime
#define speed max(iResolution.x,iResolution.y)/1200.
#define isVDJ false

float makeVDJ(float amp){ //workaround for VirtualDJ, attempts to correct for ?limiter?
	return clamp(amp + .3*amp*pow(amp,5.),.0,1.);
}

vec3 makeFfts(){ //sound compression of speech range
    vec3 ffts = vec3(0);

	// Sound (assume sound texture with 44.1kHz in 512 texels, cf. https://www.shadertoy.com/view/Xds3Rr)
    for (int n=6;n<8;n++) ffts.x  += getDat( iChannel0, vec2(n,0)).x; //speech I, 517-689Hz
    for (int n=8;n<14;n+=2) ffts.y  += getDat( iChannel0, vec2(n,0)).x; //speech II, 689-1206Hz
    for (int n=14;n<24;n+=4) ffts.z  += getDat( iChannel0, vec2(n,0)).x; //speech III, 1206-2067Hz
	ffts /= vec3(2,3,3); //normalize
	if (isVDJ) for (int n=0;n++<3;) ffts[n] = makeVDJ(ffts[n]);
    return ffts;
}

mat2 rotM(float r){float c = cos(r), s = sin(r); return mat2(c,s,-s,c);} //2D rotation matrix

float sdCircle(vec2 p){
    return length(p)-1.;
}

float sdEquilateralTriangle(vec2 p){ //source: https://iquilezles.org/articles/distfunctions2d/
    const float k = sqrt(3.);
    p.x = abs(p.x) - 1.;
    p.y = p.y + 1./k;
    if (p.x+k*p.y > 0.) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.;
    p.x -= clamp( p.x, -2., 0.);
    return -length(p)*sign(p.y);
}

float sdMix(vec2 p){
    return mix( sdCircle(p), sdEquilateralTriangle(p), sin(aTime/4.)/2.+.5);
}

float getShape(vec2 p, float r){ //get visual representation of sdFunctions
    r = abs(sdMix(p/(r+.001)));
    return smoothstep(fwidth(r),.0,r);
}

void mainImage( out vec4 col, in vec2 fC){

    // View definition
    vec2 isMaxRes = vec2( iResolution.x>=iResolution.y, iResolution.x<iResolution.y); // "bool" vector indicating if direction is max resolution
    vec2 uv = (2.*fC-iResolution.xy) / dot(iResolution.xy,isMaxRes); //long edge -1 to 1, square aspect ratio
    vec2 shift = vec2( sign(speed)*.4+.02*sin(aTime), .2*sin(aTime/8.)*sin(iTime));
    uv += mat2(shift,shift.yx) * isMaxRes; //movement
    uv *= rotM(aTime/16.); //rotate

    // View manipulation
    float rho = atan(-abs(uv.x),uv.y)/PI+1.; //polar angle, flipped & mirrored
    rho = (pow(10.,rho)-1.)/9.; //exponential frequency scale of pow10 to increase visibility of lower frequencies
    float amp = texelFetch( iChannel0, ivec2( 512.*rho, 0), 0).x; //get sound texture
    if (isVDJ) amp = makeVDJ(amp);
    uv /= max(.001, .6+.4*pow(amp,6.)); //warp polar view by sound texture

    // Draw Shape
    col = vec4(.2*getShape(uv,.4));
    if (dot(fC,isMaxRes)>speed+1.)
        col += .997*getDat( iChannel1, fC-max(1.,abs(speed))*sign(speed)*isMaxRes); //move shape

    // Storage
    if (fC.x<1. && fC.y<1.) col.xyz = makeFfts(); //?fC.x+fC.y<1. does not work?
}

