#define PI 3.14159
float Sharp(float v, float n)
{
    v = 1.-v;
    v*= v;
    return max(0., 1.-n*v);
}
vec4 BackgroundColor(vec3 vd)
{
    vd = normalize(vd);
    vec3  a = abs(vd);
    float m = max(max(a.x, a.y), a.z);
    vec2 uv;
    if      (m == a.x)
    {
        uv = vec2(vd.z, vd.y);
    }
    else if (m == a.y)
    {
        uv = vec2(vd.x, vd.z);
    }
    else
    {
        uv = vec2(vd.x, vd.y);
    }
    vec4 col = vec4(0);
    mat2 rot = mat2(vec2(0.8, 0.6), vec2(-0.6, 0.8));
    uv *= rot;
    vec4 r1  = texture(iChannel0, uv/4.);
    uv *= rot;
    vec4 r2  = texture(iChannel0, uv/2.);
    uv *= rot;
    vec4 r3  = texture(iChannel0, uv/1.);

    col += (vec4(1.0, 1.0, 1.0, 0)                              )*Sharp(r1.r*r1.b, 9.)*3.;
    col += (vec4(0.4, 0.7, 1.0, 0) + vec4(0.6, 0.3, 0.0, 0)*r1.a)*Sharp(r1.g*r1.r, 9.)*3.;
    col += (vec4(1.0, 0.8, 0.4, 0) + vec4(0.0, 0.2, 0.6, 0)*r1.a)*Sharp(r1.b*r1.g, 9.)*3.;

    col += (vec4(1.0, 1.0, 1.0, 0)                              )*Sharp(r2.r*r1.a, 4.);
    col += (vec4(0.4, 0.7, 1.0, 0) + vec4(0.6, 0.3, 0.0, 0)*r2.a)*Sharp(r2.g*r1.b, 4.);
    col += (vec4(1.0, 0.8, 0.4, 0) + vec4(0.0, 0.2, 0.6, 0)*r2.a)*Sharp(r2.b*r1.g, 4.);

    col += (vec4(1.0, 1.0, 1.0, 0)                              )*Sharp(r3.r*r1.a*r2.a, 2.)*0.5;
    col += (vec4(0.4, 0.7, 1.0, 0) + vec4(0.6, 0.3, 0.0, 0)*r3.a)*Sharp(r3.g*r1.a*r2.a, 2.)*0.5;
    col += (vec4(1.0, 0.8, 0.4, 0) + vec4(0.0, 0.2, 0.6, 0)*r3.a)*Sharp(r3.b*r1.g*r2.a, 2.)*0.5;

    return col;
}
// Checks if a ray (launched from position, in direction) hits a sphere (with radius) on the origin.
bool SphereHit(float radius, vec3 position, vec3 direction)
{
    return (dot(position, direction)*dot(position, direction) > dot(direction, direction)*(dot(position, position)) - radius*radius);
}
// Calculates the distance to the far part of the sphere
float SphereDist(float radius, vec3 position, vec3 direction)
{
    float d = -dot(position, direction) + sqrt(dot(position, direction)*dot(position, direction) - dot(direction, direction)*(dot(position, position) - radius*radius));
    return d;
}
float SphereThrough(float radius, vec3 position, vec3 direction)
{
    float d = 2.*sqrt(dot(position, direction)*dot(position, direction) - dot(direction, direction)*(dot(position, position) - radius*radius));
    return d;
}
// Calculates the normal on the surface of the sphere (equivalent to the position, for a unit sphere)
vec3 SphereNormal(float radius, vec3 position, vec3 direction)
{
    return normalize(vec3(position + direction*SphereDist(radius, position, direction)));
}
float Density(vec3 position)
{
    position *= (1.+0.05*sin(iTime*0.5));
    float r1 = texture(iChannel1, (position+vec3(0.1, 0.2, 0.3)*0.2*iTime)/6.).r;
    float r2 = texture(iChannel1, (position+vec3(0.1, 0.2, 0.3)*0.5*iTime)/3.).g;
    float r3 = texture(iChannel1, position/2.).b;
    float r4 = texture(iChannel1, position/1.).a;

    float ra = texture(iChannel1, (position+vec3( 0.1, 0.04, 0.08)*0.2*iTime)/4.).g;
    float rb = texture(iChannel1, (position+vec3(-0.1,-0.2, 0.1)*0.1*iTime)/6.).g;
    ra *= ra;
    rb *= rb;
    float rq  = ((1.-ra) + (1.-rb))*pow(1.-dot(position, position), 1.3);

    float rt = r1 + r2 + r3 + r4;
    rt+= smoothstep(0.9, 1.2, rq)*2.;
    rt = exp(rt - 2.);
    rt*= 1.-dot(position, position);
    return rt;
}
vec4 Absorb(vec3 position, float density)
{
    float rsq = dot(position, position);
    return vec4(0.3, 0.4, 0.6, 0)*density*density*2.;
}
vec4 SIC(vec3 position, float density)
{
    float rsq = dot(position, position);
    vec4 col = mix(vec4(0.8, 0.7, 0.4, 0), vec4(0.2, 0.6, 0.9, 0), 2.*(rsq+0.1*sin(0.3*iTime)+0.5-exp(-density)))*(1.-0.2*sin(iTime*0.5));
    return col;
}
vec4 Add(vec3 position, float density, float jumpedDensity)
{
    float rsq = dot(position, position);
    vec4 selfIllum = SIC(position, density)*(1.-rsq)/(0.001+pow(rsq, 1.8))*0.1*(1.+3./pow(jumpedDensity, 2.));
    vec4 extIllum  = mix(vec4(0.7, 0.4, 0.2, 0), vec4(1), rsq)*length(Absorb(position, density))*rsq*0.2;
    return tanh((selfIllum + extIllum)/SELFABS)*SELFABS;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float theta = (iMouse.x - iResolution.x / 2.) / iResolution.y * PI;
    float phi   = (iMouse.y - iResolution.y / 2.) / iResolution.y * PI;

    // CAMERA
    vec3 iCameraFwd     = vec3(sin(theta)*cos(phi),sin(phi),cos(theta)*cos(phi));
    vec3 iCameraUp      = vec3(-sin(theta)*sin(phi),cos(phi),-cos(theta)*sin(phi));
    vec3 iCameraRight   = normalize(cross(iCameraUp, iCameraFwd));

    vec3 iCameraPosition= -3.*iCameraFwd;

    float m = 1.0;

    vec3 iViewDirection = iCameraFwd + ((fragCoord.x - iResolution.x/2.0) * iCameraRight + (fragCoord.y - iResolution.y/2.0) * iCameraUp) / iResolution.x * m;
    iViewDirection      = normalize(iViewDirection);

    float rmin= length(cross(iViewDirection, iCameraFwd));
    fragColor = BackgroundColor(iViewDirection - (0.003/pow(rmin, 3.))*cross(cross(iViewDirection, iCameraFwd), iCameraFwd));

    if (SphereHit(1., iCameraPosition, iViewDirection))
    {
        vec3 startPos = iCameraPosition + iViewDirection*SphereDist(1., iCameraPosition, iViewDirection);
        float marchDis= SphereThrough(1., iCameraPosition, iViewDirection);
        for (float n = 0.; n < NJUMPS; n++)
        {
            vec3 pos = startPos - iViewDirection*marchDis*((n+0.5)/NJUMPS);
            float density = Density(pos);
            float jd      = sqrt(Density(pos*0.5)*Density(pos*0.8));
            fragColor *= exp(-Absorb(pos, density    )*(marchDis/NJUMPS));
            fragColor +=         Add(pos, density, jd)*(marchDis/NJUMPS);
        }
    }
}

