// 2-Pass Buffer Bloom - gelami
// https://www.shadertoy.com/view/cty3R3

/* 
 * A bloom implementation using only 2 buffer passes by making use of texture mipmaps
 * 
 * Since Shadertoy only has a few buffers available, we are not able to do the
 *   standard bloom methods which does multiple passes for the downsampling and upsampling
 * So instead, we can use the mipmaps with a large blur kernel for the downsampling
 * and a small blur in the upsampling to accomplish a similar thing
 * 
 * For the first pass (Buffer B), we render each mipmap level at the same time
 *   with an 11x11 gaussian blur applied for the downsampling
 * Then the second pass (Image) simply adds all the mipmaps together,
 *   with a 3x3 gaussian blur pass to remove some of the blockiness from the mipmaps
 * 
 * The 3x3 gaussian blur in the second pass was a bit of a bottleneck,
 *   so it is optimized to 4 taps using bilinear filtering.
 * More info about it on the article below
 * 
 * Buffer A: Rendering the shapes
 * Buffer B: 1st pass, downsampling
 * Image: 2nd pass, upsampling
 * Defines in Common
 * 
 * 
 * Resources:
 * 
 * Custom Bloom Post-Process in Unreal Engine - Froyok
 * https://www.froyok.fr/blog/2021-12-ue4-custom-bloom/
 * 
 * Next generation post processing in Call of Duty: Advanced Warfare - Jorge Jimenez
 * http://www.iryoku.com/next-generation-post-processing-in-call-of-duty-advanced-warfare
 * 
 * Optimized gaussian blur kernel using bilinear filtering
 * Optimizing Convolutions - John Chapman
 * https://john-chapman.github.io/2019/03/29/convolution.html
 * 
 * 
 * Other cool bloom shaders:
 *
 * Gargantua With HDR Bloom - sonicether
 * https://www.shadertoy.com/view/lstSRS
 *
 * Buffer pass bloom - robobo1221
 * https://www.shadertoy.com/view/lsBfRc
 */

vec4 SampleLodBlurred(sampler2D tex, vec2 uv, vec2 res, const int lod)
{
    vec4 result = vec4(0);
    float sc = exp2(float(lod));
    vec2 nres = floor(res / sc * 0.5);

    #ifndef OPTIMIZED_UPSAMPLE_BLUR
    const float[9] weights = float[](
        1., 2., 1.,
        2., 4., 2.,
        1., 2., 1.
    );

    float w = 0.0;
    for (int x = -1; x <= 1; x++)
    {
        for (int y = -1; y <= 1; y++)
        {
            vec2 o = vec2(x, y);
            float wg = weights[x + 1 + (y + 1) * 3];
            vec2 p = uv + o / nres;

            result += wg * SampleLod(iChannel1, saturate(p), iResolution.xy, lod);
            w += wg;
        }
    }
    result /= w;

    // Optimized blur using bilinear filtering
    // https://john-chapman.github.io/2019/03/29/convolution.html
    #else

    const vec2[4] offsets = vec2[](
        vec2(-1.0/3.0, -1.0/3.0),
        vec2( 1.0/3.0, -1.0/3.0),
        vec2(-1.0/3.0,  1.0/3.0),
        vec2( 1.0/3.0,  1.0/3.0)
    );

    for (int i = 0; i < 4; i++)
    {
        vec2 o = offsets[i];
        vec2 p = uv + o / nres;

        result += SampleLod(iChannel1, saturate(p), iResolution.xy, lod);
    }
    result *= 0.25;

    #endif

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0);

    #ifndef UPSAMPLE_BLUR
    col += SampleLod(iChannel1, uv, iResolution.xy, 0).rgb;
    col += SampleLod(iChannel1, uv, iResolution.xy, 1).rgb;
    col += SampleLod(iChannel1, uv, iResolution.xy, 2).rgb;
    col += SampleLod(iChannel1, uv, iResolution.xy, 3).rgb;
    col += SampleLod(iChannel1, uv, iResolution.xy, 4).rgb;
    col += SampleLod(iChannel1, uv, iResolution.xy, 5).rgb;

    #else

    // Skip 3x3 blur on first 3 mips
    #if 1
    col += SampleLod(iChannel1, uv, iResolution.xy, 0).rgb;
    col += SampleLod(iChannel1, uv, iResolution.xy, 1).rgb;
    col += SampleLod(iChannel1, uv, iResolution.xy, 2).rgb;
    #else
    col += SampleLodBlurred(iChannel1, uv, iResolution.xy, 0).rgb;
    col += SampleLodBlurred(iChannel1, uv, iResolution.xy, 1).rgb;
    col += SampleLodBlurred(iChannel1, uv, iResolution.xy, 2).rgb;
    #endif

    col += SampleLodBlurred(iChannel1, uv, iResolution.xy, 3).rgb;
    col += SampleLodBlurred(iChannel1, uv, iResolution.xy, 4).rgb;
    col += SampleLodBlurred(iChannel1, uv, iResolution.xy, 5).rgb;

    #endif

    col /= 6.0;
    col = mix(texture(iChannel0, uv).rgb, col, BLOOM_FACTOR);

    //col = texture(iChannel1, uv).rgb;
    //col = textureLod(iChannel0, uv, 4.0).rgb;
    //col = SampleLod(iChannel1, uv, iResolution.xy, 4).rgb;

    // Tonemappers
    //col = col / (1.0 + col);
    //col = ACESFilm(col * 0.55);
    //col = Uncharted2Tonemap(col);
    //col = ReinhardExt(col, 5.5);
    col = ReinhardExtLuma(col, 2.5); //5.5
    //col = col / (1.0 + luminance(col));
    //col = GlobalLogTonemap(col, 0.0, 3.5, 0.15);

    fragColor = vec4(linearTosRGB(col), 1);
    fragColor += (dot(hash23(vec3(fragCoord, iTime)), vec2(1)) - 0.5) / 255.;
}

