// Fork of "Gelami Raymarching Template" by gelami. https://shadertoy.com/view/mslGRs
// 2023-05-17 14:41:51

// https://iquilezles.org/articles/distfunctions2d/
// https://www.shadertoy.com/view/3tSGDy
float sdStar(in vec2 p, in float r, in int n, in float m) // m=[2,n]
{
    // these 4 lines can be precomputed for a given shape
    float an = 3.141593/float(n);
    float en = 3.141593/m;
    vec2  acs = vec2(cos(an),sin(an));
    vec2  ecs = vec2(cos(en),sin(en)); // ecs=vec2(0,1) and simplify, for regular polygon,

    // reduce to first sector
    float bn = mod(atan(p.x,p.y),2.0*an) - an;
    p = length(p)*vec2(cos(bn),abs(sin(bn)));

    // line sdf
    p -= r*acs;
    p += ecs*clamp( -dot(p,ecs), 0.0, r*acs.y/ecs.y);
    return length(p)*sign(p.x);
}

// https://www.shadertoy.com/view/3tyBzV
float dot2(vec2 p) { return dot(p, p); }
float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float smootherstep(float f)
{
    return f*f*f*(f*(f*6.0-15.0)+10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pv = (2. * (fragCoord) - iResolution.xy) / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0);

    col = vec3(1, 0.5, 1);

    float t = iTime * 0.8;

    #ifdef ROTATION
    #if 0
    pv *= rot2D(t * 0.8);
    #else
    pv *= rot2D(smootherstep(fract(t * 0.5 / PI)) * TAU);
    #endif
    #endif

    vec2 p1 = pv - vec2(0.8, 0);
    vec2 p2 = (pv + vec2(0.8, 0.2)) * 2.6;

    #ifdef ROTATION
    p1 *= rot2D(t * 1.0);

    p2.y -= 0.55;
    p2 *= rot2D(t * 1.0);
    p2.y += 0.55;
    #endif

    float d = length(pv) - 0.2;
    float r = 0.02;
    float st = sdStar(p1, 0.25 - r, 5, 3.0) - r;
    float sh = sdHeart(p2) / 2.6 - 0.02;

    if (st < d)
    {
        d = st;
        col = vec3(1, 0.5, 0.2);
    }
    if (sh < d)
    {
        d = sh;
        col = vec3(0.2, 0.5, 1);
    }

    /*
    float sb = abs(sdBox(pv, vec2(1.6, 0.8))) - 0.01;
    if (sb < d)
    {
        d = sb;
        col = vec3(1, 0.8, 0.6);
    }*/

    col *= smoothstep(2.0 / iResolution.y, 0.0, d);

    col *= EXPOSURE;
    col = max(col, vec3(0));

    fragColor = vec4(col, 1);
}

