#define PI 3.1415926535897932384626433832795

float regularPolygonDist(int sides, vec2 point){
    float dist = -point.x;
    for(int side = 1; side < sides; side++){
        float angle = PI + 2.*PI*float(side)/float(sides);
        dist = max(dist, dot(point, vec2(cos(angle), sin(angle))));
    }
    dist /= cos(float(PI/float(sides)));
    return dist;
}

vec2 componentToPolar(vec2 point){
    return vec2(length(point), atan(point.y, point.x));
}

vec2 polarToComponent(vec2 polar){
    return polar.x*vec2(cos(polar.y), sin(polar.y));
}

vec2 rotatePoint(vec2 point, float angle){
    return polarToComponent(componentToPolar(point) + vec2(0., angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.y;

    float weight = smoothstep(-0.1, 0.1, sin(iTime));
    //float weight = sin(iTime)/2.+.5;
    float shape =
        regularPolygonDist(3, rotatePoint(uv, -iTime/4.))*weight +
        regularPolygonDist(4, rotatePoint(uv, -iTime/2.))*(1. - weight);

    float col = 0.;
    col += smoothstep(1., 0.95, shape);
    //col -= shape;
    fragColor = vec4(vec3(col), 1.0);
}

