/*
Pathtracer by TPG45

feel free to use as a base for other projects, just give credit
also feel free to expand on this, just give credit for the base pathtracer

Includes:
- lighting, diffuse + specular + anisotropic
- refraction
- volumetrics
- DOF & blur
- multipass sampling
- normal maps
*/

// Shader Settings
#define FOCAL_LENGTH 500.0
#define CAM_DISTANCE 1150.0
#define MAX_BOUNCES 10
#define SAMPLES 4
#define DOF_AMOUNT 1.0
#define BLUR_AMOUNT 2.0
#define SCENE 0

#define VOLUME_STEP_SIZE 10.0

// Other random definitions
#define T true
#define F false
#define PHI 1.61803398874989484820459
#define PI 3.1415926535897932384626433832795028841971

struct Material {

    vec3 color;
    vec3 emission;
    float emissionStrength;

    float sheen;
    float roughness;
    float metallic;
    float aniso;
    float anisoStrength;

    float transparency;
    float ior;

    float density;

};

struct Sphere {

    Material material;
    vec3 center;
    float radius;

};

struct Triangle {

    Material material;
    vec3 a;
    vec3 b;
    vec3 c;
    vec2 auv;
    vec2 buv;
    vec2 cuv;

};

struct Plane {

    Material material;
    vec3 normal;
    float height;
    float scale;

};

struct Cube {

    Material material;
    vec3 position;
    vec3 size;

};

struct Ray {

    vec3 origin;
    vec3 normal;

};

struct Record {

    Ray ray;
    bool hit;
    Material material;
    vec3 normal;
    vec3 intersect;
    float dist;
    vec3 offset;
    vec3 tangent;
    vec3 bitangent;
    int refrac;
    int objID;
    int hitID;
    bool inVolume;
    vec3 volEmission;
    float volDensity;

};

mat2 rot2( float a ){ vec2 v = sin(vec2(1.570796, 0) + a);	return mat2(v, -v.y, v.x); }
// Extra trig + hyperbolic functions by me, if you want to use please credit.
float cot(float x){return cos(x)/sin(x);}vec2 cot(vec2 x){return cos(x)/sin(x);}vec3 cot(vec3 x){return cos(x)/sin(x);}vec4 cot(vec4 x){return cos(x)/sin(x);}float sec(float x){return 1./cos(x);}vec2 sec(vec2 x){return 1./cos(x);}vec3 sec(vec3 x){return 1./cos(x);}vec4 sec(vec4 x){return 1./cos(x);}float csc(float x){return 1./sin(x);}vec2 csc(vec2 x){return 1./sin(x);}vec3 csc(vec3 x){return 1./sin(x);}vec4 csc(vec4 x){return 1./sin(x);}float coth(float x){float a=exp(x);float b=exp(-x);return(a+b)/(a-b);}vec2 coth(vec2 x){vec2 a=exp(x);vec2 b=exp(-x);return(a+b)/(a-b);}vec3 coth(vec3 x){vec3 a=exp(x);vec3 b=exp(-x);return(a+b)/(a-b);}vec4 coth(vec4 x){vec4 a=exp(x);vec4 b=exp(-x);return(a+b)/(a-b);}float sech(float x){return 2./(exp(x)+exp(-x));}vec2 sech(vec2 x){return 2./(exp(x)+exp(-x));}vec3 sech(vec3 x){return 2./(exp(x)+exp(-x));}vec4 sech(vec4 x){return 2./(exp(x)+exp(-x));}float csch(float x){return 2./(exp(x)-exp(-x));}vec2 csch(vec2 x){return 2./(exp(x)-exp(-x));}vec3 csch(vec3 x){return 2./(exp(x)-exp(-x));}vec4 csch(vec4 x){return 2./(exp(x)-exp(-x));}

uint raySphere(in Sphere sph, inout Record rec) {
    vec3 offset = rec.ray.origin - sph.center;
    float a = 2.0 * dot(offset, rec.ray.normal);
    float b = dot(offset, offset) - sph.radius * sph.radius;
    float disc = a * a - 4.0 * b;
    rec.objID++;
    if (disc > 0.0) {
        float s = sqrt(disc);
        float dstNear = max(0.0,(-a-s)/2.0);
        float dstFar = (-a+s)/2.0;
        if(dstNear < rec.dist) {
            if(dstNear > 0.0) {

                rec.intersect = (rec.ray.normal * dstNear) + rec.ray.origin;
                rec.normal = normalize(rec.intersect - sph.center);
                rec.dist = dstNear;
                rec.material = sph.material;
                rec.hit = true;
                rec.offset = (rec.intersect - sph.center - sph.radius) / sph.radius - 0.5;
                rec.tangent = normalize(vec3(rec.normal.z, 0.0, -rec.normal.x));
                rec.bitangent = normalize(cross(rec.normal, rec.tangent));
                rec.hitID = rec.objID;
                rec.refrac = 1;
                return uint(0x1);

            } else if(dstFar > 0.0) {

                rec.inVolume = rec.inVolume || sph.material.density > 0.0;
                rec.volEmission += sph.material.emission * sph.material.emissionStrength;
                rec.volDensity += sph.material.density;

                if(dstFar < rec.dist) {

                    rec.intersect = (rec.ray.normal * dstFar) + rec.ray.origin;
                    rec.normal = normalize(rec.intersect - sph.center);
                    rec.dist = dstFar;
                    rec.material = sph.material;
                    rec.hit = true;
                    rec.offset = (rec.intersect - sph.center - sph.radius) / sph.radius - 0.5;
                    rec.tangent = normalize(vec3(rec.normal.z, 0.0, -rec.normal.x));
                    rec.bitangent = normalize(cross(rec.normal, rec.tangent));
                    rec.hitID = rec.objID;
                    rec.refrac = -1;

                    return uint(0x1);

                }

            }
        }
        return uint(0x0);
    }
}
uint rayTri(in Triangle tri, inout Record rec) {
    vec3 ab = tri.b - tri.a;
    vec3 ac = tri.c - tri.a;
    vec3 bc = tri.c - tri.b;
    vec3 normal = ab.yzx * ac.zxy - ab.zxy * ac.yzx;
    vec3 ao = rec.ray.origin - tri.a;
    vec3 dao = cross(ao, rec.ray.normal);    
    float det = 1.0 / (-dot(rec.ray.normal, normal));    
    float dist = dot(ao, normal) * det;
    float u = dot(ac, dao) * det;
    float v = -dot(ab, dao) * det;
    float w = 1.0 - u - v;    
    bool hit = det >= 1e-6 && dist >= 0.0 && u >= 0.0 && v >= 0.0 && w >= 0.0;    
    rec.objID++;
    if(hit && dist < rec.dist) {    
        rec.intersect = (rec.ray.normal * dist) + rec.ray.origin;
        rec.normal = normalize(normal);
        rec.dist = dist;
        rec.material = tri.material;
        rec.hit = true;
        rec.offset = vec3(u, v, w);

        float xba = tri.buv.x - tri.auv.x;
        float xcb = tri.cuv.x - tri.buv.x;
        float yba = tri.buv.y - tri.auv.y;
        float ycb = tri.cuv.y - tri.buv.y;
        rec.tangent = normalize(ycb * ab - yba * bc);
        rec.bitangent = normalize(-xcb * ab + xba * bc);
        rec.hitID = rec.objID;
        rec.refrac = 1;

        return uint(0x1);
    }
    return uint(0x0);
}
uint rayPlane(in Plane plane, inout Record rec) {

    rec.objID++;
    vec3 dao = cross(rec.ray.origin, rec.ray.normal);
    float det = 1.0 / (-dot(rec.ray.normal, plane.normal));
    float dist = dot(rec.ray.origin, plane.normal) * det;

    if(dist >= 0.0 && det >= 1e-6) {

        if(dist < rec.dist) {

            rec.intersect = (rec.ray.normal * dist) + rec.ray.origin;
            rec.normal = plane.normal;
            rec.dist = dist;
            rec.material = plane.material;
            rec.hit = true;
            rec.offset = rec.intersect.xyz * plane.scale;

            rec.tangent = normalize(vec3(rec.normal.z, 0.0, -rec.normal.x));
            if(rec.normal.x + rec.normal.z == 0.0) {

                rec.tangent = vec3(1.0, 0.0, 0.0);

            }
            rec.bitangent = normalize(cross(rec.normal, rec.tangent));
            rec.hitID = rec.objID;
            rec.refrac = 1;

            return uint(0x1);

        }

    }

    return uint(0x0);

}
uint rayCube(in Cube cube, inout Record rec) {

    vec3 o = rec.ray.origin - cube.position;

    rec.objID++;

    vec3 m = sign(rec.ray.normal) / max(abs(rec.ray.normal), 1e-8);

    vec3 n = m * o;
    vec3 k = abs(m) * cube.size;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

    float dist;

    if (tN > tF || tF <= 0.) {

        return uint(0x0);

    } else {

        if (tN >= 0.0001) {

            if(tN < rec.dist) {

                rec.normal = -sign(rec.ray.normal) * step(t1.yzx,t1.xyz) * step(t1.zxy,t1.xyz);
                rec.dist = tN;
                rec.intersect = rec.dist * rec.ray.normal + rec.ray.origin;
                rec.material = cube.material;
                rec.hit = true;
                rec.offset = vec3(0.0);
                rec.tangent = normalize(vec3(rec.normal.z, 0.0, -rec.normal.x));
                if(rec.normal.x + rec.normal.z == 0.0) {

                    rec.tangent = vec3(1.0, 0.0, 0.0);

                }
                rec.bitangent = normalize(cross(rec.normal, rec.tangent));
                rec.hitID = rec.objID;
                rec.refrac = 1;
                return uint(0x1);

            }

        } else if (tF >= 0.0001) {

            rec.inVolume = rec.inVolume || cube.material.density > 0.0;
            rec.volEmission += cube.material.emission * cube.material.emissionStrength;
            rec.volDensity += cube.material.density;

            if (tF < rec.dist) { 

                rec.normal = -sign(rec.ray.normal) * step(t1.yzx,t1.xyz) * step(t1.zxy,t1.xyz);
                rec.dist = tF;
                rec.intersect = rec.dist * rec.ray.normal + rec.ray.origin;
                rec.material = cube.material;
                rec.hit = true;
                rec.offset = vec3(0.0);
                rec.tangent = normalize(vec3(rec.normal.z, 0.0, -rec.normal.x));
                if(rec.normal.x + rec.normal.z == 0.0) {

                    rec.tangent = vec3(1.0, 0.0, 0.0);

                }
                rec.bitangent = normalize(cross(rec.normal, rec.tangent));
                rec.hitID = rec.objID;
                rec.refrac = -1;

                return uint(0x1);

            }

        } else {

            return uint(0x0);

        }

    }

}

vec3 random(inout uvec3 v) {

    v = v * 1664525u + 1013904223u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    v ^= v >> 16u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    return vec3(v)*(1.0/float(0xffffffffU));

}
vec3 randomNormal(vec3 n, inout uvec3 state) {

    vec2 rv2 = random(state).xy;

    vec3  uu = normalize( cross( n, vec3(0.0,1.0,1.0) ) );
	vec3  vv = cross( uu, n );

	float ra = sqrt(rv2.y);
	float rx = ra*cos(6.2831*rv2.x); 
	float ry = ra*sin(6.2831*rv2.x);
	float rz = sqrt( 1.0-rv2.y );
	vec3  rr = vec3( rx*uu + ry*vv + rz*n );

    return normalize( rr );

}
vec3 randomScattering(uvec3 state) {
    float a = random(state).x * 6.2831853;
    float x = random(state).x * 2.0 - 1.0;
    float z = random(state).x;

	return pow(z, 1.0 / 3.0) * vec3(sqrt(1.0 - x * x) * vec2(sin(a), cos(a)), x);
}

Material randomMaterial(in uvec3 state) {

    vec3 color = random(state);

    Material mat = Material(

        color,
        color,
        pow(random(state).x, 14.0) * 20.0,
        random(state).x,
        random(state).x,
        random(state).x,
        0.0,
        0.0,
        0.0,
        1.5,
        0.0
    );
    return mat;

}

float F_Schlick(float f0, float theta) {

    return f0 + (1.0 - f0) * pow(1.0 - theta, 5.0);

}

