void initRay( in vec2 fragCoord, inout Ray ray, uvec3 state ) {
    vec2 mouse = iMouse.xy/iResolution.xy - 0.5;
    ray.origin = vec3(0.0, 0.0, -FOCAL_LENGTH) + vec3(random(state).xy * DOF_AMOUNT, 0.0);
    ray.normal = normalize(vec3(fragCoord + random(state).x * BLUR_AMOUNT, 0.0) - ray.origin);
    ray.origin.z -= CAM_DISTANCE;
    #if 0
    mat2 rot = rot2(-0.7);
    ray.origin.yz *= rot; ray.normal.yz *= rot;
    rot = rot2(0.0);
    ray.origin.xz *= rot; ray.normal.xz *= rot;
    #else
    mat2 rot = rot2(8.0 * mouse.y);
    ray.origin.yz *= rot; ray.normal.yz *= rot;
    rot = rot2(8.0 * mouse.x);
    ray.origin.xz *= rot; ray.normal.xz *= rot;
    #endif
}

void initRecord(inout Record rec, in Ray ray) {

    rec.ray = ray;
    rec.hit = false;
    rec.dist = 100000.0;
    rec.volEmission = vec3(0.0);
    rec.volDensity = 0.0;
    rec.objID = 0;

}

vec3 worldSpace(vec3 map, vec3 t, vec3 b, vec3 n) {

    mat3x3 tbn = mat3x3(t.x, b.x, n.x, t.y, b.y, n.y, t.z, b.z, n.z);
    return normalize(map * tbn);

}

vec3 Trace( in vec2 fragCoord, in uvec3 state ) {

    vec3 final = vec3(0.0);

    for( int i = 0; i < SAMPLES; i++ ) {

        Ray ray;
        Record rec;
        initRay( fragCoord, ray, state );
        initRecord( rec, ray );
        rec.refrac = -1;

        vec3 color = vec3(0.0);
        vec3 increment = vec3(1.0);

        for( int j = 0; j < MAX_BOUNCES; j++ ) {

            distances( rec );

            if( rec.hit ) {

                #if 0

                return rec.material.color;

                #endif

                #if 0

                return vec3(rec.material.sheen, rec.material.roughness, rec.material.metallic);

                #endif

                float c = 0.0;

                if(rec.inVolume) {

                    c = (rec.dist / VOLUME_STEP_SIZE) * rec.volDensity;

                }

                if(c > random(state).x) {

                    rec.ray.origin += (random(state).x * rec.dist) * rec.ray.normal;
                    rec.ray.normal = randomScattering(state);

                    color += rec.volEmission * increment;

                } else {

                    vec3 matColor = rec.material.color;

                    float det = (1.0/(rec.material.roughness+1.0));

                    float theta = dot(rec.normal, -rec.ray.normal);
                    float sheen = F_Schlick(rec.material.sheen, theta);
                    float refrac = rec.material.transparency / sheen;

                    float spec = float(sheen > random(state).x);

                    vec3 diffuseCol = matColor * (1.0 - rec.material.metallic);
                    vec3 specCol = mix(vec3(1.0), matColor, rec.material.metallic * 0.5);

                    vec3 anisoNorm = normalize(vec3(0.0, random(state).x * sign(random(state).x * 2.0 - 1.0) * rec.material.anisoStrength, 1.0));
                    vec3 diffuseNorm = mix(randomNormal(rec.normal, state), rec.normal, spec * det);

                    rec.ray.origin = rec.intersect + rec.normal * 0.05;

                    rec.normal = mix(diffuseNorm, worldSpace(anisoNorm, rec.tangent, rec.bitangent, rec.normal), rec.material.aniso);

                    if(refrac > random(state).x || rec.refrac == -1) {

                        if(rec.refrac == 1) {

                            rec.ray.origin = rec.intersect - rec.normal * 0.05;;
                            rec.ray.normal = refract(rec.ray.normal, rec.normal, clamp(1.0 / rec.material.ior, 0.0, 1.0));

                        } else {

                            rec.ray.origin = rec.intersect + rec.normal * 0.05;
                            rec.ray.normal = refract(rec.ray.normal, -rec.normal, clamp(rec.material.ior, 0.0, 1.0));

                        }

                    } else {

                        rec.ray.normal = reflect(rec.ray.normal, rec.normal);

                    }

                    initRecord(rec, rec.ray);

                    color += (rec.material.emission * rec.material.emissionStrength) * increment;
                    increment *= mix(diffuseCol, specCol, spec);

                }

            } else {

                color += texture(iChannel0, rec.ray.normal).xyz * increment;
                break;

            }

        }

        final += color;

    }

    final /= float(SAMPLES);

    return final;

}

