    vec2 coord = ratio * fragCoord;

    vec3 det = vec3(coord, iFrame);

    uvec3 state = floatBitsToUint(det);

    fragColor = vec4(Trace( coord - ratio * iResolution.xy / 2.0, state ), 1.0);

}

