vec3 skyColor( in vec3 rd )
{
    vec3 sundir = normalize( vec3(.0, .1, 1.) );

    float yd = min(rd.y, 0.);
    rd.y = max(rd.y, 0.);

    vec3 col = vec3(0.);

    col += vec3(.4, .4 - exp( -rd.y*20. )*.15, .0) * exp(-rd.y*9.); // Red / Green 
    col += vec3(.4, .6, .8) * (1. - exp(-rd.y*8.) ) * exp(-rd.y*.9) ; // Blue

    col = mix(col*1.2, vec3(.3),  1.-exp(yd*100.)); // Fog

    col += vec3(1.0, .8, .55) * pow( max(dot(rd,sundir),0.), 15. ) * 0.6; // Sun
    col += pow(max(dot(rd, sundir),0.), 150.0) * 4.0;

    return col * 1.2;
}

