        vec2 uv = (-R.xy + 2.*(F+o))/max(R.x,R.y);
#else    
        vec2 uv = (-R.xy + 2.*F)/max(R.x,R.y);
#endif
        vec3 color = render(uv,F);       
        color = 1.35*color/(1.+color);
