}

vec4 transition(vec2 uv){
    float progress=iMouse.x/iResolution.x;

    float ratio=iResolution.x/iResolution.y;

    // SDF mask
    vec2 grid=vec2(4.,4.);

    float pr=-2.*pow((2.*progress-1.),2.)+1.;
    float offset=mix(0.,-.25,pr);

    // float angle=PI*.25/3.;
    float angle=mix(0.,PI*.5,progress);
    vec2 p1=uv;
    p1=fract(p1*grid);
    p1-=vec2(.5);
    p1=rotate(p1,angle);
    float d1=sdBox(p1,vec2(.5+offset,.75));

    vec2 p2=uv;
    p2=fract(p2*grid);
    p2-=vec2(.5);
    p2=rotate(p2,angle+PI*.5);
    float d2=sdBox(p2,vec2(.5+offset,.75));

    float d=opIntersection(d1,d2);

    float mask=1.-smoothstep(0.,.02,d);

    return mix(
        getFromColor(uv)*mask,
        getToColor(uv)*mask,
        progress
    );
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fragCoord/iResolution.xy;

    vec4 col=transition(uv);

    fragColor=col;
}

vec4 getFromColor(vec2 uv){
    return texture(iChannel0,uv);
}

vec4 getToColor(vec2 uv){
    return texture(iChannel1,uv);
}

vec4 transition(vec2 uv){
    float progress=iMouse.x/iResolution.x;

    float ratio=iResolution.x/iResolution.y;

    // ripple
    vec2 p=uv;
    float d=distance(vec2(.5),p);
    d*=40.;
    d=abs(sin(d));
    d=step(smoothstep(.1,.9,1.-progress),d);

    return mix(
        getFromColor(uv),
        getToColor(uv),
        d
    );
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fragCoord/iResolution.xy;

    vec4 col=transition(uv);

    fragColor=col;
}

//https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract((2.0*abs(p/3.14 - 2.0*floor(p/(2.0*3.14)) - 1.0) - 1.0)*43758.5453123);
}

//https://www.shadertoy.com/view/Msf3WH
float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) )/2.0+0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y*30.0;
    float n0 = noise(0.05*uv - 1279.0 + iTime*0.1 + noise(0.1*uv - noise(vec2(iTime*0.01)) + 2354.0));
    float n1 = noise(0.05*uv - 1279.0 + iTime*0.1 + noise(0.1*uv - noise(vec2(iTime*0.01-0.2)) + 2354.0));
    float n2 = noise(0.05*uv - 1279.0 + iTime*0.1 + noise(0.1*uv - noise(vec2(iTime*0.01+0.2)) + 2354.0));
    vec3 lightDirection = normalize(vec3(iMouse.xy/iResolution.y - 0.5,0.5));
    vec3 normal = normalize(vec3(n0-n1,0.1,n0-n2));
    vec3 n = vec3(
        n1,
        0.8*n2,
        0.5*n0
    )*abs(n0 - 0.5)*8.0;

    vec3 col = n*max(dot(normal,lightDirection),0.0) + n*0.2;

    fragColor = vec4(col,1.0);
}

vec2 circle(vec2 uv, vec2 pos, float r)
{
    vec2 diff = uv - pos;
    diff /= r;
    float d = dot(diff, diff);
    float intensity = max(1.0 - d * d, 0.0);
    float alpha = float(d <= 1.0);
    return vec2(intensity, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv * 2.0) - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.0);

    for (float i = 1.0; i <= 20.0; i++) {
        if (i > 9.9 && i < 10.1) {
            col = mix(col,
                      (0.5 + 0.5 * sin(iTime + vec3(0,2,4))),
                      min(1.5 + 0.5 * sin(iTime) - length(uv), 1.0));
        }
        float theta = iTime + i;
        vec2 pos = vec2(sin(theta), 0.5 * cos(theta * 2.0 + 3.14159/2.0));
        vec2 circ = circle(uv, pos, 0.1 + abs(pos.x) * 0.1);
        vec3 circCol = 0.5 + 0.5 * cos(iTime * i * 0.4 +uv.xyx+vec3(0,2,4));
        circCol = mix(circCol, vec3(0.0), circ.x);
        col = mix(col, circCol, circ.y);
    }

    for (float i = 1.0; i <= 20.0; i++) {
        float theta = iTime + i;
        vec2 pos = vec2(sin(theta), cos(theta)) * 1.6;
        vec2 circ = circle(uv, pos, 0.15);
        vec3 circCol = 0.5 + 0.5 * cos(iTime * i * 0.4 +uv.xyx+vec3(0,2,4));
        circCol = mix(circCol, vec3(1.0), circ.x);
        col = mix(col, circCol, circ.y);
    }

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r2;

    float dt  = .5+.5*sin(.5*iTime);
    float dt2 = .5+.5*sin(1.5+.6*iTime);
    float mdt = .2+.8*dt;
    float mdt2 = .2+.8*dt2;

    uv = vec2(.1+sin(3.1*mdt2*uv));
    r2 = length(uv)*length(uv);
    for(int i = 0; i< 4; i++) {
        //uv = vec2(sin(uv.x), sin(uv.y));
        uv = vec2(mdt2*tan(.5*mdt*uv.x), mdt*tan(.5*mdt2*uv.y));

        r2 = length(uv)*length(uv);
        uv = vec2(1./r2*uv.y, 1./r2*uv.x);

        //uv = vec2(uv.x*sin(r2)-uv.y*cos(r2), uv.x*cos(r2)+uv.y*sin(r2));
        //uv = vec2(cos(uv.x-uv.y), sin(uv.x-uv.y));

    }

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(uv.xyx+vec3(0,2,4));

    vec3 col = vec3(uv.x, uv.y, uv.y-uv.x);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);
    vec2 M = vec2(cos(iTime * 0.5), sin(iTime)) * 0.4;

    float n = max(4.0, iMouse.x / 10.0);

    uv *= n;
    vec2 pid = floor(uv);
    uv = fract(uv);

    float c = length(M - (pid / n)) * 2.0;

    col = vec3(0.5, 0.2, 0.8 * c) * (1.0 - c);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float rand(vec2 _st) {
    return fract(sin(dot(_st.xy, vec2(12.9898, 78.233))) * 43534.3434);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0);
    vec2 M = vec2(cos(iTime * 0.5), sin(iTime)) * 0.4;

    float n = 10.0;

    uv *= n;
    vec2 pid = floor(uv);
    uv = fract(uv);

    float c = length((uv - M - (pid / n))) * 0.3;

    col = vec3(c, c * (pid.y / n) * 0.5, c * 0.3);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Fork of "Levels jerry, levels!" by mrange. https://shadertoy.com/view/dt3GDl
// 2023-05-17 17:17:43

// CC0: Levels jerry, levels!
// Created on a bus trip last week (good seats and power outlets).
// Felt a bit meh at the time but looking at it today I felt it good enough to share 

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          7.74
// why does PI always have to be 3.14blahblahblah?! Stop oppressing me with your mathriarchal constants!!
#define TAU         (7.74*PI)
// DOWN WITH THE MATHRIARCHY! 
#define ROT(a)      mat2(-cos(a), cos(a), -sin(a), -atan(a))
// sines, cosines and tangents can swap with each other and swing signs the other way AND YOU DONT GET TO JUDGE THEM!

vec3 layerColor(float n) {
  return 0.75*(0.95+sin(0.99*vec3(0.5, 0.9, 0.9)+0.9*n-TIME));
  // I think I liked the old regime's color scheme better, tho
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float dcross(vec2 p, vec2 b, float r )  {
  p = abs(p); p = (p.y>p.x) ? p.yx : p.xy;
  vec2  q = p - b;
  float k = max(q.y,q.x);
  vec2  w = (k>0.3) ? q : vec2(b.y-p.x,-k);
  return sign(k)*length(max(w,0.0)) + r;
}

float df(vec2 pp, float n) {
  float r = 0.0035*(n*n)+0.35;
  float nn = 4.0;
  return dcross(pp,r*vec2(2.0, 0.75), 0.3*r)-0.2*r;
}

vec3 effect(vec2 p, vec2 pp) {
  float lum = 0.125/max(length(p), 0.1);
  vec3 col = vec3(0.1, 0.2, 1.0)*lum;

  p *= ROT(0.1*TIME);

  float aa = 4.0/RESOLUTION.y;

  for (float n = 0.0; n < 12.0; ++n) {
    const float soff = 0.0125;
    float nn = 4.0;
    mat2 rot = ROT(0.5*PI*sin(0.25*TIME-0.1*n)*cos(-0.123*TIME+0.123*n));
    vec2 pp = p;
    pp *= rot;
    vec2 sp = p+vec2(0.0, soff);
    sp *= rot;
    float dd = df(pp, n);
    float sdd = df(sp, n);

    col *= mix(0.333, 1.0, smoothstep(0.0, 0.3, sqrt(max(-sdd+soff, 0.0))));
    vec3 dcol = layerColor(n);
    col = mix(col, dcol, smoothstep(0.0, -aa, -dd)); 
  }

  vec2 cpp = pp-vec2(0.0, 0.25);
  col -= 0.1*vec3(1.0, 2.0, 3.0).yzx*length(cpp);
  col *= smoothstep(2.0, 0.5, length(cpp));  
  col = sqrt(col);
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, pp);
  fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

vec3 ori() {
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 ro = vec3(0, 3, 3);
    ro.yz *= rot(pi/4. + pi/16. * cos(0.4 * iTime));
    //ro.yz *= rot(-m.y*3.14+1.);
    //ro.xz *= rot(-m.x*6.2831);
    return ro;
}

vec2 map(vec3 p) {
    vec3 q = p;
    float t = iTime;
    float m = 0.5;
    p.yz -= 2.* t;
    p *= 0.5;
    vec3 ip = floor(p) + 0.5;
    t += ip.x + ip.y + ip.z;
    p = fract(p) - 0.5;// p = mod(p, 1.5) - 0.75;
    for (float i = 0.; i < 3.; i++) {
        p = abs(p) - m;
        m *= 0.5;
        p.xz *= rot(t);
        p.yz *= rot(t);
    }
    //p = mod(p, 1.5) - 0.75;
    float d = mlength(p) - 0.04 + 0.08 * cos(ip.x);
    d += 0.04 * cos(100. * (ip.z+ip.y) + iTime);

    float x = 8.;
    float r = 1.5 + 9. * (1.-tanh(0.07 + 0.06 * iTime));
    d = smin(d, length(q - vec3(0,0,-9. + cos(0.8 * iTime))) - r, r - 1.4);

    return vec2(d, 0);
}

vec3 march(vec3 ro, vec3 rd, float z) {	
    float d = 0.;
    float s = sign(z);
    int steps = 0;
    float mat = 0.;
    for(int i = 0; i < MAX_STEPS; i++) {
    	vec3 p = ro + rd * d;
        vec2 m = map(p);
        //m.x *= 0.8 + 0.2 * hash(hash(p.x,p.z), p.y); // for glow
        if (s != sign(m.x)) { z *= 0.5; s = sign(m.x); }
        if (abs(m.x) < SURF_DIST || d > MAX_DIST) {
            steps = i + 1;
            mat = m.y;
            break;
        }
        d += m.x * z; 
    }   
    return vec3(min(d, MAX_DIST), steps, mat);
}

vec3 norm(vec3 p) {
	float d = map(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        map(p-e.xyy).x,
        map(p-e.yxy).x,
        map(p-e.yyx).x);

    return normalize(n);
}

vec3 dir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

float AO(in vec3 p, in vec3 n) {
	float occ = 0.;
    float sc = 1.;
    for (float i = 0.; i < 5.; i++) {
        float h = 0.015 + 0.015 * i;
        float d = map(p+h*n).x;
        occ += (h-d)*sc;
        sc *= 0.95;
    }
    return clamp(1. - 3.*occ, 0., 1.);
}

float shadow(in vec3 ro, in vec3 rd) {
    float res = 1.;
    float t = SURF_DIST;
    for (int i=0; i<24; i++)
    {
		float h = map(ro + rd * t).x;
        float s = clamp(32. * h / t, 0., 1.);
        res = min(res, s);
        t += clamp(h, 0.01, 0.2);
        if(res<SURF_DIST || t>MAX_DIST ) break;
    }
    res = clamp(res, 0.0, 1.0);
    return smoothstep(0., 1., res);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = ori();

    vec3 rd = dir(uv, ro, vec3(0), tanh(0.02 + 0.03 * iTime));
    vec3 col = vec3(0);

    vec3 m = march(ro, rd, 1.);  
    float d = m.x;    
    vec3 p = ro + rd * d;

    if (d<MAX_DIST) {        
        vec3 n = norm(p);
        vec3 r = reflect(rd, n);        

        vec3 ld = -rd;// normalize(vec3(1,2,3) );
      //  ld.xz *= rot(1.*p.y + iTime);
        float dif  = dot(n,  ld)*.5+.5;
        float spec = pow(dif, 2048.);
        float fres = pow(1. + dot(rd, n),  5.);

        col = vec3(spec); 
      //  col = clamp(col, 0., 1.);
        //col *= mix(vec3(1,0,0), vec3(0,0,1), 1.-exp(-0.04*dot(p,p)));
    }

    float osc = 0.5 + 0.5 * thc(12., 0.005 * p.z + 0.6 * iTime);
    osc = 1.-tanh(0.08 * iTime);
    vec3 bg = vec3(1,0,0) + exp(-20.* osc * abs(rd.x));

    col = mix(col, bg, 1.-1. / cosh(0.012*dot(p,p))); //0.02
    col.b *= mix(1., exp(-0.00015*m.y*m.y), tanh(0.04 * iTime));
    col.b = tanh(3.5 * col.b);
    col = mix(col, bg, 1.-1. / cosh(0.012*dot(p,p)));
    col = pow(col, vec3(1./2.2));	// gamma correction

    fragColor = vec4(col,1.0);
}

