// License CC0: Fortress Harkonnen
// Inspired by: http://www.fractalforums.com/new-theories-and-research/very-simple-formula-for-fractal-patterns/

// SABS from ollj
#define LESS(a,b,c) mix(a,b,step(0.,c))
#define SABS(x,k)   LESS((.5/k)*x*x+k*.5,abs(x),abs(x)-k)

#define PI      3.141592654
#define TAU     (0.25*3.141592654)
#define TIME    iTime
#define PERIOD  0.1
#define PERIODS 2.0

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

float plane(vec2 p, vec2 n, float m) {
  return dot(p, n) + m;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

float holey(float d, float k) {
  return abs(d) - k;
}

float tanh2(float x) {
  // Hack around precision problem
  if (abs(x) > 80.0) {
    return sign(x);
  } else {
    return tanh(x);
  }
}

float nfield(vec2 p, vec2 c) {
  vec2 u = p;

  float a = 0.0;
  float s = 3.0;


  for (int i = 0; i < 25; ++i) {
    float m = dot(u,u);
    u = SABS(u, 0.0125)/m + c;
    u *= pow(s, 0.95);
    a += pow(s, 18.0)*m;
    s *= 0.75;
  }

  return -tanh2(0.125*a);
}

vec3 normal(vec2 p, vec2 c) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(2.0/iResolution.y, 0);

  vec3 n;
  n.x = nfield(p + e.xy, c) - nfield(p - e.xy, c);
  n.y = 1.0*e.x;
  n.z = nfield(p + e.yx, c) - nfield(p - e.yx, c);

  return normalize(n);
}

vec3 field(vec2 p, vec2 c) {
  vec2 u = p;

  float a = 0.0;
  float s = 3.0;

  vec2 tc = vec2(0.5, 0.3);
  rot(tc, TAU*TIME/PERIOD);
  vec2 tpn = normalize(vec2(1.0));
  float tpm = 0.0 + 1.4*tanh(length(p));

  float tcd = 1E10;
  float tcp = 1E10;

  for (int i = 0; i < 18; ++i) {
    float m = dot(u,u);
    u = SABS(u, 0.0125)/m + c;
    tcd = min(tcd, holey(circle(u-tc, 0.05), -0.1));
    tcp = min(tcp, holey(plane(u, tpn, tpm), -0.1));
    u *= pow(s, 0.5);
    a += pow(s, 1.0)*m;
    s *= 0.75;
  }

  return vec3(tanh(0.125*a), tanh(tcd), tanh(tcp));

}

vec3 postProcess(vec3 col, vec2 q) {
  col=pow(clamp(col,0.0,1.0),vec3(.75)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= iResolution.x/iResolution.y;

  float currentPeriod = mod(floor(TIME/PERIOD), PERIODS);
  float timeInPeriod = mod(TIME, PERIOD);

  p *= 0.25 + (0.05*timeInPeriod) + pow(1.35, currentPeriod);
  vec2 c = vec2(-0.5, -0.55);

  vec3 gp = vec3(p.x, 1.0*tanh(1.0 - (length(p))), p.y);
  vec3 lp1 = vec3(-1.0, 1.5, 1.0);
  vec3 ld1 = normalize(lp1 - gp);
  vec3 lp2 = vec3(1.0, 1.5, 1.0);
  vec3 ld2 = normalize(lp2 - gp);
  vec3 f = field(p, c);

  vec3 n = normal(p, c);

  float diff1 = max(dot(ld1, n), 0.0);
  float diff2 = max(dot(ld2, n), 0.0);

  vec3 col = vec3(0.0);

  const vec3 dcol1 = vec3(0.3, 0.5, 0.7).xyz;
  const vec3 dcol2 = 0.5*vec3(0.7, 0.5, 0.3).xyz;
  const vec3 scol1 = 0.5*vec3(1.0);
  const vec3 scol2 = 0.5*0.5*vec3(1.0);

  col += diff1*dcol1;
  col += diff2*dcol2;
  col += scol1*pow(diff1, 10.0);
  col += scol2*pow(diff2, 3.0);
  col -= vec3(tanh(f.y-0.1));
  col += 0.5*(diff1+diff2)*(1.25*pow(vec3(f.z), 5.0*vec3(1.0, 4.0, 5.0)));

  col = postProcess(col, q);

  const float fade = 0.0;
  float fadeIn  = smoothstep(0.0, fade, timeInPeriod);
  float fadeOut = 1.0-smoothstep(PERIOD - fade, PERIOD, timeInPeriod);
  col *= fadeIn*fadeOut;

  fragColor = vec4(col, 1.0);
}

// "Hexagonally Discretized Waves" by dr2 - 2023
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

// Wave watching on a hexagonal grid (mouseable)

/*
  No. 7 in "Hexagon Waves" series
    "Cookie Waves"         (wlSSWy)
    "Paper Rolls"          (WlKSRd)
    "Truchet Waves"        (3tScDc)
    "Edible Edifices"      (3ljBWt)
    "Gold Edifices"        (NldSzM)
    "Losing Focus 2"       (sdSBzc)
*/

#define AA  0   // (= 0/1) optional antialiasing

#define VAR_GRID_SIZE   0  // (= 0/1)

#define VAR_ZERO min (nFrame, 0)

vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float HexEdgeDist (vec2 p, float h);
float Minv3 (vec3 p);
float SmoothMax (float a, float b, float r);
vec3 HsvToRgb (vec3 c);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Noisev2v4 (vec4 p);

vec3 qHit, ltDir;
vec2 cId, cMid;
float dstFar, tCur, hgSize, wavHt, whFac;
int nFrame, idObj;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p - vec3 (cMid, wavHt).xzy;
  d = SmoothMax (HexEdgeDist (q.xz, hgSize - 0.005), q.y, 0.1);
  DMINQ (1);
  return dMin;
}

float WaveHt (vec2 p, float tWav)
{ // (from "Barque Fleet")
  vec4 t4;
  vec2 q, t, tw;
  float wFreq, wAmp, h;
  q = p;
  wFreq = 1.;
  wAmp = 1.;
  tw = tWav * vec2 (1., -1.);
  h = 0.;
  for (int j = VAR_ZERO; j < 3; j ++) {
    t4 = wFreq * (q.xyxy + tw.xxyy);
    t4 = abs (sin (t4 + 2. * Noisev2v4 (t4).xxyy - 1.));
    t4 = (1. - t4) * (t4 + sqrt (1. - t4 * t4));
    t = 1. - sqrt (t4.xz * t4.yw);
    t *= t;
    h += wAmp * dot (t, t);
    q *= mat2 (1.6, -1.2, 1.2, 1.6);
    wFreq *= 2.;
    wAmp *= 0.25;
  }
  return h;
}

void SetConf ()
{
  cMid = HexToPix (cId * hgSize);
  wavHt = whFac * WaveHt (0.01 * cMid, 0.1 * tCur);
}

float ObjRay (vec3 ro, vec3 rd)
{ // (mod from "Cookie Waves")
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM;
  float dHit, d, s, eps;
  bool cNu;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  eps = 0.001;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]), dot (ro.xz, edN[2])) / hgSize;
  dHit = 0.;
  cId = PixToHex (ro.xz / hgSize);
  pM = HexToPix (cId);
  cNu = true;
  for (int j = VAR_ZERO; j < 400; j ++) {
    if (j == 0 || cNu) {
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
      SetConf ();
      cNu = false;
    }
    d = ObjDf (ro + dHit * rd);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      cId = PixToHex ((ro.xz + dHit * rd.xz) / hgSize);
      pM += sqrt3 * edN[(s == hv.x) ? 0 : ((s == hv.y) ? 1 : 2)];
      cNu = true;
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 cIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  cIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    cId = PixToHex (p.xz / hgSize);
    if (cId != cIdP) {
      cIdP = cId;
      SetConf ();
    }
    h = ObjDf (p);
    sh = min (sh, smoothstep (0., 0.1 * d, h));
    d += 0.05;
    if (sh < 0.05 || d > 5.) break;
  }
  return 0.7 + 0.3 * sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, bgCol;
  float dstObj, sh, h, nDotL;
  whFac = 16.;
  dstObj = ObjRay (ro, rd);
  bgCol = vec3 (0.4, 0.4, 0.6);
  col = bgCol;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    h = clamp (wavHt / (2.4 * whFac), 0., 1.);
    col4 = vec4 (HsvToRgb (vec3 (fract (0.7 * (0.9 - h)), 0.7, 1.)), 0.1);
    col4 *= 1. - 0.1 * ((qHit.y < -0.01) ? smoothstep (0.5, 0.6, sin (4. * pi * qHit.y)) :
       smoothstep (0.5, 0.6, sin (12. * pi * HexEdgeDist (qHit.xz/ hgSize, 1.))));
    sh = ObjSShadow (ro + 0.01 * vn, ltDir);
    nDotL = max (dot (vn, ltDir), 0.);
    col = col4.rgb * (0.2 + 0.3 * max (dot (vn, ltDir * vec3 (-1., 1., -1.)), 0.) +
       0.8 * sh * pow (nDotL, 1.5)) +
       col4.a * step (0.95, sh) * pow (max (dot (reflect (ltDir, vn), rd), 0.), 32.);
    col = mix (col, bgCol, smoothstep (0.8, 1., dstObj / dstFar));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr, sMax;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
#if VAR_GRID_SIZE
  sMax = 4.;
  hgSize = exp2 (sMax - 0.5 - abs (floor (mod (0.3 * tCur, 2. * sMax)) - sMax + 0.5));
#else
  hgSize = 1.;
#endif
  az = 0.1 * pi;
  el = -0.12 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  el = clamp (el, -0.4 * pi, -0.1 * pi);
  vuMat = StdVuMat (el, az);
  ro = vec3 (2. * cos (0.1 * tCur), 60., tCur);
  zmFac = 3.;
  dstFar = 300.;
  ltDir = normalize (vec3 (1., 0.7, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    rd = vuMat * rd;
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), 1.5 * h.y);
}

float HexEdgeDist (vec2 p, float h)
{
  p = abs (p);
  p -= vec2 (0.5, - sqrt3/2.) * min (p.x - sqrt3 * p.y, 0.);
  p.x -= h * sqrt3/2.;
  return sign (p.x) * max (abs (p.x), abs (p.y) - 0.5 * h);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + vec4 (0., 1., 57., 58.)) * cHashM);
}

vec2 Noisev2v4 (vec4 p)
{
  vec4 ip, fp, t1, t2;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t1 = Hashv4f (dot (ip.xy, vec2 (1., 57.)));
  t2 = Hashv4f (dot (ip.zw, vec2 (1., 57.)));
  return vec2 (mix (mix (t1.x, t1.y, fp.x), mix (t1.z, t1.w, fp.x), fp.y),
               mix (mix (t2.x, t2.y, fp.z), mix (t2.z, t2.w, fp.z), fp.w));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r2;

    uv += .2*vec2(sin(.3*iTime));
    r2 = length(uv)*length(uv);
    for(int i = 0; i< 7; i++) {
        uv = vec2(tan(uv.x), tan(uv.y));

        r2 = length(uv)*length(uv);
        uv = vec2(1./r2*uv.y, 1./r2*uv.x);

        //uv = vec2(uv.x*sin(r2)-uv.y*cos(r2), uv.x*cos(r2)+uv.y*sin(r2));
        //uv = vec2(cos(uv.x-uv.y), sin(uv.x-uv.y));
        //uv = vec2(tan(uv.x), tan(uv.y));

    }

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(uv.xyx+vec3(0,2,4));

    vec3 col = vec3(uv.x, uv.y, uv.y-uv.x);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 O, vec2 f ) {
  vec3 v=texelFetch(iChannel0,ivec2(f),0).rgb;
  O.rgb=min(12.9*v,abs(1.054*pow(v,v-v+.4166)-.095)+.04);// https://www.shadertoy.com/view/sl3cRs
}

