void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y ; 

    vec2 uv = fragCoord/iResolution.xy*vec2(aspect,1.0);

    vec4 textureColor = texture(iChannel0, fragCoord/iResolution.xy);

    textureColor *= 0.96;

    vec2 circ_center = 0.5*vec2(aspect*(1.0+cos(2.0*iTime)),(1.0 + sin(2.0*iTime)));

    vec2 circ_center_2 = 0.5*vec2(aspect*(1.0 + cos(3.7*iTime)),(1.0 + sin(2.5*iTime)));

    float circ = step(0.06, distance(uv, circ_center)) - step(0.03, distance(uv, circ_center));

    float circ2 = step(0.06, distance(uv, circ_center_2)) - step(0.03, distance(uv, circ_center_2));
    vec4 circ_color = vec4(1.0);
    textureColor = mix(textureColor, circ_color, -circ);
    textureColor = mix(textureColor, circ_color, -circ2);



    // line sdf (from https://www.shadertoy.com/view/3s2fDG)
    vec2 line_direction = circ_center - circ_center_2;
    vec2 point_on_line = circ_center_2;
    float segment_length = length(line_direction);

    float projected_distance = dot(normalize(line_direction), uv - point_on_line);
    vec2 closest_point = point_on_line + projected_distance * line_direction / segment_length;

    float distance_from_line =  length(closest_point - uv);
    float distance_from_start = - projected_distance;
    float distance_from_end =  -segment_length + projected_distance;

    float distance_from_capsule = max(distance_from_line, distance_from_start);
    distance_from_capsule = max(distance_from_capsule, distance_from_end);


    float brush_size = 0.02;

    vec3 brush_color = vec3(segment_length*0.3,0.5,0.1 + segment_length*0.5);
    vec4 brush_c = vec4(brush_color, 1.0);

    float brush = 1.0 - min(distance_from_capsule, brush_size) / brush_size;
    brush *= 2.0;
    brush = clamp(brush, 0.0, 1.0);

    textureColor = mix(textureColor, brush_c, brush);

    fragColor = textureColor;



}

