vec4 lastFragColor;
float zoom = 1.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    vec3 ray_origin = vec3(0.0, 0.0, -2.0);
    vec3 ray_dir = normalize(vec3(p.xy, 1.0));

    const int MAX_STEPS = 256;
    const float MAX_DIST = 25.0;
    float distance = 0.0;
    vec3 pos = ray_origin;

    float t = iTime * 0.5;
    mat3 rotationX = mat3(
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, cos(t), -sin(t)),
        vec3(0.0, sin(t), cos(t))
    );

    mat3 rotationY = mat3(
        vec3(cos(t), 0.0, sin(t)),
        vec3(0.0, 1.0, 0.0),
        vec3(-sin(t), 0.0, cos(t))
    );

    mat3 rotationZ = mat3(
        vec3(cos(t), -sin(t), 0.0),
        vec3(sin(t), cos(t), 0.0),
        vec3(0.0, 0.0, 1.0)
    );

    // Automatic zoom in and out
    float bpm = 60.0; // Beats per minute
    float beatTime = mod(iTime, 60.0 / bpm);
    float scale = mix(1.2, 1.34, abs(beatTime - 0.5) * 2.0);
    zoom *= scale;

    for(int i = 0; i < MAX_STEPS; i++) {
        distance = length(pos);
        if(distance > MAX_DIST) break;

        // Mandlebox formula
        vec3 z = abs(pos * zoom);
        z = (z * z * z) - 0.1;
        distance = length(z);

        pos += ray_dir * distance * 0.5;
        pos = rotationX * rotationY * rotationZ * pos;
    }

    vec3 color = vec3(0.5) + 0.5 * sin(3.0 * (pos.xzy + vec3(0.0, 2.0, 4.0) + iTime));
    fragColor = vec4(color, 1.0);

    // Add feedback effect
    fragColor = mix(lastFragColor, fragColor, 0.88);
    lastFragColor = fragColor;
}

struct Biome {
    float biomeSize;
    vec3 biomeColor;
};

struct BiomeType {
    float heightMin;
    float heightMax;
    Biome[3] biomes;
};

//biomes
const Biome forest = Biome(1.0,vec3(0,0.5,0));
const Biome desert = Biome(0.5,vec3(1.0,0.9,0.5));
const Biome grassy_plain = Biome(1.0,vec3(0.2,1.0,0.1));
