const BiomeType biomeTypes[] = BiomeType[](river,mountain,plain,swamp,taiga,ocean,beach);

//noise functions

//https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract((2.0*abs(p/3.14 - 2.0*floor(p/(2.0*3.14)) - 1.0) - 1.0)*43758.5453123);
}

//https://www.shadertoy.com/view/Msf3WH
float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) )/2.0+0.5;
}

//  2 out, 2 in...
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

float voronoi2dedges(vec2 uv){
    float d1 = 999.0;
    float d2 = 999.0;
    for (int x = -1; x < 2; x++){
        for (int y = -1; y < 2; y++){
            float d = distance(floor(uv) + vec2(x,y) + hash22(floor(uv) + vec2(x,y)),uv);
            if (d < d1) {
                d2 = d1;
                d1 = d;
            } else if (d < d2){
                d2 = d;
            }
        }
    }
    return abs(d1-d2);
}

vec2 vorronoiCellNoise(vec2 uv){
    float d1 = 999.0;
    float d2 = 999.0;
    float c1 = 0.0;
    float c2 = 0.0;
    for (int x = -1; x < 2; x++){
        for (int y = -1; y < 2; y++){
            vec2 r = (hash(floor(uv) + vec2(x,y)) + 1.0)/2.0;
            float d = distance(floor(uv) + vec2(x,y) + r,uv);
            if (d < d1) {
                d2 = d1;
                d1 = d;
                c2 = c1;
                c1 = r.x;
            } else if (d < d2){
                d2 = d;
                c2 = r.x;
            }
        }
    }
    return vec2(c1,c2);
}

