void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y*20.0;
    vec2 draggedPos = texelFetch(iChannel0, ivec2(1,0), 0).xy;
    uv -= draggedPos/iResolution.y*20.0;

    //calculate noises
    float rivernoise = abs(noise(0.5 * uv + 3167.0)*0.5 + noise(0.05*uv - 1279.0)*2.0 - 1.25);
    float mountainnoise = pow(noise(uv*0.1 + 3812.0),6.0)*4.0 + noise(uv*0.5 + rivernoise*0.5 + 7843.0)*0.5;
    float oceannoise = noise(0.05*uv - 1279.0);
    //generate heightmap
    float heightmap = mix(rivernoise-0.05 - smoothstep(0.5,1.0,oceannoise), mountainnoise - smoothstep(0.2,1.0,oceannoise), rivernoise);
    vec3 col = vec3(0);
    //get biometype
    for (int i = 0; i < biomeTypes.length(); i++){
        if (biomeTypes[i].heightMin <= heightmap && heightmap < biomeTypes[i].heightMax){
            //choose biome
            float dist = min(abs(biomeTypes[i].heightMin - heightmap),abs(biomeTypes[i].heightMax - heightmap));
            vec2 cell = vorronoiCellNoise(uv+dist+noise(uv));
            //set color based on biome
            col = biomeTypes[i].biomes[int(cell.x*3.0)].biomeColor;
        }
    }

    fragColor = vec4(col,1.0);
}

