      d = max(d, 2.4);
      // add in noise to reduce banding and create fuzz
      d=abs(d)*(1.+10.28*rand(seed*vec2(i)));
      #else
      // add in noise to reduce banding and create fuzz
      d=abs(d)*(1.4+1.1*rand(seed*vec2(i)));
