#define SC (250.0)

vec2 triwave(vec2 uv){
    return
        abs(fract(uv)-.5)
        //max(abs(fract(uv)-.5),abs(fract(uv.yx+.5)-.5))
    ;
}

#define OCTAVES 8

mat2 rotate2D(float r) {
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

float fbm(in vec2 uv,int octaves)
{
    //this function generates the terrain height
    float value = 0.,
    value1=value,
    amplitude = 2.;
    uv /= 32./4.;
    vec2 t1 = vec2(0.);
    mat2 r = rotate2D(12.);
    vec2 uv1 = uv;
    for (int i = 0; i < octaves; i++)
    {
        t1= triwave(uv-triwave(uv1*r/2.15))-t1.yx;
        value1=sqrt(value1*value1+value*value);
        value =
            ((t1.x-t1.y) * amplitude-value)
            //abs(abs(t1.x+t1.y) * amplitude-value)
        ;
        uv1 = uv;
        amplitude /= 2.15;
        uv = (uv.yx*2.15 + t1)*r;
    }

    return value1;
}

float fbm(in vec2 uv){
    return fbm(uv,OCTAVES);
}

float f(in vec3 p,int iters)
{   
    float h = fbm(p.xz,iters);
    return h;
}

float f(in vec3 p)
{   
    float h = fbm(p.xz,12);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;

    //vec2 prev = vec2(0.);
    //float t_prev = 0.;

	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
        float f1 = f(pos,OCTAVES);
		float h = pos.y - f1;
        //if(prev.y < h && prev.y < prev.x)
        //return t_prev;
        //prev = vec2(prev.y,h);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
        //t_prev = t;

	}

	return t;
}
vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
}

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(-.8, .15, -.3));

    vec3 camStep = vec3(lightDir.x, 0., lightDir.z) * (iTime+12.)/4.;
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;


    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    ro.y += fbm(ro.xz,OCTAVES)-1.8;

    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));

    if (length(iMouse.xy) > 40.0) {
        rd.yx *= rot(3.14*0.5-iMouse.y/iResolution.y*3.14);
        rd.xz *= rot(3.14-iTime/8.-iMouse.x/iResolution.x*3.14*2.0);
    }


    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

#define tau 6.283

float u2f(uint x) {
    return uintBitsToFloat(0x3F800000u | (x >> 9)) - 1.;
}

uint hash(uint x, uint s) {
    x ^= s            ; x ^= x >> 16; x *= 0x7FEB352Du;
    x += s^0x7FEB352Du; x ^= x >> 15; x *= 0x846CA68Bu;
                        x ^= x >> 16;
    return x;
}

float hashf(uint x, uint s) {
    return u2f(hash(x,s));
}

float noise(float x, uint s) {
    float fx =          fract(x)  ;
    uint  ix = uint(int(floor(x)));

    float mx = (3.-2.*fx)*fx*fx;

    float l = hashf(ix+0u, s);
    float h = hashf(ix+1u, s);

    return mix(l,h,mx);
}

vec2 cossin(float a) {
    return vec2(cos(a), sin(a));
}

mat2 rot(float a) {
    vec2 cs = cossin(a);
    return mat2(cs, cs.yx * vec2(-1,1));
}

void mainImage(out vec4 col, in vec2 coord) {
    //coord *= rot(sin(iTime*.2)*.3);
    coord *= rot(.4);
    uint id = uint(int(floor(coord.x*.5)));

    float tmp = coord.y * .002;
    tmp += (iTime + noise(iTime * .2 + hashf(id, 0u), id) * 2.) * 2.;
    tmp += noise(tmp, id) * .6;
    tmp *= 10.;
    tmp += hashf(id, 1u);

    uint rand = hash(uint(int(floor(tmp))), id);

    float randf = u2f(rand);
    col.rgb = mix(vec3(.1,.2,.6),vec3(.1,.3,.7), randf*randf);

    //col.rgb = vec3(0);

    if((rand & 0xFFu) < 0x10u) {
        float a = fract(-tmp);
        a *= a;
        col.rgb *= 1.-a;
        col.rgb += vec3(a);
    }
}


