// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

int randint(int seed, int mod) {
  return int(ihash(uint(seed))%uint(mod));
}

int swap(inout int i, inout int j) {
  int t = j; j = i; i = t;
  return t;
}

