float Gyroid(vec3 p, float scale, float bias, float thickness) {
    p *= scale;
    float d = abs(dot(sin(p), cos(p.yzx))+bias)-thickness;
	return d/scale;
}

vec4 GetGyroids(vec3 p) {    
    float t = iTime*.1;

    p.xy *= Rot(p.z*.3);
    p.z += t;

    float g1 = Gyroid(p, 5., .4, .1);
    float g2 = Gyroid(p, 17., .3, .1);
    float g3 = Gyroid(p, 39., .3, .1);
    float g4 = Gyroid(p, -7., .3, .1);

    return vec4(g1, g2, g3, g4);              
}

float GetDist(vec3 p) {
	float d = p.y;
    p.x+=.33;

    float t = iTime*.1;
    float scale = 20.;

    p.xy *= Rot(p.z*.3);
    p.z += t;

    float g1 = Gyroid(p, 5., 1.4, .1);
    float g2 = Gyroid(p, 17., .3, .1);
    float g3 = Gyroid(p, 39., .3, .1);
    float g4 = Gyroid(p, 89., .3, .1);
    float g5 = Gyroid(p, 189., .3, .1);
    float g6 = Gyroid(p, 289., .0, .1);

    d = g1*.7;
    d -= g2*.3;
    d += g3*.2;
    d += g4*.1;
    d += g5*g4*20.;
    d += g6*.1;

   	vec3 P = p;
    P.xz = fract(P.xz)-.5;
    vec2 id = floor(p.xz);
    float n = Hash21(id);
    //P = fract(P)-.5;


    return d;              
}

float sdSpark(vec3 p) {
    float t = iTime*.5;

    p.xz *= Rot(t*.1);

    vec3 id = floor(p);
    p.xz = fract(p.xz)-.5;

    float n = Hash21(id.xz);

	float z = fract(t+n)-.5;
   	z *= 10.;

    n *= 6.2832;
    float size = .3+.2*sin(t*.1);
    vec3 p1 = vec3(0, z-size*1., 0);
    vec3 p2 = vec3(0, z+size*1., 0);

    p.x += sin(p.y*3.)*.1;

    float d = sdCapsule(p, p1, p2, size*.1);

    d = min(d, length(p.xz)+size*2.);

    return d;
}

vec2 SparkMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float dM=MAX_DIST;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = sdSpark(p);
        if(dS<dM) dM = dS;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return vec2(dO, dM);
}

vec3 GetSparkNormal(vec3 p) {
	float d = sdSpark(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        sdSpark(p-e.xyy),
        sdSpark(p-e.yxy),
        sdSpark(p-e.yyx));

    return normalize(n);
}


vec2 RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float dM=MAX_DIST;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if(dS<dM) dM = dS;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return vec2(dO, dM);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z, vec3 up) {
    vec3 f = normalize(l-p),
        r = normalize(cross(up, f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 GetPos(float t) {
    float y = .6+cos(t)*.2;
	return mix(vec3(-.3, y, t), vec3(2.2, y, t), (sin(t)*.5+.5)*1.);
}

vec3 Bg(vec3 rd) {
    float b = -rd.y*.5+.5;
	vec3 col = vec3(.9, .6, .5)*b*4.;

    float a = atan(rd.x, rd.z);

    //col += sin(a*10.+iTime)*(1.-rd.y);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

