	for (float i=0.0; i<WAVES + 1.0; i++) {
		float freq = texture(iChannel0, vec2(i / WAVES, 0.0)).x * 3.0;

		vec2 p = vec2(uv);

		p.x += i * 0.0008 + freq * 0.03;
		p.y += sin(p.x * 10.0 + time) * cos(p.x * 2.0) * freq * 0.2 * ((i + 1.0) / WAVES);
		float intensity = abs(0.01 / p.y) * clamp(freq, 0.35, 2.0);
		color += vec3(1.0 * intensity * (i / 90.0), 0.5 * intensity, 2.5 * intensity) * (9.0 / WAVES);
	}

	fragColor = vec4(color, 1.0);
}

