}

float sdSpark(vec3 p) {
    float t = iTime*.5;

    p.xz *= Rot(t*.1);

    vec3 id = floor(p);
    p.xz = fract(p.xz)-.5;

    float n = Hash21(id.xz);

	float z = fract(t+n)-.5;
   	z *= 10.;

    n *= 6.2832;
    float size = .3+.2*sin(t*.1);
    vec3 p1 = vec3(0, z-size*1., 0);
    vec3 p2 = vec3(0, z+size*1., 0);

    p.x += sin(p.y*3.)*.1;

    float d = sdCapsule(p, p1, p2, size*.1);

    d = min(d, length(p.xz)+size*2.);

    return d;
}

vec2 SparkMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float dM=MAX_DIST;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = sdSpark(p);
        if(dS<dM) dM = dS;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return vec2(dO, dM);
}

vec3 GetSparkNormal(vec3 p) {
	float d = sdSpark(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        sdSpark(p-e.xyy),
        sdSpark(p-e.yxy),
        sdSpark(p-e.yyx));

    return normalize(n);
}


vec2 RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float dM=MAX_DIST;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if(dS<dM) dM = dS;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return vec2(dO, dM);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z, vec3 up) {
    vec3 f = normalize(l-p),
        r = normalize(cross(up, f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

