float modSecs(float modulo)
{
    return mod(floor(iTime), modulo);
}

// an array of 13 int32 numbers can hold the 400bit number for us?
int[13] number;

bool get_bit(int idx)
{   
    // register of which idx of the array we are in
    int reg = idx >> 5;
    // remainder of which index in the specific int32 we are in
    int rem = idx & 0x1F;

    return ((number[reg] >> int(rem)) & 1) == 0;
}

bool get_bit_from_float(float inp, int idx)
{   
    return ((int(inp) >> idx) & 1) == 1;
}

void set_bit(bool val, int idx)
{
    // register of which idx of the array we are in
    int reg = idx >> 5;
    // remainder of which index in the specific int32 we are in
    int rem = idx & 0x1F;

    number[reg] |= (int(val) << rem);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Define the resolution of the screen
    vec2 uv = fragCoord/iResolution.xy;

    vec2 fields = floor(uv * 20.0);

    float field_idx = fields.x + (fields.y * 20.0);

    // fill number with something "random"
    for (int i = 0; i < 13; i++)
    {
        // number[i] = 0xFA0AFA0A; // some funny pattern
        number[i] = int(fract(sin(floor(iTime)))*165191048.7*float(i+1)); // some "random" number constructor for now
    }

    // set_bit(true, (int(iTime)*2)); // not persistent?

    // Determine the color of the pixel based on its position
    // color.r (red) is one field at a time, walks around the whole screen in 400 seconds
    // color.g (green) does nothing right now (working on LSFR to run through all 400 bit of possible numbers randomly)
    // color.b (blue) is a binary counter of iDate.w (the seconds), counts up to 3600, but runs over at 32 bit.
    vec3 color = vec3(mod(field_idx - modSecs(400.0),400.0), get_bit(int(field_idx)), get_bit_from_float(iDate.w, int(field_idx)));

    // just look at red channel
    // color = vec3(color.r);

    // just look at green channel
    color = vec3(color.g);

    // just look at blue channel
    // color = vec3(color.b);

    // Set the color of the pixel
    fragColor = vec4(color, 1.0);
}

float bpm;
  vec3 erot(vec3 p,vec3 ax,float t){return mix(dot(ax,p)*ax,p,cos(t))+cross(ax,p)*sin(t);}
float txt(vec2 p,float t){
     ivec2 i = ivec2(abs(p)*128.);
      return dot(sin(float(i)),cos(float(i.yx)*t))+float(i.x&i.y)/128.;;
  }
  float rand(vec2 p){
       return fract(535.55*sin(dot(p,vec2(85.5,479.5))));
    }
  vec3 path(vec3 p){

     vec3 o = vec3(0.);
       o.x +=sin(p.z*.1)*2.;
       o.x +=sin(p.z*.55)*.44;
       o.y +=sin(p.z*.33)*.44;
       o.y +=cos(p.z*.24)*.5;
      return o;
    }
    float terr(vec3 p){
        float d=0.;
         for(float i=.5;i<5.;i+=i){

              d+= dot(asin(sin(erot(p*i,vec3(0.,1.,0),i))),vec3(.5))/i/4.;
           }

           return d;

      }
vec2 sdf(vec3 p){
     vec2 h;
     vec3 hp=p;

     vec3 ph= path(hp);

     float ff = 1.-tanh(abs(hp.x-ph.x)-1.);
     h.x  = dot(hp,vec3(0.,1.,0.))+1.+ff+terr(hp)+texture(iChannel0,fract(vec2(.3+ff*.01))).r;;
     h.y= 1.;

     vec2 t;
     vec3 tp=p;

      tp-=ph;  
      tp.y +=1.;
   float gy = dot(sin(tp*1.5),cos(tp.zxy));
    tp+=gy*.1;
     tp= erot(tp,vec3(0,0,1),tp.z);
  tp.xy= abs(tp.xy)-.2;tp.xy=abs(tp.xy)-.1;
     t.x =  max(abs(tp.z-bpm)-5.,length(tp.xy)-.02);
     t.y = 2.;
     h=t.x < h.x ? t:h;
     return h;

  }
  #define q(s) s*sdf(p+s).x

  vec3 norm(vec3 p,float ee){vec2 e=vec2(-ee,ee);return normalize(q(e.xyy)+q(e.yxy)+q(e.yyx)+q(e.xxx));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
  bpm = +iTime*2.;
   bpm+=+rand(uv)*.1;
  bpm = floor(bpm) + pow(fract(bpm),2.1);
vec3 col = vec3(0.);
// Imagine revision logo
    //col.r= texture(iChannel1,clamp(uv+.5+texture(iChannel0,vec2(1.)*fract(floor(bpm)*.1+txt(uv,1.))).r,0.,1.)).r;
   //  col.gb= texture(iChannel1,clamp(uv+.5+texture(iChannel0,vec2(1.)*fract(floor(bpm)*.1+txt(uv,2.)))).r,0.,1.)).gb;

  vec3 ro=vec3(1.,1.,bpm+cos(bpm)),rt=vec3(0.,0.,bpm+tanh(sin(bpm*.25))*15.);
  ro+=path(ro);
  rt+=path(rt);
  vec3 z=normalize(rt-ro),x=normalize(cross(z,vec3(0.,-1.,0))),y=cross(z,x);  
  vec3 rd = mat3(x,y,z)*normalize(vec3(uv,1.+sin(bpm)*.1));

  vec3 rp= ro;
   vec3 acc= vec3(0.);
  // AZERTY FOR THE WIN
   float dd =0.;
  vec3 light = normalize(vec3(1.,2.,-3.));
  for(float i=0.;i++<128. && dd < 50.;){

      vec2 d = sdf(rp);

       if(d.y ==2.){
          acc+=vec3(.0,.3,.7)*exp(-abs(d.x))/(50.-min(45.,texture(iChannel0,vec2(fract(.3+bpm+rp.z*.1))).r*100.));
          d.x = max(.001,abs(d.x));
         }
      rp+=rd*d.x;
      dd+=d.x;
       if(d.x < .001){
          vec3 n = norm(rp,.005);
         vec3 n2 = norm(rp,.007);
           float dif = max(0.,dot(light,n));
            float fr= pow(1.+dot(rd,n),4.);
             col = +dif * vec3(.1);
             if(d.y==1.){
                  col  += smoothstep(.001,.1,length(n-n2))+fr*vec3(1.,.7,.3)*max(0.,1.-abs(rp.z-bpm-5.)*.2);
                  rd= reflect(rd,n);
                  rp+=rd*.1;
                  continue;

               }
             break;

         }
    }
    col = mix(col,vec3(.1),(.5)-exp(-dd));


    // Output to screen
    fragColor = vec4(sqrt(col+acc),1.);
    }

#define SCALE 6.0
#define R iResolution.xy
#define M iMouse
#define PI 3.14159265358979

// gradient map ( equation, time, hardness, shadow, reciprocals )
float gm(float eq, float t, float h, float s, bool i)
{
    float sg = min(abs(eq), 1.0/abs(eq)); // smooth gradient
    float og = abs(sin(eq*PI-t)); // oscillating gradient
    if (i) og = min(og, abs(sin(PI/eq+t))); // reciprocals
    return pow(1.0-og, h)*pow(sg, s);
}

void mainImage(out vec4 RGBA, in vec2 XY)
{
    float t = iTime/2.0;
    float z = (M.z > 0.0) ? SCALE/2.0/(M.y/R.y): SCALE; // zoom
    float h = 5.0; // hardness
    float s = 0.25; // shadow
    bool rc = false; // reciprocals
    vec3 bg = vec3(0); // black background

    float aa = 2.0; // anti-aliasing
    for (float j = 0.0; j < aa; j++)
    for (float k = 0.0; k < aa; k++)
    {
        vec3 c = vec3(0);
        vec2 o = vec2(j, k)/aa;
        vec2 sc = (XY-0.5*R+o)/R.y*z; // screen coords
        float x2 = sc.x*sc.x;
        float y2 = sc.y*sc.y;

        // square root grids
        c += gm(x2, t, h, s, rc); // x
        c += gm(y2, 0.0, h, s, rc); // y
        c += gm(x2+y2, t, h, s, rc); // addition
        c += gm(x2-y2, t, h, s, rc); // subtraction
        c += gm(x2*y2, t, h, s, rc); // multiplication
        c += gm(x2/y2, t, h, s, rc); // division

        bg += c;
    }
    bg /= aa*aa;

    bg *= sqrt(bg)*0.5; // brightness & contrast
    RGBA = vec4(bg, 1.0);
}

// variant of https://shadertoy.com/view/mtKGWD
// cf https://www.desmos.com/calculator/zd265cjxqi
#define rot2(a)  mat2(cos(a+vec4(0,11,33,0)))
#define arc(L)   t = min(t, max( length( vec2( length((L).xy)-r,(L).z) ) -.1, abs(atan((L).x,(L).y)-A)-A ))

void mainImage(out vec4 O, vec2 U)
{
    float t=9.,d=1., h,r,A,i, T, y, s = 1.5;
    vec3  R = iResolution, 
          D = normalize(vec3(U+U, -12.*R.y) - R),        // ray direction
          p = vec3(0,0,30), q,                           // marching point along ray 
          M = iMouse.z > 0. ? 6.*iMouse.xyz/R -3.        // camera control
              :  vec3( -.8,-.5,0);

    for ( O=vec4(1); O.x > 0. && t > .01; O-=.01 ) {     // march scene
        q = p, 
        q.yz *= rot2(-M.y),                              // rotations
        q.xz *= rot2(-M.x-1.57),
        T = mod(.3*iTime,1.33), q.y +=  6.; q.x -= T*1.5;   // infinite scroll
        if (q.x>1.) T = 2.65-T;
        T -= floor(abs(q.x/2.)+.5)*1.3, q.x = abs(mod(q.x-1.,2.)-1.); // repeat along x
        for( i=0.; i<8.; i++ , q.y -= s )                // foreach thread
            h = 3.*clamp(T-i+3.7,1e-3,1.), r = (d*d+h*h)/h/4., A = atan(h/d),
            y = .5*sin(s*q.y),
            arc(     vec3(1, r  , y ) - q ), 
            arc( q - vec3(0, h-r, y )     ),
            t = min(t, length(q-vec3(1,.75,0))+.1 );     // trick to limit the artifact
         // t = min(t,max( length(q.yz)-.1, 1.-abs(q.x)) );
        p += .5*t*D;                                     // step forward = dist to obj    
    }
   O *= O*O*O*2.;                                        // color scheme 
}

// Extruded Hexagon Traversal - gelami
// https://www.shadertoy.com/view/Dly3zm

/*
 * Fast ray-traversal of an extruded hexagonal grid
 * Features hexagonal depth of field with FXAA 3.11
 * 
 * Mouse drag to look around
 * Defines in Common
 * 
 * Hexagonal grid traversals are nothing new and have been done alot of times already
 * but I figured I'd give it a try myself, which turned out to be pretty neat
 * 
 * The traversal works by precomputing the side distances in the slab intersection
 *   which allows us to only use additions to advance the ray, like you would in DDA
 * That simplifies the traversal to only use additions for the side distances,
 *   and a couple of checks for picking the axis
 * 
 * The rest is the usual extruded traversal method,
 * where you intersect the far-hit only, checking if the y position is less than height
 * before and after advancing the ray, if before its a top hit, else a side hit
 * 
 * The DoF pass actually takes longer to do than rendering the actual scene itself lol
 * 
 * Would be cool to add rounded edges to the hexagons, but perhaps for next time
 * Also a 3D voxel traversal equivalent to this, something like a truncated octahedron
 * or a a rhombic dodecahedral honeycomb would be quite interesting to explore as well
 * 
 * Other hexagonal grid traversal shaders:
 * 
 * Hexagonal Grid Traversal - 3D - iq
 * https://www.shadertoy.com/view/WtSfWK
 *
 * Hex Voxel Scene - Shane
 * https://www.shadertoy.com/view/4dsBz4
 * 
 * Hex grid traversal - mattz
 * https://www.shadertoy.com/view/XdSyzK
 *
 * 
 * FXAA 3.11 implementation and DoF from:
 * Two Grid Traversal w/ 1Pass DoF - gelami
 * https://www.shadertoy.com/view/dtt3DS
 * 
 */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;

    #ifdef CHROMATIC_ABBERATION
    col = vec3(0);
    vec3 w = vec3(0);

    float caStr = smoothstep(0.0, 0.5, dot(uv - 0.5, uv - 0.5));

    vec2 dir = normalize(uv - 0.5) / iResolution.xy;

    const float count = CHROMATIC_ABBERATION_STEPS;
    for (float i = 0.0; i < count; i++)
    {
        vec3 c = palette((i + 0.5) / count);
        w += c;
        col += texture(iChannel0, uv + caStr * dir * i).rgb * c;
    }
    col /= w;
    #endif

    #ifdef VIGNETTE
    col *= smoothstep(1.0, 0.0, dot(uv - 0.5, uv - 0.5)) * 0.8 + 0.2;
    #endif

    col = col / (1.0 + col);

    //col = mix(col, smoothstep(vec3(0), vec3(1), col), 0.2);
    //col = ACESFilm(col);
    fragColor = vec4(linearTosRGB(col), 1);
    fragColor += (dot(hash23(vec3(fragCoord, iTime)), vec2(1)) - 0.5) / 255.;
}

