// Scene

// Fork of "Gelami Raymarching Template" by gelami. https://shadertoy.com/view/mslGRs
// 2023-05-10 08:41:22

float map(vec2 p)
{
    vec2 r = texture(iChannel0, 0.35 * (p - 1.5) / iChannelResolution[0].xy).xy;
    float h = r.x;

    //h = (h-0.5)*1.7+0.5;
    //h = saturate(h)*0.8+0.1;
    h = smoothstep(0.22, 0.78, h)*0.8+0.1;

    #if MOTION == 0
    h = saturate(sin((h + iTime * 0.4 + p.x * 0.06 + p.y * 0.08) * TAU)*0.5+0.5)*(h*0.7+0.3);
    #elif MOTION == 1
    h += sin((h + iTime * 0.55 + p.x * 0.06 + p.y * 0.08) * TAU)*0.13;
    #endif

    return saturate(h) * 0.9999 * MAX_HEIGHT;
}

struct HitInfo
{
    float t;
    vec3 n;
    vec2 id;
};

// Traversal method
bool trace(vec3 ro, vec3 rd, out HitInfo hit)
{
    if (rd.y > 0.0 && ro.y > MAX_DIST)
        return false;

    // Normals
    const vec2 n0 = vec2(1, 0);
    const vec2 n1 = vec2(0.5, sqrt(3.0)/2.0);
    const vec2 n2 = vec2(0.5, -sqrt(3.0)/2.0);

    const vec2 ts = vec2(1, sqrt(3.0));

    // ID
    vec2 p0 = floor(ro.xz / ts);
    vec2 p1 = floor(ro.xz / ts - 0.5) + 0.5;

    vec2 c0 = (ro.xz - p0 * ts) - ts*0.5;
    vec2 c1 = (ro.xz - p1 * ts) - ts*0.5;

    vec2 id = dot(c0, c0) < dot(c1, c1) ? p0 : p1;

    vec2 pc = id * ts + ts*0.5;
    vec2 pp = ro.xz - pc;

    // Pre-compute variables
    vec3 prd = 1.0 / vec3(dot(rd.xz, n0), dot(rd.xz, n1), dot(rd.xz, n2));
    vec3 pro = vec3(dot(pp, n0), dot(pp, n1), dot(pp, n2)) * prd;
    vec3 ard = abs(prd);
    vec3 srd = sign(prd);

    vec2 o0 = n0 * srd.x;
    vec2 o1 = n1 * srd.y;
    vec2 o2 = n2 * srd.z;

    vec2 i0 = vec2(1, 0) * srd.x;
    vec2 i1 = vec2(0.5, 0.5) * srd.y;
    vec2 i2 = vec2(0.5, -0.5) * srd.z;

    vec3 pp0 = vec3(dot(i0 * ts, n0), dot(i0 * ts, n1), dot(i0 * ts, n2)) * prd;
    vec3 pp1 = vec3(dot(i1 * ts, n0), dot(i1 * ts, n1), dot(i1 * ts, n2)) * prd;
    vec3 pp2 = vec3(dot(i2 * ts, n0), dot(i2 * ts, n1), dot(i2 * ts, n2)) * prd;

    vec2 nrd = vec2(0);
    float lt = 0.0;
    vec3 pos = ro;

    vec3 k = ard * 0.5;

    vec2 ido = vec2(0);
    vec3 ppo = vec3(0);

    for (int i = 0; i < STEPS; i++)
    {
        float h = map(id + ido);

        // Side hit
        if (pos.y < h)
        {
            hit.t = lt;
            hit.n = -vec3(nrd.x, 0, nrd.y);
            hit.id = id + ido;
            return true;
        }

        // Compute side distances
        vec3 n = pro - ppo;
        vec3 t2 = -n + k;

        // Pick smallest axis
        float tF = t2.x;
        vec2 no = i0;
        vec3 ppp = pp0;
        nrd = o0;

        if (t2.y < tF)
        {
            tF = t2.y;
            no = i1;
            nrd = o1;
            ppp = pp1;
        }
        if (t2.z < tF)
        {
            tF = t2.z;
            no = i2;
            nrd = o2;
            ppp = pp2;
        }

        pos = ro + rd * tF;

        // Early exit if above max height
        if (rd.y > 0.0 && pos.y > MAX_HEIGHT)
            return false;

        // Top hit
        if (pos.y < h)
        {
            hit.t = -(ro.y - h) / rd.y;
            hit.n = vec3(0, 1, 0);
            hit.id = id + ido;
            return true;
        }

        // Advance ray
        lt = tF;
        id += no;
        ppo += ppp;
    }

    return false;
}

vec3 getSky()
{
    return palette2(0.35) * 0.6 + 0.1;
}

vec3 shade(vec3 pos, HitInfo hit, const bool shadow)
{
    vec3 col = vec3(0);
    vec3 alb = palette2(hash12(hit.id));

    col += alb * dot(abs(vec3(hit.n)), vec3(0.8, 1, 0.9));

    vec3 ldir = normalize(vec3(1, 3, 1));
    vec3 ligCol = vec3(1, 0.6, 0.5)*1.2;

    float diff = max(dot(hit.n, ldir), 0.0);

    if (shadow)
    {
        HitInfo hitL;
        bool isHitL = trace(pos + hit.n * EPS, ldir, hitL);

        diff *= float(!isHitL);
    }

    col *= smoothstep(0.0, MAX_HEIGHT*0.8, pos.y)*0.7+0.3;

    col *= (diff * 0.8 + 0.2) * ligCol;

    //col += alb * getSky() * 0.15;

    return col;
}

float fresnel(float r0, vec3 rd, vec3 n)
{
    return r0 + (1.0 - r0) * pow(1.0 - dot(-rd, n), 5.0);
}

vec3 fresnel(vec3 r0, vec3 rd, vec3 n)
{
    return r0 + (1.0 - r0) * pow(1.0 - dot(-rd, n), 5.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef SSAA
    vec4 tot = vec4(0);

    for (int x = 0; x < SSAA; x++)
    {
        for (int y = 0; y < SSAA; y++)
        {
    vec2 o = (vec2(x, y) + 0.5) / float(SSAA);
    vec2 pv = (2. * (fragCoord + o - 0.5) - iResolution.xy) / iResolution.y;
    #else
    vec2 pv = (2. * (fragCoord) - iResolution.xy) / iResolution.y;
    #endif

    vec2 uv = fragCoord / iResolution.xy;

    vec3 ro = vec3(0, 0, CAMERA_DISTANCE);
    vec3 lo = vec3(0, MAX_HEIGHT, 0);

    lo.xz += vec2(1, 0.4) * iTime * 0.8;

    vec2 m = iMouse.xy / iResolution.xy;

    float ax = iMouse.z < 0. || iMouse.x == 0. ? -TAU * 0.3 + iTime * .21 : -m.x * TAU + PI;
    float ay = iMouse.z < 0. || iMouse.y == 0. ? PI * (0.18+(sin(iTime*0.13)*0.5+0.5)*0.2) : -m.y * PI * 0.5 + PI * 0.5;

    ro.yz *= rot2D(ay);
    ro.xz *= rot2D(ax);
    ro += lo;

    mat3 cmat = getCameraMatrix(ro, lo);

    vec3 rd = normalize(cmat * vec3(pv, 2));

    vec3 pos = ro;
    float pt = 0.0;

    if (pos.y > MAX_HEIGHT)
    {
        pt = -(ro.y - MAX_HEIGHT) / rd.y;
        pos = pos + rd * pt;
    }

    HitInfo hit;
    bool isHit = trace(pos, rd, hit);
    //vec2 id = round((hit.id - 0.5) / vec2(1, sqrt(3.0)));

    float t = pt + hit.t;

    pos = pos + rd * hit.t;

    vec3 col = shade(pos, hit, true);

    vec3 alb = palette2(hash12(hit.id));

    vec3 ref = reflect(rd, hit.n);

    HitInfo hitR;
    bool isHitR = trace(pos + hit.n * EPS, ref, hitR);

    vec3 refPos = pos + ref * hitR.t;

    vec3 refCol = shade(refPos, hitR, true);

    if (!isHitR)
    {
        refCol = getSky();
    }

    vec3 specCol = mix(alb, vec3(1), 0.8);
    float r0 = hash12(hit.id.yx) < 0.1 ? 1.0 : 0.1;

    float fre = fresnel(r0, rd, hit.n);

    col = mix(col, refCol * specCol, fre);

    if (!isHit)
    {
        col = getSky();
    }

    #ifdef FOG
    float fog = 1.0 - exp(-max(t - 20.0, 0.0) * 0.001);
    col = mix(col, getSky(), fog);
    #endif


    #ifdef DOF_FLAT_Z_DEPTH
    t *= dot(rd, cmat[2]);
    #endif

    #ifndef SSAA
    col = max(col, vec3(0));

    col = col / (1.0 + col);
    col = linearTosRGB(col);

    fragColor = vec4(saturate(col), t);

    #else
        tot += vec4(col, t);
        }
    }
    tot /= float(SSAA*SSAA);

    tot.rgb = tot.rgb / (1.0 + tot.rgb);
    tot.rgb = linearTosRGB(tot.rgb);
    tot.rgb = saturate(tot.rgb);

    fragColor = tot;
    #endif
}

