// Depth of Field

// Hexagonal bokeh shape from:
// Morphing bokeh shape sample taps - BeRo
// https://www.shadertoy.com/view/MtlGRn
// http://www.crytek.com/download/Sousa_Graphics_Gems_CryENGINE3.pdf
vec2 getBokehCoord(vec2 o, const float f, const float n, const float offset){
    vec2 ab = o;    
    vec2 p;
    if (ab.x * ab.x > ab.y * ab.y)
        p = vec2(abs(ab.x) > 1e-8 ? PI * 0.25 * ab.y / ab.x : 0.0, ab.x);
    else
        p = vec2(abs(ab.y) > 1e-8 ? (PI * 0.5 - (PI * 0.25 * ab.x / ab.y)) : 0.0, ab.y); 
    p.x += f * offset;

    if (f > 0.0)
        p.y *= pow((cos(PI / n) / cos(p.x - ((TAU / n) * floor((n * p.x + PI) / TAU)))), f);

    return vec2(cos(p.x), sin(p.x)) * p.y;
}

float getCoC(float t, float focalDist, float scale)
{
    return saturate(abs(1.0 / focalDist - 1.0 / t) * scale) * DOF_RADIUS;
}

// Depth of Field method modified from:
// Two Grid Traversal w/ 1Pass DoF - gelami
// https://www.shadertoy.com/view/dtt3DS
vec3 depthOfField(sampler2D tex, vec2 fragColor, vec2 res)
{
    vec2 px = 1.0 / res;
    vec2 uv = fragColor * px;

    vec4 center = texture(tex, uv);

    float coc = getCoC(center.a, DOF_FOCUS_DIST, DOF_FOCUS_SCALE);

    if (coc < 0.5)
        return center.rgb;

    const float DOF_SIDES = 6.0;
    const float f = 1.0;

    vec3 color = center.rgb;
    float w = 1.0;

    float radStep = DOF_RADIUS / float(DOF_STEPS);
    float rad = radStep * 0.5;
    float ang = 0.0;

    for (int i = 0; i < DOF_STEPS; i++)
    {
        //vec2 p = vec2(cos(ang), sin(ang));
        vec2 p = getBokehCoord(vec2(cos(ang), sin(ang)), f, DOF_SIDES, PI * 0.5);

        vec4 samp = texture(tex, uv + p * rad * px);

        float sampCoC = getCoC(samp.a, DOF_FOCUS_DIST, DOF_FOCUS_SCALE);

        if (samp.a > center.a)
            sampCoC = clamp(sampCoC, 0.0, coc * 2.0);

        float r = rad * length(p);
        float m = smoothstep(r - 0.5, r + 0.5, sampCoC);

        color += mix(color / w, samp.rgb, m);
        ang += GOLDEN_ANGLE;
        rad += radStep / rad;
        //rad = sqrt(DOF_RADIUS * float(i) / float(DOF_STEPS));
        w += 1.0;
    }

    return color / w;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef DOF
    vec3 col = depthOfField(iChannel0, fragCoord, iResolution.xy);
    #else
    vec3 col = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;
    #endif

    fragColor = vec4(col, 1);
}

