#define TIME            iTime
#define RESOLUTION      iResolution
#define PI              3.141592654
#define TAU             (2.0*PI)

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const float hoff = 0.58;
const vec3 dbcol = HSV2RGB(vec3(hoff+0.96, 0.8, 0.75));
const vec3 sbcol = HSV2RGB(vec3(hoff+0.95, 0.4, 1.0));
const vec3 gbcol = HSV2RGB(vec3(hoff+0.98, 0.9, 0.001));
const vec3 fbcol = HSV2RGB(vec3(hoff+0.95, 0.7, 2.0));

// License: Unknown, author: Claude Brezinski, found: https://mathr.co.uk/blog/2017-09-06_approximating_hyperbolic_tangent.html
float tanh_approx(float x) {
  //  Found this somewhere on the interwebs
  //  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 0.51f;
  float b = 2.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 1.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);

}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
float pabs(float a, float k) {
  return -pmin(a, -a, k);
}

float dot2(vec2 p) {
  return dot(p, p);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float heart(vec2 p) {
  p.x = pabs(p.x, 0.001);

  if( p.y+p.x>1.0 )
      return sqrt(dot2(p-vec2(0.05,0.36))) - sqrt(2.0)/5.0;
  return sqrt(min(dot2(p-vec2(0.03,0.04)),
                  dot2(p-0.2*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

float df(vec2 p) {
  vec2 hp = p;
  const float hz = 1.40;
  hp /= hz;
  hp.y -= -1.2;
  float d = heart(hp)*hz;
  return d;
}

float hf(vec2 p) {
  float d = df(p);
  float h = (-20.0*d);
  h = tanh_approx(h);
  h -= 3.0*length(p);
  h = pmin(h, 0.0, 1.);
  h *= 1.25;
  return h;
}

vec3 nf(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = hf(p + e.xy) - hf(p - e.xy);
  n.y = hf(p + e.yx) - hf(p - e.yx);
  n.z = 2.0*e.x;

  return normalize(n);
}

vec2 hash(vec2 p) {
  p = vec2(dot (p, vec2 (127.1, 311.7)), dot (p, vec2 (269.5, 183.3)));
  return -1. + 2.*fract (sin (p)*43758.5453123);
}

float noise(vec2 p) {
  const float K1 = .366025404;
  const float K2 = .211324865;

  vec2 i = floor (p + (p.x + p.y)*K1);

  vec2 a = p - i + (i.x + i.y)*K2;
  vec2 o = step (a.yx, a.xy);    
  vec2 b = a - o + K2;
  vec2 c = a - 1. + 2.*K2;

  vec3 h = max (.5 - vec3 (dot (a, a), dot (b, b), dot (c, c) ), .0);

  vec3 n = h*h*h*h*vec3 (dot (a, hash (i + .0)),dot (b, hash (i + o)), dot (c, hash (i + 1.)));

  return dot (n, vec3 (70.));
}

float fbm(vec2 pos, float tm) {
  vec2 offset = vec2(cos(tm), sin(tm*sqrt(0.5)));
  float aggr = 0.0;

  aggr += noise(pos);
  aggr += noise(pos + offset) * 0.5;
  aggr += noise(pos + offset.yx) * 0.25;
  aggr += noise(pos - offset) * 0.125;
  aggr += noise(pos - offset.yx) * 0.0625;

  aggr /= 1.0 + 0.5 + 0.25 + 0.125 + 0.0625;

  float f = (aggr * 0.5) + 0.5;

  return f;
}

float divf(float offset, float f) {
  const float goff = 0.2;
  const float gfloor = 0.001;
  float r = abs(goff + offset - f);
  r = max(r, gfloor);
  return r;
}

// This way of computing "lightning" I found at shadertoy. Unfortunately I don't remember where.
vec3 lightning(vec2 pos, vec2 pp, float offset) {
  vec3 sub = 0.006*vec3(0.4, 0.01, 0.6).zyx*length(pp);

  float time = TIME+13.4;
  float stime = time/100.0;
  vec3 col = vec3(0.0);
  vec2 f = 10.0*cos(vec2(sqrt(0.5), 1.0)*stime)+vec2(0.0, -7.0)*stime;
  const float glow = 0.0025;
  const float goff = 0.9;
  const float gfloor = 0.005;
  for (float i = 0.0; i < 5.0; ++i) {
    vec3 gcol0 = (1.0+cos(1.59*vec3(1.1, 0.0, 2.0) -time+3.0*pos.x+8.33*i));
    vec3 gcol1 = (1.0+cos(1.75*vec3(0.0, 1.6, 0.50) -2.*time+pos.y+12.25*i));
    float btime = stime*145.0 + (i);
    float rtime = stime*104.0 + (i);
    float div1 = divf(offset, fbm((pos + f) * 1.4, rtime));
    float div2 = divf(offset, fbm((pos + f) * 2.0, btime));
    float d1 = offset * glow / div1;
    float d2 = offset * glow / div2;
    col += (d1 * gcol0)-sub;
    col += (d2 * gcol1)-sub;
  }

  return col;
}

vec3 effect(vec2 p, vec2 pp) {
  float aa = 4.0/RESOLUTION.y;
  float d = df(p);
  float h = hf(p);
  vec3 n = nf(p);
  const vec3 lp = vec3(-9.0, -5.0, 5.0);
  const vec3 ro = vec3(0.4, -5.0, 10.0);
  vec3 p3 = vec3(p, h); 
  vec3 rd = normalize(p3-ro);
  vec3 ld = normalize(lp-p3);
  vec3 r = reflect(rd, n);
  float diff = max(dot(ld, n), 0.0);
  vec3 dcol = dbcol*mix(vec3(0.009), vec3(1.2), diff);
  float spe = pow(max(dot(ld, r), -0.6), 5.0);
  vec3 scol = spe*sbcol;
  float gd = d-0.25;
  vec2 gp = p;
  vec3 gcol = lightning(gp, pp, gd);
  vec3 hcol = dcol;
  hcol += scol;
  vec3 col = vec3(-0.05);
  col += gbcol/max(0.01*(dot2(p)-0.15), 0.0001);
  col += gcol;
  col = mix(col, hcol, smoothstep(0.0, -aa, d));
  col = mix(col, fbcol, smoothstep(0.0, -aa, abs(d+0.01)-0.01));
  col *= smoothstep(1.75, 0.25, length(pp));

  col = aces_approx(col); 
  col = sqrt(col); 
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1.3 + 2.5 * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, pp); 
  fragColor = vec4(col.xyz, 1.0);
}

///////////////////////////////////////////////////////////
//
// Barth Sextic Surfaces.
//
// Barth discovered a 1-parameter family of sextic surfaces,
// with 'the' Barth sextic corresponding to parameter k = 0.5
// containing 65 nodes, the maximum possible for a surface of order 6.
//
// Here, the surface in  is stereographically projected to  via
// the double cover of  by S. ie. point p = (x,y,z,w) on the
// hypersphere corresponds to point in  with homogeneous coordinates
// (x,y,z,w) (which is also represented by -p on the hypersphere
// so the surface is duplicated by the projection).
//
// <mouse>: rotate view of surface
// <up>/<down>: zoom in/out
// 'a': enable AA
// 'c','t': align various surface features with rotation
// 'k': animate surface parameter k
// 'm': mirror effect (best with AA enabled)
// 'q': toggle S quaternion rotation
// 's': use projection (x,y,z,w) -> (x/w,y/w,z/w)
//
///////////////////////////////////////////////////////////

bool doMirror = false;
bool doGamma = true;
bool doSpecular = true;
bool doDiffuse = true;

const float ambient = 0.5;
const float diffuse = 0.5;
float specpower = 4.0;

const vec3 defaultColor = vec3(0.7,1.0,1.0);

const float PI =  3.141592654;
const float TWOPI = 2.0*PI;
const float phi = 1.618033;
const float phi2 = phi*phi;
const float phi4 = phi2*phi2;

vec3 light;

vec4 qmul(vec4 p, vec4 q) {
  vec3 P = p.xyz, Q = q.xyz;
  return vec4(p.w*Q+q.w*P+cross(P,Q),p.w*q.w-dot(P,Q));
}

vec2 rotate(vec2 p, float t) {
  return cos(t)*p + sin(t)*vec2(-p.y,p.x);
}

vec4 transform4(vec3 p3) {
  vec4 p = vec4(p3,0);
  if (key(CHAR_S)) {
    p.w = 1.0;
  } else {
    // Invert at south pole of S3 - equivalent to a stereographic projection.
    p.w += 1.0;
    p *= 2.0/dot(p,p);
    p.w -= 1.0;
  }
  if (key(CHAR_Q)) return p;
  float k = 0.0;
  k = 0.1*PI*iTime;
  vec3 axis = vec3(0,0,-1);
  // axis = normalize(axis);
  p = qmul(p,vec4(sin(k)*axis,cos(k)));
  if (!key(CHAR_C)) p.xz = rotate(p.xz,0.55); // Pentagon centred
  if (key(CHAR_T)) p.yz = rotate(p.yz,0.36);   // Triangle centred
  return p;
}

float Barth0(vec4 p, float K) {
  float x = p.x; float y = p.y;
  float z = p.z; float w = p.w;
  float x2 = x*x; float y2 = y*y;
  float z2 = z*z; float w2 = w*w;
  float A = 4.0*(phi2*x2-y2)*(phi2*y2-z2)*(phi2*z2-x2);
  float B = x2 + y2 + z2 - w2;
  return (1.0-K)*(1.0+2.0*phi)*w2*B*B - K*A;
}

float Barth(vec4 p, float K) {
  // Slightly more compact version.
  vec4 p2 = p*p;
  vec3 t = phi2*p2.xyz-p2.yzx;
  float A = 4.0*t.x*t.y*t.z;
  float B = dot(p2.xyz,vec3(1))-p2.w;
  return (1.0-K)*(1.0+2.0*phi)*p2.w*B*B - K*A;
}

// Barth Decic
float Barth10(vec4 p){//decic
  float r2 = dot(p.xyz,p.xyz);
  vec4 p2 = p*p;
  float r4 = dot(p2.xyz,p2.xyz);
  vec4 p4 = p2*p2;
  // 8.0*(p2.xyz-phi4*p2.yzx)*(r4-2.0*dot2(p.xyz*p.yzx))+(3.0+5.0*phi)*...
  return (8.0*(p2.x-phi4*p2.y)*(p2.y-phi4*p2.z)*(p2.z-phi4*p2.x)*
          (r4-2.0*((p.x*p.y)*(p.x*p.y)+(p.x*p.z)*(p.x*p.z)+(p.y*p.z)*(p.y*p.z)))
         +(3.0+5.0*phi)*(r2-p2.w)*(r2-p2.w)*(r2-(2.0-phi)*p2.w)*(r2-(2.0-phi)*p2.w)*p2.w);
}

float Fun(vec3 p3) {
  vec4 p = transform4(p3);
  //return Barth10(p);
  float K = 0.5;
  if (key(CHAR_K)) K = sin(-0.1*PI*iTime)+0.01; // K is mixing parameter
  return Barth(p,K);
}

vec3 getColor(vec3 r, vec3 n) {
  if (!doMirror) {
    //return defaultColor;
    return smoothstep(0.0,1.0,abs(n));
  } else {
    vec3 color = textureLod(iChannel0,n,1.0).rgb;
    color = pow(color,vec3(2.2));
    return color;
  }
}

// Solution parameters.
const int iterations = 200;    // Maximum number of iterations
const float maxincrease = 1.03; // Largest allowed step increase.
const float maxstep = 2.0;     // The largest step that can be taken.
const float minstep = 0.001;  // The smallest step
const float initstep = 0.1;
const float maxdist = 20.0;

vec3 solve(vec3 p0, vec3 r) {
  bool bracketed = false;
  bool found = false;
  float step = initstep;
  vec3 color = vec3(0);//texture(iChannel0,r,0.0).rgb;
  color = pow(color,vec3(2.2));
  vec3 p = p0;
  float t0 = 0.0, t1;
  float f0 = Fun(p), f1;
  // The idea here is to step forward carefully until a change of
  // sign, then use bisection to home in on (hopefully) closest root.
  for (int i = 0; i < iterations; i++) {
    //assert(i < 50);
    t1 = t0 + step;
    if (t1 > maxdist) return color;
    p = p0 + t1*r;
    f1 = Fun(p);
    if (f0*f1 <= 0.0) {
      // We can hit exactly 0 - this counts as bracketed.
      bracketed = true;
      break;
    } else {
      float step0 = step;
      step = f1*step/(f0-f1);
      if (!key(CHAR_R) && step < 0.0) step = 1.0; // Don't step backwards - check this!
      step = abs(step);
      step = min(step,t1*maxstep);
      // Don't grow step by more than a certain amount
      step = max(step,minstep);
      step = min(step,maxincrease*step0);
      t0 = t1; f0 = f1;
    }
  }
  if (!bracketed) return color;
  for (int i = 0; i < 10; i++) {
    //assert(i < 5);
    // Once we are bracketed, just use bisection
    // We could use eg. the secant method, but this is fine and
    // is reasonably quick (since the stepping procedure tends
    // to produce fairly small intervals).
    if ((t1-t0)/t0 < minstep) break;
    float t2 = (t0 + t1)/2.0;
    p = p0+t2*r;
    float f2 = Fun(p);
    if (f0*f2 <= 0.0) {
      t1 = t2; f1 = f2;
    } else {
      t0 = t2; f0 = f2;
    }
  }

  // Compute gradient & normal
  float eps = 1e-3;
  vec2 delta = max(t0,1.0)*vec2(eps,0.0);
  p = p0 + t0*r; // Ensure p corresponds to t0 and a0
#if 1
  vec3 n = vec3(Fun(p + delta.xyy), Fun(p + delta.yxy), Fun(p + delta.yyx)) - f0;
#else
  // Not sure how much difference this makes, not a lot, I suspect.
  vec3 n = vec3(Fun(p + delta.xyy) - Fun(p - delta.xyy),
                Fun(p + delta.yxy) - Fun(p - delta.yxy),
                Fun(p + delta.yyx) - Fun(p - delta.yyx));
#endif
  vec4 p4 = transform4(p);
  float grad = abs(length(n));
  n = normalize(n);

  // Point normal towards eye
  //assert(dot(r,n) < 0.0);
  if (dot(r,n) > 0.0) n *= -1.0;
  vec3 baseColor = getColor(r,n);
  color = baseColor;
  if (!doMirror) {
    color *= ambient;
    float k = dot(light,n);
    if (doDiffuse && k > 0.0) {
      color += baseColor*diffuse*k;
    }
    if (doSpecular && k > 0.0) {
      float specular = pow(max(0.0,dot(reflect(light,n),r)),specpower);
      color += 0.8*specular*vec3(1);
    }
  }
  return color;
}

vec3 transform(vec3 p) {
  if (iMouse.x > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  //p.zx = rotate(p.zx,iTime * 0.2);
  return p;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  doMirror = key(CHAR_M);
  light = vec3(0.5,1,1);
  // Projection parameters
  float camera = 4.0;
  camera *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  vec3 p = vec3(0,0,camera);
  p = transform(p);
  light = transform(light); // Light moves with camera
  light = normalize(light);
  vec3 color = vec3(0);
  int AA = key(CHAR_A) ? 2 : 1;
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 inc = vec2(i,j)/float(AA);
      vec2 uv = (2.0*(fragCoord+inc) - iResolution.xy)/iResolution.y;
      vec3 r = normalize(vec3(uv,-2));
      r = transform(r);
      r = normalize(r);
      color += solve(p,r);
    }
  }
  color /= float(AA*AA);
  color = pow(color,vec3(0.4545));
  if (alert) color.r = 1.0;
  fragColor = vec4(color,1);
}

