// Facebook: https://www.facebook.com/groups/theartofcode/
//
// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .0001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime
#define sSAUCE -3.

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}


float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}
float sdSphere(vec3 p, vec4 s) {
	return length(p-s.xyz)-s.w;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}
float sdBoxFrame( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float GetDist(vec3 p) {

    float d2 = sdBox(p,vec3(.55,2,.098));
    float d8 = sdRoundBox(p-vec3(0,2.8,0), vec3(.1,.5,.1),0.1);
    float d14 = sdBox(p-vec3(0,2.2,0), vec3(1,.2,.2));

    float d10 = sdSphere(p-vec3(0,-0.6,0), vec4(0,0,0,1.67));

    float d11 = sdSphere(p-vec3(-1.5,-1.,0), vec4(0,0,0,1));
    float d16 = sdSphere(p-vec3(1.5,-1.,0), vec4(0,0,0,1));

    float d18 = sdSphere(p-vec3(-.5,-1.2,-1.3), vec4(0,0,0,.8));
    float d19 = sdSphere(p-vec3(1.5,-1.,1), vec4(0,0,0,1));

    float d12 = sdSphere(p-vec3(-1,-0.7,1), vec4(0,0,0,1.4));
    float d17 = sdSphere(p-vec3(1,-0.7,-1), vec4(0,0,0,1.4));

    float d15 = unionSDF(unionSDF(d10,unionSDF(unionSDF(d11,unionSDF(d18,d19)),unionSDF(d11,d17))),unionSDF(d12,d16));

    float d6 = unionSDF(d15,unionSDF(d2,unionSDF(d8,d14)));
    float d7 = sdBoxFrame(p,vec3(4.3,2.3,4.3),.2);
    float d13 = unionSDF(unionSDF(unionSDF(
    sdRoundBox(p -vec3(0,-2.1,0),vec3(4.4,.21,4.4),0.1)
    ,sdRoundBox(p -vec3(0,-2.6,0),vec3(4.5,.21,4.5),0.1))
    ,sdRoundBox(p -vec3(0,-3.1,0),vec3(4.6,.21,4.6),0.1))
    ,sdRoundBox(p -vec3(0,-3.6,0),vec3(4.7,.21,4.7),0.1));


    float torus1 = sdTorus(p-vec3(0,4,0),vec2(3.3,.2));
    float torus2 = sdTorus(p-vec3(0,5,0),vec2(3,.2));
    float torus3 = sdTorus(p-vec3(0,6,0),vec2(2.6,.2));
    float torus4 = sdTorus(p-vec3(0,7,0),vec2(2,.2));


    return unionSDF(unionSDF(unionSDF(d6, d7),d13),unionSDF(unionSDF(torus1,torus4),unionSDF(torus2,torus3)));
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i+=7) {
        //maybe this helps cuz I heard that GPUs are shit at branching
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;

        p = ro + rd*dO;
        dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;

        p = ro + rd*dO;
        dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;

        p = ro + rd*dO;
        dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;

                p = ro + rd*dO;
        dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;

        p = ro + rd*dO;
        dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;

        p = ro + rd*dO;
        dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.0001, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

float luminance(vec3 v)
{
    return dot(v, vec3(0.2126f, 0.7152f, 0.0722f));
}

vec3 change_luminance(vec3 c_in, float l_out)
{
    float l_in = luminance(c_in);
    return c_in * (l_out / l_in);
}

vec3 reinhard_extended(vec3 v, float max_white_l)
{
    float l_old = luminance(v);
    float numerator = l_old * (1.0f + (l_old / (max_white_l * max_white_l)));
    float l_new = numerator / (1.0f + l_old);
    return change_luminance(v, l_new);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 10, -10);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,1.,0), 1.);
    vec3 col = texture(iChannel0,rd).rgb*vec3(1.,.8,.7);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, vec3(1.5,1.7,.3)) + 1.5;
        col = vec3(dif) * texture(iChannel0,r).rgb*vec3(1.40,.7,0.06);
    }

    col = pow(reinhard_extended(col,.81), vec3(.7));	// gamma correction

    fragColor = vec4(col,1.0);
}

#define M_PI (3.1415926535897932384626433832795)

float qScanLine (vec2 uv, float n, float rotation) {
	uv -= 0.5; // move origin to center
    float theta = radians(rotation); // convert to degrees

    // Apply rotation
    float x = uv.x * cos(theta) - uv.y * sin(theta);
    float y = uv.x * sin(theta) + uv.y * cos(theta);

    uv = vec2(x, y);
    uv += 0.5; // move origin back to corner

    return abs(sin(uv.y * M_PI * n));
}

float qVignete (vec2 uv,float q, float o) {
	float x = clamp (1.0 - distance (uv, vec2 (0.5,0.5))*q, 0.0, 1.0);
	return (log((o - 1.0/exp (o))*x + 1.0/exp (o)) + o)/(log(o) + o);
}


vec2 vCrtCurvature (vec2 uv, float q) {
	float x = 1.0- distance (uv, vec2 (0.5, 0.5));
	vec2 g = vec2 (0.5, 0.5) - uv;
	return uv + g*x*q;
}

vec4 v2DNoiseSample (vec2 gPos) {
	vec2 nPos = vec2(
		mod (gPos.x+iTime*9.66,1.0),
		mod (gPos.y+iTime*7.77,1.0)
	);		
	return texture (iChannel1, nPos);
}

vec4 v1DNoiseSample (float idx, float s) {	
	return texture (iChannel1, vec2 (
		mod (idx, 1.0), 
		mod (iTime*s, 1.0))
	);
}

float q2DNoiseSample (vec2 gPos) {
 	vec4 nPnt = v2DNoiseSample (gPos);
	return nPnt.x;
}

float q1DNoiseSample (float idx, float s){
	vec4 nPnt = v1DNoiseSample (idx, s);
	return nPnt.x;
}

vec4 cSignalNoise (vec4 c,float q, vec2 gPos) {
	return c*(1.0 - q) + q*q2DNoiseSample(gPos);
}

vec2 vScanShift (vec2 uv, float q, float dy, float dt) {
	return vec2 (uv.x + q1DNoiseSample (uv.y*dy, dt)*q, uv.y);
}

vec2 vFrameShift (vec2 uv, float q, float dt) {
	float s = (q1DNoiseSample (0.5, dt) - 0.5)/500.0;
	return vec2 (uv.x, mod (uv.y + iTime*(q+s), 1.0));	
}

vec2 vDirShift (vec2 uv, float angle, float q) {
	float a =(angle/180.0)*M_PI;
	vec2 dir = vec2 (sin (a), cos (a));
	return uv + dir*q;
}

vec4 vRGBWithShift (vec2 uv, float angle, float q) {
	vec2 rPos = vDirShift (uv, angle, q);
	vec2 gPos = uv;
	vec2 bPos = vDirShift (uv, -angle, q);
	vec4 rPix = texture (iChannel0, rPos);
	vec4 gPix = texture (iChannel0, gPos);
	vec4 bPix = texture (iChannel0, bPos);
	return vec4 (rPix.x, gPix.y, bPix.z, 1.0);
}

vec4 vPowerNoise (vec4 col, vec2 uv, float b, float dt, float w) {
	float s = q1DNoiseSample (0.0, 0.001)/500.0;
	float y = mod (iTime * (dt + s) , 1.0);
	float d = 1.0 - clamp (abs (uv.y - y), 0.0, w)/w;
	return pow (col,vec4(1.0/(1.0 + b*d)) ) ;
}

vec4 qGamma (vec4 i, vec4 g){
	return pow(i, 1.0/g);
}

vec4 vRGBTint (vec4 col, vec3 g, float q) {	
	return qGamma (col, vec4 (g, 1.0))*q + (1.0 - q)*col;	
}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec4 vColorDrift (vec4 col, float q) {
	vec3 hsv = rgb2hsv (col.xyz);
	hsv.y = mod (hsv.y * q, 1.0);
	return vec4 (hsv2rgb (hsv), col.w);
}

vec4 vColorFilter(vec4 color, vec3 cFilter) {
    vec3 adjustedColor = color.rgb * cFilter;
    return vec4(adjustedColor, color.a);
}

vec4 vApplyMask(vec4 baseColor, vec2 uv) {
    // Sample mask color from iChannel2
    vec4 maskColor = texture(iChannel2, uv);

    // Multiply baseColor by the red channel of the mask color
    // Assuming mask is a grayscale image, so red, green, and blue channels are equal
    return baseColor * maskColor.r;
}

vec4 applyTint(vec4 baseColor, vec4 tintColor, float tintStrength) {
    return mix(baseColor, tintColor, tintStrength);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 cRes = iChannelResolution [0].xy;
	vec2 gRes = iResolution.xy;
	vec2 gPos = fragCoord.xy / gRes;	
	vec2 cPos = gPos ;
	vec4 cCol = vec4(1.0);
	vec2 bPos = vec2(1.0);
	float qNoise = q1DNoiseSample(0.01,0.01);

	//cPos = vScanShift (cPos, 0.02, 0.1, 0.1);			// snaline shift
	//cPos = vCrtCurvature (cPos, 0.3);					// crt curving of coords
	//bPos = vCrtCurvature (gPos, 0.3);					// curvature for the noize bar
	//cPos = vFrameShift (cPos, 0.01, 0.001);				// frame shift		
	//cCol = vColorDrift (cCol, 1.0 - qNoise);	
	cCol = vRGBWithShift (cPos, 100.0, 0.00); 			// sample signal color	
	//cCol = cSignalNoise (cCol, qNoise * 0.8, gPos);				// add signal noise
	//cCol = vPowerNoise (cCol, bPos, 4.0, -0.2, 0.1); 	// power line noize
	//cCol = vRGBTint (cCol, vec3 (0.5, 0.5, 1.0), 1.0);	// gamma tint
	cCol = cCol * qScanLine (gPos, 150.0, 0.0); 				// add scanlines
	//cCol = cCol * qVignete (gPos, 1.5, 3.0); 			// add edge darkening
    cCol = vColorFilter(cCol, vec3 (0.5, 0.5, 1.0));

    cCol = applyTint(cCol, vec4(0.0, 0.0, 1.0, 1.0), 0.4);
    cCol = vApplyMask(cCol, gPos); //apply mask
	fragColor = cCol;

}

void scale(inout vec2 p, float ratio, vec2 origin)
{
    p-= origin;
    p/= ratio;
    p+= origin;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = floor(fragCoord);
    if(KEY_S > 0.) scale(fragCoord, iResolution.y*0.005, iMouse.xy);

    vec4 o = T(fragCoord);
    //fragColor = vec4(abs(c.xy)*1.0f, abs(c.z)*100.0f, c.w);

    vec4 color = vec4(.2, .8, 1, 1)*.5;
    fragColor = color*o.w;
    fragColor += length(o.xy)*0.05;
    fragColor -= o.z*0.005;

    if(isObstacle(fragCoord, iResolution.xy)) fragColor = mix(vec4(.3,.2,.1,1.), fragColor, 0.25);;
}

