// Runge-Kutta 4 backward advection
vec2 RK4(vec2 p){
    vec2 k1 = T(p).xy;
    vec2 k2 = T(p - 0.5 * k1).xy;
    vec2 k3 = T(p - 0.5 * k2).xy;
    vec2 k4 = T(p - k3).xy;
    return (0.5 * k1 + k2 + k3 + 0.5 * k4) / 3.;
}

