#define T(U) texture(iChannel0, (U)/iResolution.xy)
#define KEY_G round(texelFetch(iChannel3, ivec2(71,2) , 0 ).x)
#define KEY_S round(texelFetch(iChannel3, ivec2(83,0) , 0 ).x)

const float dt = 1.0;
const float dx = 1.0;
const float halfrdx = 0.5 / dx;
const float ov = 1.9;
const float g = 0.1;
const float curl = 0.1;

float jacobi(float e, float w, float n, float s, float alpha, float bC, float rBeta){
    return (e + w + n + s + alpha * bC) * rBeta;
}

vec4 bilerp(sampler2D tex, vec2 pos, vec2 size){
    vec2 i = floor(pos);
    vec2 j = fract(pos);
    vec4 x1 = texture(tex, (i)/size),
         x2 = texture(tex, (i + vec2(1,0))/size),
         y1 = texture(tex, (i + vec2(0,1))/size),
         y2 = texture(tex, (i + vec2(1,1))/size);

    vec4 z1 = mix(x1, x2, j.x);
    vec4 z2 = mix(y1, y2, j.x);
    return mix(z1, z2, j.y); 
}

bool isObstacle(vec2 fragCoord, vec2 size){
    //land
    float ratio = size.y/100.;
    float nScale = 3. * ratio;
    float noise = 10. *ratio *(cos(fragCoord.x/nScale) * sin(fragCoord.y/nScale));
    bool land = length(fragCoord - size/2.) + noise < size.y/4.;
    //wall
    bool wall = fragCoord.x < 1. || fragCoord.y < 1. || fragCoord.x > size.x-1. || fragCoord.y > size.y-1.;

    return  wall || land;
}

