// pressure poisson

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 n = T(fragCoord + vec2(0, 1));
    vec4 e = T(fragCoord + vec2(1, 0));
    vec4 s = T(fragCoord + vec2(0, -1));
    vec4 w = T(fragCoord + vec2(-1, 0));
    fragColor = T(fragCoord);

    // divergence of the velocity
    float div = (e.x - w.x + n.y - s.y) * halfrdx * ov;

    //jacobi(float e, float w, float n, float s, float alpha, float bC, float rBeta)
    fragColor.z = jacobi(e.z, w.z, n.z, s.z, -dx*dx, div, 0.25);
}

