void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 n = T(fragCoord + vec2(0, 1));
    vec4 e = T(fragCoord + vec2(1, 0));
    vec4 s = T(fragCoord + vec2(0, -1));
    vec4 w = T(fragCoord + vec2(-1, 0));
    fragColor = T(fragCoord);

    // gradient of the pressure
    vec2 grad = vec2( e.z - w.z, n.z - s.z ) * halfrdx;

    // project
    fragColor.xy -= grad;
}

