vec3 randomOnSphere(Ray ray) {
	vec3 rand = vec3(
    random(ray.direction.xy),
    random(ray.direction.yz),
    random(ray.direction.zx));
	float theta = rand.x * 2.0 * 3.14159265;
	float v = rand.y;
	float phi = acos(2.0 * v - 1.0);
	float r = pow(rand.z, 1.0 / 3.0);
	float x = r * sin(phi) * cos(theta);
	float y = r * sin(phi) * sin(theta);
	float z = r * cos(phi);
	return vec3(x, y, z);
}

vec3 light = normalize(vec3(0.0, -1.0, 1.0));

vec3 getSky(vec3 rd) {
	vec3 col = vec3(0.3, 0.6, 1.0);
	vec3 sun = vec3(0.95, 0.9, 1.0);
	sun *= max(0.0, pow(dot(rd, light), 256.0));
	//col *= max(0.0, pow(dot(rd, light), 256.0));
    col *= pow(dot(rd * 2.0,vec3(0.0,1.0,0.0)),0.1);
    //return vec3(0.0);
	return clamp(sun+col,0.0,1.0);
}

float planeIntersect(Ray ray,Plane plane)
{
    return -(dot(ray.origin,plane.normal)+plane.position)/dot(ray.direction,plane.normal);
}

vec2 sphereIntersect(Ray ray, Sphere sphere)
{
    vec3 oc = ray.origin - sphere.position;
    float b = dot( oc, ray.direction );
    float c = dot( oc, oc ) - sphere.radius*sphere.radius;
    float h = b*b - c;
    if( h<0.0 ) return vec2(-1.0);
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}

vec2 boxIntersection(Ray ray, Box box, out vec3 outNormal) 
{
    ray.origin -= box.position;
    vec3 m = 1.0/ray.direction; 
    vec3 n = m*ray.origin;   
    vec3 k = abs(m)*box.size;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.0) return vec2(-1.0); 
    if (tN > 0.0)
    {
        outNormal = step(vec3(tN),t1);
    }
    else
    {
        outNormal = step(t2,vec3(tF));
    }
    outNormal *= -sign(ray.direction);
    return vec2( tN, tF );
}

