vec3 traceRay(Ray ray) {
	vec3 color = vec3(1.0);
	for(int i = 0; i < MaxReflections; i++)
	{
		vec4 reflectedColor = castRay(ray);

		if(reflectedColor.a == -2.0)
        {
            float a = 0.8;
            color *= vec3(
            pow(reflectedColor.r,a),
            pow(reflectedColor.g,a),
            pow(reflectedColor.b,a));
            if (distance(color,vec3(1.0,1.0,1.0)) < 0.310009)
            {
                color = color * lightStrenth;
            }
            return color;
        }
        color *= reflectedColor.rgb;
        if(reflectedColor.a == -3.0) 
        {

            return color * reflectedColor.rgb;
        }
	}
	return vec3(0.0);
}

